INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (51, '溶解液', 'ようかいえき', 'Acid', 'poison', '毒', '特殊', 40, 100, 30, '將強酸潑向對手進行攻擊。有時會降低對手的特防。'), (52, '火花', 'ひのこ', 'Ember', 'fire', '火', '特殊', 40, 100, 25, '向對手發射小火焰進行攻擊。有時會讓對手陷入灼傷狀態。'), (53, '噴射火焰', 'かえんほうしゃ', 'Flamethrower', 'fire', '火', '特殊', 90, 100, 15, '向對手發射強烈火焰進行攻擊。有時會讓對手陷入灼傷狀態。'), (54, '白霧', 'しろいきり', 'Mist', 'ice', '冰', '變化', 0, 100, 30, '用白霧覆蓋身體。在５回合內不會讓對手降低自己的能力。'), (55, '水槍', 'みずでっぽう', 'Water Gun', 'water', '水', '特殊', 40, 100, 25, '向對手噴射強烈水流進行攻擊。'), (56, '水炮', 'ハイドロポンプ', 'Hydro Pump', 'water', '水', '特殊', 110, 80, 5, '向對手猛烈地噴射大量水流進行攻擊。'), (57, '衝浪', 'なみのり', 'Surf', 'water', '水', '特殊', 90, 100, 15, '使用大浪攻擊自己周圍所有的寶可夢。'), (58, '冰凍光束', 'れいとうビーム', 'Ice Beam', 'ice', '冰', '特殊', 90, 100, 10, '向對手發射冰凍光束進行攻擊。有時會讓對手陷入冰凍狀態。'), (59, '暴風雪', 'ふぶき', 'Blizzard', 'ice', '冰', '特殊', 110, 70, 5, '將猛烈的暴風雪吹向對手進行攻擊。有時會讓對手陷入冰凍狀態。'), (60, '幻象光線', 'サイケこうせん', 'Psybeam', 'psychic', '超能力', '特殊', 65, 100, 20, '向對手發射神奇的光線進行攻擊。有時會使對手混亂。'), (61, '泡沫光線', 'バブルこうせん', 'Bubble Beam', 'water', '水', '特殊', 65, 100, 20, '向對手猛烈地噴射泡沫進行攻擊。有時會降低對手的速度。'), (62, '極光束', 'オーロラビーム', 'Aurora Beam', 'ice', '冰', '特殊', 65, 100, 20, '向對手發射七彩光束進行攻擊。有時會降低對手的攻擊。'), (63, '破壞光線', 'はかいこうせん', 'Hyper Beam', 'normal', '一般', '特殊', 150, 90, 5, '向對手發射強烈的光線進行攻擊。下一回合自己將無法動彈。'), (64, '啄', 'つつく', 'Peck', 'flying', '飛行', '物理', 35, 100, 35, '用尖銳的喙或角刺向對手進行攻擊。'), (65, '啄鑽', 'ドリルくちばし', 'Drill Peck', 'flying', '飛行', '物理', 80, 100, 20, '一邊旋轉，一邊將尖喙刺進對手進行攻擊。'), (66, '地獄翻滾', 'じごくぐるま', 'Submission', 'fighting', '格鬥', '物理', 80, 80, 20, '將對手連同自己一起摔向地面進行攻擊。自己也會受到少許傷害。'), (67, '踢倒', 'けたぐり', 'Low Kick', 'fighting', '格鬥', '物理', 0, 100, 20, '用力踢對手的腳，讓對手摔倒進行攻擊。對手越重，威力越大。'), (68, '雙倍奉還', 'カウンター', 'Counter', 'fighting', '格鬥', '物理', 0, 100, 20, '將來自對手的物理攻擊傷害加倍後，返還給該對手。'), (69, '地球上投', 'ちきゅうなげ', 'Seismic Toss', 'fighting', '格鬥', '物理', 0, 100, 20, '利用引力將對手甩飛出去。給予對手和自己等級相同的傷害。'), (70, '怪力', 'かいりき', 'Strength', 'normal', '一般', '物理',