INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (501, '快速防守', 'ファストガード', 'Quick Guard', 'fighting', '格鬥', '變化', 0, 100, 15, '保護自己和同伴，防禦來自對手的先制攻擊。'), (502, '交換場地', 'サイドチェンジ', 'Ally Switch', 'psychic', '超能力', '變化', 0, 100, 15, '用神奇的力量瞬間移動，讓自己和同伴的所在位置互換。連續使用時容易失敗。'), (503, '熱水', 'ねっとう', 'Scald', 'water', '水', '特殊', 80, 100, 15, '向對手噴射滾燙的熱水進行攻擊。有時會讓對手陷入灼傷狀態。'), (504, '破殼', 'からをやぶる', 'Shell Smash', 'normal', '一般', '變化', 0, 100, 15, '破殼而出。自己的防禦和特防會降低，但能大幅提高攻擊、特攻和速度。'), (505, '治癒波動', 'いやしのはどう', 'Heal Pulse', 'psychic', '超能力', '變化', 0, 100, 10, '釋放出治癒波動，讓對手回復最大ＨＰ的一半。'), (506, '禍不單行', 'たたりめ', 'Hex', 'ghost', '幽靈', '特殊', 65, 100, 10, '接二連三地向對手進行攻擊。攻擊陷入異常狀態的對手時，能給予較大的傷害。'), (507, '自由落體', 'フリーフォール', 'Sky Drop', 'flying', '飛行', '物理', 60, 100, 10, '使用後的第１回合將對手帶到空中，並於第２回合摔下對手進行攻擊。被帶到空中的對手將無法行動。'), (508, '換檔', 'ギアチェンジ', 'Shift Gear', 'steel', '鋼', '變化', 0, 100, 10, '轉動齒輪，提高自己的攻擊，同時大幅提高自己的速度。'), (509, '巴投', 'ともえなげ', 'Circle Throw', 'fighting', '格鬥', '物理', 60, 90, 10, '摔飛對手，強制讓後備寶可夢上場。對手為野生寶可夢時，戰鬥將直接結束。'), (510, '燒盡', 'やきつくす', 'Incinerate', 'fire', '火', '特殊', 60, 100, 15, '用火焰攻擊對手。能夠燒掉對手攜帶的樹果等道具，讓對手無法使用。'), (511, '延後', 'さきおくり', 'Quash', 'dark', '惡', '變化', 0, 100, 15, '壓制對手，讓對手在回合的最後才能行動。'), (512, '雜技', 'アクロバット', 'Acrobatics', 'flying', '飛行', '物理', 55, 100, 15, '輕巧地攻擊對手。自己沒有攜帶道具時，能給予對手較大的傷害。'), (513, '鏡面屬性', 'ミラータイプ', 'Reflect Type', 'normal', '一般', '變化', 0, 100, 15, '在自己身上反映出對手的屬性，讓自己也變成相同的屬性。'), (514, '報仇', 'かたきうち', 'Retaliate', 'normal', '一般', '物理', 70, 100, 5, '為倒下的同伴報仇。前一回合有同伴被打倒時，招式的威力會提高。'), (515, '搏命', 'いのちがけ', 'Final Gambit', 'fighting', '格鬥', '特殊', 0, 100, 5, '捨身攻擊對手。雖然自己會陷入瀕死，但能夠給予對手和自己現有ＨＰ相同的傷害。'), (516, '傳遞禮物', 'ギフトパス', 'Bestow', 'normal', '一般', '變化', 0, 100, 15, '當對手沒有攜帶道具時，能夠將自己攜帶的道具交給對手。'), (517, '煉獄', 'れんごく', 'Inferno', 'fire', '火', '特殊', 100, 50, 5, '用猛烈的火焰包圍對手進行攻擊。能讓對手陷入灼傷狀態。'), (518, '水之誓約', 'みずのちかい', 'Water Pledge', 'water', '水', '特殊', 80, 100, 10, '用水柱進行攻擊。和火之誓約同時使用時，威力會提高，天空會出現彩虹。'), (519, '火之誓約', 'ほのおのちかい', 'Fire Pledge', 'fire', '火', '特殊', 80, 100, 10, '用火柱進行攻擊。和草之誓約同時使用時，威力會提高，周圍會變成火海。'), (520, '草之誓約', 'くさのちかい', 'Grass Pledge', 'grass', '草', '特殊', 80, 100, 10, '用草柱進行攻擊。和水之誓約同時使用時，威力會提高，周圍會變成濕地。'), (521, '伏特替換', 'ボルトチェンジ', 'Volt Switch', 'electric', '電', '特殊', 70, 100, 20, '攻擊後迅速返回，和後備寶可夢進行替換。'), (522, '蟲之抵抗', 'むしのていこう', 'Struggle Bug', 'bug', '蟲', '特殊', 50, 100, 20, '抵抗對手進行攻擊。可降低對手的特攻。'), (523, '重踏', 'じならし', 'Bulldoze', 'ground', '地面', '物理', 60, 100, 20, '用力踩踏地面，攻擊自己周圍所有的寶可夢。可降低對手的速度。'), (524, '冰息', 'こおりのいぶき', 'Frost Breath', 'ice', '冰', '特殊', 60, 90, 10, '將冰冷的氣息吹向對手進行攻擊。必定會擊中要害。'), (525, '龍尾', 'ドラゴンテール', 'Dragon Tail', 'dragon', '龍', '物理', 60, 90, 10, '彈飛對手，強制讓後備寶可夢上場。對手為野生寶可夢時，戰鬥將直接結束。'), (526, '自我激勵', 'ふるいたてる', 'Work Up', 'normal', '一般', '變化', 0, 100, 30, '激勵自己，提高自己的攻擊和特攻。'), (527, '電網', 'エレキネット', 'Electroweb', 'electric', '電', '特殊', 55, 95, 15, '用電網捉住對手進行攻擊。可降低對手的速度。'), (528, '瘋狂伏特', 'ワイルドボルト', 'Wild Charge', 'electric', '電', '物理', 90, 100, 15, '讓電流圍繞全身，衝撞對手進行攻擊。自己也會受到少許傷害。'), (529, '直衝鑽', 'ドリルライナー', 'Drill Run', 'ground', '地面', '物理', 80, 95, 10, '像鑽頭一樣旋轉身體，猛烈地撞擊對手。容易擊中要害。'), (530, '二連劈', 'ダブルチョップ', 'Dual Chop', 'dragon', '龍', '物理', 40, 90, 15, '用身體的堅硬部分攻擊對手。連續２次給予傷害。'), (531, '愛心印章', 'ハートスタンプ', 'Heart Stamp', 'psychic', '超能力', '物理', 60, 100, 25, '用可愛的動作讓對手疏忽，趁機發出強烈的一擊。有時會使對手畏縮。'), (532, '木角', 'ウッドホーン', 'Horn Leech', 'grass', '草', '物理', 75, 100, 10, '將角刺進對手，吸取養分。可以回復給予對手傷害的一半ＨＰ。'), (533, '聖劍', 'せいなるつるぎ', 'Sacred Sword', 'fighting', '格鬥', '物理', 90, 100, 15, '用劍斬切對手進行攻擊。可不顧對手的能力變化給予傷害。'), (534, '貝殼刃', 'シェルブレード', 'Razor Shell', 'water', '水', '物理', 75, 95, 10, '用鋒利的貝殼斬切對手進行攻擊。有時會降低對手的防禦。'), (535, '高溫重壓', 'ヒートスタンプ', 'Heat Crash', 'fire', '火', '物理', 0, 100, 10, '用燃燒的身體重壓對手進行攻擊。體重比對手重越多，威力越大。'), (536, '青草攪拌器', 'グラスミキサー', 'Leaf Tornado', 'grass', '草', '特殊', 65, 90, 10, '用銳利的葉子包圍住對手進行攻擊。有時會降低對手的命中率。'), (537, '瘋狂滾壓', 'ハードローラー', 'Steamroller', 'bug', '蟲', '物理', 65, 100, 20, '旋轉蜷縮成圓形的身體，壓扁對手。有時會使對手畏縮。'), (538, '棉花防守', 'コットンガード', 'Cotton Guard', 'grass', '草', '變化', 0, 100, 10, '用軟綿綿的絨毛保護自己的身體。極大幅提高自己的防禦。'), (539, '暗黑爆破', 'ナイトバースト', 'Night Daze', 'dark', '惡', '特殊', 85, 95, 10, '放出黑暗的衝擊波攻擊對手。有時會降低對手的命中率。'), (540, '精神擊破', 'サイコブレイク', 'Psystrike', 'psychic', '超能力', '特殊', 100, 100, 10, '將神奇的念力波實體化後攻擊對手。給予對手物理傷害。'), (541, '掃尾拍打', 'スイープビンタ', 'Tail Slap', 'normal', '一般', '物理', 25, 85, 10, '將堅硬的尾巴掃向對手進行攻擊。可連續攻擊２～５次。'), (542, '暴風', 'ぼうふう', 'Hurricane', 'flying', '飛行', '特殊', 110, 70, 10, '用強烈的風席捲對手進行攻擊。有時會使對手混亂。'), (543, '爆炸頭突擊', 'アフロブレイク', 'Head Charge', 'normal', '一般', '物理', 120, 100, 15, '用帥氣的爆炸頭猛撞對手進行攻擊。自己也會受到少許傷害。'), (544, '齒輪飛盤', 'ギアソーサー', 'Gear Grind', 'steel', '鋼', '物理', 50, 85, 15, '向對手投擲鋼鐵齒輪進行攻擊。連續２次給予傷害。'), (545, '火焰彈', 'かえんだん', 'Searing Shot', 'fire', '火', '特殊', 100, 100, 5, '用熊熊烈火攻擊自己周圍所有的寶可夢。有時會讓對手陷入灼傷狀態。'), (546, '高科技光炮', 'テクノバスター', 'Techno Blast', 'normal', '一般', '特殊', 120, 100, 5, '向對手發射光彈。招式的屬性會隨著自己攜帶的卡帶不同而改變。'), (547, '古老之歌', 'いにしえのうた', 'Relic Song', 'normal', '一般', '特殊', 75, 100, 10, '唱出古老之歌，打動對手的心進行攻擊。有時會讓對手陷入睡眠狀態。'), (548, '神秘之劍', 'しんぴのつるぎ', 'Secret Sword', 'fighting', '格鬥', '特殊', 85, 100, 10, '用長角斬切對手進行攻擊。角上擁有的神奇力量會給予對手物理傷害。'), (549, '冰封世界', 'こごえるせかい', 'Glaciate', 'ice', '冰', '特殊', 65, 95, 10, '放出冰凍的寒氣向對手進行攻擊。可降低對手的速度。'), (550, '雷擊', 'らいげき', 'Bolt Strike', 'electric', '電', '物理', 130, 85, 5, '用強大的電流圍繞身體，猛撞對手進行攻擊。有時會讓對手陷入麻痺狀態。');