INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (551, '青焰', 'あおいほのお', 'Blue Flare', 'fire', '火', '特殊', 130, 85, 5, '用美麗而激烈的青焰燃燒對手進行攻擊。有時會讓對手陷入灼傷狀態。'), (552, '火之舞', 'ほのおのまい', 'Fiery Dance', 'fire', '火', '特殊', 80, 100, 10, '讓火焰圍繞全身後，振翅攻擊對手。有時會提高自己的特攻。'), (553, '冰凍伏特', 'フリーズボルト', 'Freeze Shock', 'ice', '冰', '物理', 140, 90, 5, '製造出帶有電流的冰塊，在下一回合攻擊對手。有時會讓對手陷入麻痺狀態。'), (554, '極寒冷焰', 'コールドフレア', 'Ice Burn', 'ice', '冰', '特殊', 140, 90, 5, '在使用後的下一回合，用能夠凍結一切的強烈寒氣包圍對手。有時會讓對手陷入灼傷狀態。'), (555, '大聲咆哮', 'バークアウト', 'Snarl', 'dark', '惡', '特殊', 55, 95, 15, '沒完沒了地向對手大聲咆哮，降低對手的特攻。'), (556, '冰柱墜擊', 'つららおとし', 'Icicle Crash', 'ice', '冰', '物理', 85, 90, 10, '將巨大冰柱猛烈地砸向對手進行攻擊。有時會使對手畏縮。'), (557, 'Ｖ熱焰', 'Ｖジェネレート', 'V-create', 'fire', '火', '物理', 180, 95, 5, '在前額產生灼熱的火焰，然後捨身撞擊對手。自己的防禦、特防和速度會降低。'), (558, '交錯火焰', 'クロスフレイム', 'Fusion Flare', 'fire', '火', '特殊', 100, 100, 5, '放出巨大的火焰攻擊對手。受到巨大的閃電影響時，招式的威力會提高。'), (559, '交錯閃電', 'クロスサンダー', 'Fusion Bolt', 'electric', '電', '物理', 100, 100, 5, '放出巨大的閃電攻擊對手。受到巨大的火焰影響時，招式的威力會提高。'), (560, '飛身重壓', 'フライングプレス', 'Flying Press', 'fighting', '格鬥', '物理', 100, 95, 10, '從空中俯衝向對手進行攻擊。此招式同時具有格鬥屬性和飛行屬性。'), (561, '掀榻榻米', 'たたみがえし', 'Mat Block', 'fighting', '格鬥', '變化', 0, 100, 10, '將掀起來的榻榻米當作盾牌，防禦住攻擊我方的攻擊招式。但無法防禦住變化招式的攻擊。'), (562, '打嗝', 'ゲップ', 'Belch', 'poison', '毒', '特殊', 120, 90, 10, '朝著對手打嗝進行攻擊。沒吃下樹果時無法使用。'), (563, '耕地', 'たがやす', 'Rototiller', 'ground', '地面', '變化', 0, 100, 10, '耕耘土地，讓草木更容易成長。可提高草屬性寶可夢的攻擊和特攻。'), (564, '黏黏網', 'ねばねばネット', 'Sticky Web', 'bug', '蟲', '變化', 0, 100, 20, '在對手周圍撒下黏黏的網，降低替換出場的對手的速度。'), (565, '致命針刺', 'とどめばり', 'Fell Stinger', 'bug', '蟲', '物理', 50, 100, 25, '使用此招式打倒對手時，自己的攻擊會極大幅提高。'), (566, '潛靈奇襲', 'ゴーストダイブ', 'Phantom Force', 'ghost', '幽靈', '物理', 90, 100, 10, '使用後的第１回合消失到某處，並於第２回合攻擊對手。可不顧對手的守護進行攻擊。'), (567, '萬聖夜', 'ハロウィン', 'Trick-or-Treat', 'ghost', '幽靈', '變化', 0, 100, 20, '邀請對手參加萬聖夜。讓對手追加幽靈屬性。'), (568, '戰吼', 'おたけび', 'Noble Roar', 'normal', '一般', '變化', 0, 100, 30, '發出戰吼威嚇對手，降低對手的攻擊和特攻。'), (569, '等離子浴', 'プラズマシャワー', 'Ion Deluge', 'electric', '電', '變化', 0, 100, 25, '讓帶電粒子擴散開來，使一般屬性的招式變成電屬性。'), (570, '拋物面充電', 'パラボラチャージ', 'Parabolic Charge', 'electric', '電', '特殊', 65, 100, 20, '攻擊自己周圍所有的寶可夢。可以回復給予傷害的一半ＨＰ。'), (571, '森林詛咒', 'もりののろい', 'Forest''s Curse', 'grass', '草', '變化', 0, 100, 20, '向對手施加森林詛咒。中了詛咒的對手會被追加草屬性。'), (572, '落英繽紛', 'はなふぶき', 'Petal Blizzard', 'grass', '草', '物理', 90, 100, 15, '猛烈地刮起飛雪般的落花，攻擊自己周圍所有的寶可夢。'), (573, '冷凍乾燥', 'フリーズドライ', 'Freeze-Dry', 'ice', '冰', '特殊', 70, 100, 20, '急速冷凍對手，有時會讓對手陷入冰凍狀態。對水屬性寶可夢也是效果絕佳。'), (574, '魅惑之聲', 'チャームボイス', 'Disarming Voice', 'fairy', '妖精', '特殊', 40, 100, 15, '發出迷人的叫聲，給予對手精神上的傷害。攻擊必定會命中。'), (575, '拋下狠話', 'すてゼリフ', 'Parting Shot', 'dark', '惡', '變化', 0, 100, 20, '拋下狠話恐嚇對手，降低對手的攻擊和特攻後，和後備寶可夢進行替換。'), (576, '顛倒', 'ひっくりかえす', 'Topsy-Turvy', 'dark', '惡', '變化', 0, 100, 20, '讓對手身上所有的能力變化顛倒過來，變成和原來相反的狀態。'), (577, '吸取之吻', 'ドレインキッス', 'Draining Kiss', 'fairy', '妖精', '特殊', 50, 100, 10, '親吻對手來吸取對手的ＨＰ。可以回復給予對手傷害的一半以上的ＨＰ。'), (578, '戲法防守', 'トリックガード', 'Crafty Shield', 'fairy', '妖精', '變化', 0, 100, 10, '使用神奇的力量防禦住攻擊我方的變化招式。但無法防禦住攻擊招式的攻擊。'), (579, '鮮花防守', 'フラワーガード', 'Flower Shield', 'fairy', '妖精', '變化', 0, 100, 10, '使用神奇的力量，提高場上全體草屬性寶可夢的防禦。'), (580, '青草場地', 'グラスフィールド', 'Grassy Terrain', 'grass', '草', '變化', 0, 100, 10, '在５回合內將腳下變成青草場地。地面上的寶可夢每回合都會回復ＨＰ。草屬性招式的威力會提高。'), (581, '薄霧場地', 'ミストフィールド', 'Misty Terrain', 'fairy', '妖精', '變化', 0, 100, 10, '在５回合內，地面上的寶可夢不會陷入異常狀態。龍屬性招式的傷害也會減半。'), (582, '輸電', 'そうでん', 'Electrify', 'electric', '電', '變化', 0, 100, 20, '如果在對手使用招式前輸電，該回合對手的招式會變成電屬性。'), (583, '嬉鬧', 'じゃれつく', 'Play Rough', 'fairy', '妖精', '物理', 90, 90, 10, '在和對手嬉鬧的同時進行攻擊。有時會降低對手的攻擊。'), (584, '妖精之風', 'ようせいのかぜ', 'Fairy Wind', 'fairy', '妖精', '特殊', 40, 100, 30, '刮起妖精之風，吹向對手進行攻擊。'), (585, '月亮之力', 'ムーンフォース', 'Moonblast', 'fairy', '妖精', '特殊', 95, 100, 15, '借用月亮的力量攻擊對手。有時會降低對手的特攻。'), (586, '爆音波', 'ばくおんぱ', 'Boomburst', 'normal', '一般', '特殊', 140, 100, 10, '利用震耳欲聾的爆炸聲所產生的破壞力，攻擊自己周圍所有的寶可夢。'), (587, '妖精之鎖', 'フェアリーロック', 'Fairy Lock', 'fairy', '妖精', '變化', 0, 100, 10, '封鎖對戰場地，下一回合所有的寶可夢都無法逃走。'), (588, '王者盾牌', 'キングシールド', 'King''s Shield', 'steel', '鋼', '變化', 0, 100, 10, '防住對手攻擊的同時，使自己變為防禦姿態。降低接觸到的對手的攻擊。'), (589, '和睦相處', 'なかよくする', 'Play Nice', 'normal', '一般', '變化', 0, 100, 20, '和對手和睦相處，讓對手失去戰鬥意志，降低對手的攻擊。'), (590, '密語', 'ないしょばなし', 'Confide', 'normal', '一般', '變化', 0, 100, 20, '向對手低聲密語，使對手失去集中力，降低對手的特攻。'), (591, '鑽石風暴', 'ダイヤストーム', 'Diamond Storm', 'rock', '岩石', '物理', 100, 95, 5, '吹起鑽石風暴給予對手傷害。有時會大幅提高自己的防禦。'), (592, '蒸汽爆炸', 'スチームバースト', 'Steam Eruption', 'water', '水', '特殊', 110, 95, 5, '將超高溫的蒸汽噴向對手。有時會讓對手陷入灼傷狀態。'), (593, '異次元洞', 'いじげんホール', 'Hyperspace Hole', 'psychic', '超能力', '特殊', 80, 100, 5, '利用異次元洞，突然出現在對手的側面進行攻擊。可避開對手的守住和看穿等招式。'), (594, '飛水手裡劍', 'みずしゅりけん', 'Water Shuriken', 'water', '水', '特殊', 15, 100, 20, '射出用黏液製成的手裡劍，連續攻擊２～５次。必定能夠發動先制攻擊。'), (595, '魔法火焰', 'マジカルフレイム', 'Mystical Fire', 'fire', '火', '特殊', 75, 100, 10, '從口中吐出特別灼熱的火焰進行攻擊。可降低對手的特攻。'), (596, '尖刺防守', 'ニードルガード', 'Spiky Shield', 'grass', '草', '變化', 0, 100, 10, '防禦來自對手的攻擊，同時削減接觸到自己的對手的ＨＰ。'), (597, '芳香薄霧', 'アロマミスト', 'Aromatic Mist', 'fairy', '妖精', '變化', 0, 100, 20, '利用神奇的芳香，提高我方寶可夢的特防。'), (598, '怪異電波', 'かいでんぱ', 'Eerie Impulse', 'electric', '電', '變化', 0, 100, 15, '從身體發出怪異電波影響對手，大幅降低對手的特攻。'), (599, '毒液陷阱', 'ベノムトラップ', 'Venom Drench', 'poison', '毒', '變化', 0, 100, 20, '向對手發射特殊的毒液。陷入中毒狀態的對手攻擊、特攻和速度會降低。'), (600, '粉塵', 'ふんじん', 'Powder', 'bug', '蟲', '變化', 0, 100, 20, '被撒到粉塵的對手使用火屬性招式時，會發生爆炸並受到傷害。');