INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (601, '大地掌控', 'ジオコントロール', 'Geomancy', 'fairy', '妖精', '變化', 0, 100, 10, '使用後的第１回合吸收能量，並於第２回合大幅提高自己的特攻、特防和速度。'), (602, '磁場操控', 'じばそうさ', 'Magnetic Flux', 'electric', '電', '變化', 0, 100, 20, '操控磁場，提高特性是正電和負電的寶可夢的防禦和特防。'), (603, '歡樂時光', 'ハッピータイム', 'Happy Hour', 'normal', '一般', '變化', 0, 100, 30, '使用歡樂時光後，戰鬥結束時獲得的錢會加倍。'), (604, '電氣場地', 'エレキフィールド', 'Electric Terrain', 'electric', '電', '變化', 0, 100, 10, '在５回合內將腳下變成電氣場地。地面上的寶可夢將不會陷入睡眠狀態。電屬性招式的威力會提高。'), (605, '魔法閃耀', 'マジカルシャイン', 'Dazzling Gleam', 'fairy', '妖精', '特殊', 80, 100, 10, '發出強烈的閃光，給予對手傷害。'), (606, '慶祝', 'おいわい', 'Celebrate', 'normal', '一般', '變化', 0, 100, 40, '寶可夢會為了非常開心的你而慶祝。'), (607, '牽手', 'てをつなぐ', 'Hold Hands', 'normal', '一般', '變化', 0, 100, 40, '我方的寶可夢手牽手。心情會變得非常幸福。'), (608, '圓瞳', 'つぶらなひとみ', 'Baby-Doll Eyes', 'fairy', '妖精', '變化', 0, 100, 30, '用圓圓的眼睛凝視對手，降低對手的攻擊。必定能夠發動先制攻擊。'), (609, '蹭蹭臉頰', 'ほっぺすりすり', 'Nuzzle', 'electric', '電', '物理', 20, 100, 20, '用帶電的臉頰磨蹭對手進行攻擊。能讓對手陷入麻痺狀態。'), (610, '手下留情', 'てかげん', 'Hold Back', 'normal', '一般', '物理', 40, 100, 40, '在攻擊的時候手下留情，讓對手的ＨＰ一定會剩下１。'), (611, '死纏爛打', 'まとわりつく', 'Infestation', 'bug', '蟲', '特殊', 20, 100, 20, '在４～５回合內死纏爛打地進行攻擊。在此期間對手將無法逃走。'), (612, '增強拳', 'グロウパンチ', 'Power-Up Punch', 'fighting', '格鬥', '物理', 40, 100, 20, '反覆出拳擊打對手，使自己的拳頭慢慢變硬。命中對手時會提高自己的攻擊。'), (613, '死亡之翼', 'デスウイング', 'Oblivion Wing', 'flying', '飛行', '特殊', 80, 100, 10, '從鎖定的對手身上吸取ＨＰ。可以回復給予對手傷害的一半以上的ＨＰ。'), (614, '千箭齊發', 'サウザンアロー', 'Thousand Arrows', 'ground', '地面', '物理', 90, 100, 10, '能夠命中飄浮在空中的寶可夢。飄浮在空中的對手會被擊落掉到地面。'), (615, '千波激盪', 'サウザンウェーブ', 'Thousand Waves', 'ground', '地面', '物理', 90, 100, 10, '化成波浪從地面進行攻擊。被捲入波浪中的對手將無法從戰鬥中逃走。'), (616, '大地神力', 'グランドフォース', 'Land''s Wrath', 'ground', '地面', '物理', 90, 100, 10, '聚集大地的能量後，將力量集中攻擊對手給予傷害。'), (617, '破滅之光', 'はめつのひかり', 'Light of Ruin', 'fairy', '妖精', '特殊', 140, 90, 5, '借用永恆之花的力量，發射出強力光線。自己也會受到非常大的傷害。'), (618, '根源波動', 'こんげんのはどう', 'Origin Pulse', 'water', '水', '特殊', 110, 85, 10, '放出無數閃耀著青白色光輝的光線攻擊對手。'), (619, '斷崖之劍', 'だんがいのつるぎ', 'Precipice Blades', 'ground', '地面', '物理', 120, 85, 10, '將大地的力量化為利刃攻擊對手。'), (620, '畫龍點睛', 'ガリョウテンセイ', 'Dragon Ascent', 'flying', '飛行', '物理', 120, 100, 5, '從天空中急速下降攻擊對手。自己的防禦和特防會降低。'), (621, '異次元猛攻', 'いじげんラッシュ', 'Hyperspace Fury', 'dark', '惡', '物理', 100, 100, 5, '使用許多手臂，發動可不顧守住和看穿的連續攻擊。自己的防禦會降低。'), (659, '集沙', 'すなあつめ', 'Shore Up', 'ground', '地面', '變化', 0, 100, 5, '回復自己最大ＨＰ的一半。在沙暴中回復得更多。'), (660, '迎頭一擊', 'であいがしら', 'First Impression', 'bug', '蟲', '物理', 90, 100, 10, '招式的威力雖高，但只有在出場戰鬥時，立刻使出才能成功。'), (661, '碉堡', 'トーチカ', 'Baneful Bunker', 'poison', '毒', '變化', 0, 100, 10, '防住對手攻擊的同時，讓碰觸到自己的對手中毒。'), (662, '縫影', 'かげぬい', 'Spirit Shackle', 'ghost', '幽靈', '物理', 80, 100, 10, '攻擊的同時，縫住對手的影子，使其無法逃走。'), (663, 'ＤＤ金勾臂', 'ＤＤラリアット', 'Darkest Lariat', 'dark', '惡', '物理', 85, 100, 10, '旋轉雙臂並打向對手。可不顧對手的能力變化給予傷害。'), (664, '泡影的詠歎調', 'うたかたのアリア', 'Sparkling Aria', 'water', '水', '特殊', 90, 100, 10, '隨著歌聲放出許多水球。受此招式攻擊時，灼傷會被治癒。'), (665, '冰錘', 'アイスハンマー', 'Ice Hammer', 'ice', '冰', '物理', 100, 90, 10, '揮出強力而沉重的拳頭，給予對手傷害。自己的速度會降低。'), (666, '花療', 'フラワーヒール', 'Floral Healing', 'fairy', '妖精', '變化', 0, 100, 10, '回復對手最大ＨＰ的一半。在青草場地時，效果會提高。'), (667, '十萬馬力', '１０まんばりき', 'High Horsepower', 'ground', '地面', '物理', 95, 95, 10, '使出全身力量，猛攻對手。'), (668, '吸取力量', 'ちからをすいとる', 'Strength Sap', 'grass', '草', '變化', 0, 100, 10, '將自己的ＨＰ回復到和對手的攻擊力相同。然後降低對手的攻擊。'), (669, '日光刃', 'ソーラーブレード', 'Solar Blade', 'grass', '草', '物理', 125, 100, 10, '使用後的第１回合吸收大量日光，並於第２回合把力量注入劍裡進行攻擊。'), (670, '樹葉', 'このは', 'Leafage', 'grass', '草', '物理', 40, 100, 40, '將葉片打向對手，進行攻擊。'), (671, '聚光燈', 'スポットライト', 'Spotlight', 'normal', '一般', '變化', 0, 100, 15, '幫寶可夢打聚光燈，該回合只能瞄準該寶可夢。'), (672, '毒絲', 'どくのいと', 'Toxic Thread', 'poison', '毒', '變化', 0, 100, 20, '將混有毒的絲吐向對手。使其中毒，進而降低對手的速度。'), (673, '磨礪', 'とぎすます', 'Laser Focus', 'normal', '一般', '變化', 0, 100, 30, '集中精神，下次攻擊必定會擊中要害。'), (674, '輔助齒輪', 'アシストギア', 'Gear Up', 'steel', '鋼', '變化', 0, 100, 20, '藉由啟動齒輪，提高特性為正電和負電的寶可夢的攻擊和特攻。'), (675, '地獄突刺', 'じごくづき', 'Throat Chop', 'dark', '惡', '物理', 80, 100, 15, '受到此招式攻擊的對手，會因為地獄般的痛苦，在２回合內無法使用會發出聲音的招式。'), (676, '花粉團', 'かふんだんご', 'Pollen Puff', 'bug', '蟲', '特殊', 90, 100, 15, '對敵人使用時，用會爆炸的花粉團來攻擊。對我方同伴使用時，則給予有回復效果的花粉團。'), (677, '擲錨', 'アンカーショット', 'Anchor Shot', 'steel', '鋼', '物理', 80, 100, 20, '將錨纏住對手進行攻擊。使對手無法逃走。'), (678, '精神場地', 'サイコフィールド', 'Psychic Terrain', 'psychic', '超能力', '變化', 0, 100, 10, '在５回合內，地面上的寶可夢不會受到先制招式攻擊。超能力屬性的招式威力會提高。'), (679, '猛撲', 'とびかかる', 'Lunge', 'bug', '蟲', '物理', 80, 100, 15, '全力猛撲對手進行攻擊。進而降低對手的攻擊。'), (680, '火焰鞭', 'ほのおのムチ', 'Fire Lash', 'fire', '火', '物理', 80, 100, 15, '用燃燒的鞭子抽打對手。受到攻擊的對手防禦會降低。'), (681, '囂張', 'つけあがる', 'Power Trip', 'dark', '惡', '物理', 20, 100, 10, '耀武揚威地攻擊對手，自己的能力提高得越多，威力就越大。'), (682, '燃盡', 'もえつきる', 'Burn Up', 'fire', '火', '特殊', 130, 100, 5, '燃盡全身的火焰，給予對手巨大的傷害。自己的火屬性將會消失。'), (683, '速度互換', 'スピードスワップ', 'Speed Swap', 'psychic', '超能力', '變化', 0, 100, 10, '將對手和自己的速度進行互換。'), (684, '修長之角', 'スマートホーン', 'Smart Strike', 'steel', '鋼', '物理', 70, 100, 10, '用尖尖的角刺進對手進行攻擊。攻擊必定會命中。'), (685, '淨化', 'じょうか', 'Purify', 'poison', '毒', '變化', 0, 100, 20, '治癒對手的異常狀態。治癒後可以回復自己的ＨＰ。'), (686, '覺醒之舞', 'めざめるダンス', 'Revelation Dance', 'normal', '一般', '特殊', 90, 100, 15, '全力跳舞進行攻擊。此招式的屬性將變得和自己的屬性相同。'), (687, '核心懲罰者', 'コアパニッシャー', 'Core Enforcer', 'dragon', '龍', '特殊', 100, 100, 10, '如果攻擊的對手在該回合已經結束行動，對手的特性就會被消除。');