INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (688, '熱帶踢', 'トロピカルキック', 'Trop Kick', 'grass', '草', '物理', 70, 100, 15, '向對手使出來自南國的火熱腳踢。進而降低對手的攻擊。'), (689, '號令', 'さいはい', 'Instruct', 'psychic', '超能力', '變化', 0, 100, 15, '對手使出招式之後，能夠透過指令讓對手再次使出相同招式。'), (690, '鳥嘴加農炮', 'くちばしキャノン', 'Beak Blast', 'flying', '飛行', '物理', 100, 100, 15, '先加熱鳥嘴後再進行攻擊。若對手在加熱時觸碰的話，就會灼傷。'), (691, '鱗片噪音', 'スケイルノイズ', 'Clanging Scales', 'dragon', '龍', '特殊', 110, 100, 5, '摩擦全身鱗片，發出響亮的聲音並進行攻擊。攻擊後自己的防禦會降低。'), (692, '龍錘', 'ドラゴンハンマー', 'Dragon Hammer', 'dragon', '龍', '物理', 90, 100, 15, '像錘子般地使用身體向對手發動襲擊，給予傷害。'), (693, '狂舞揮打', 'ぶんまわす', 'Brutal Swing', 'dark', '惡', '物理', 60, 100, 20, '用自己的身體狂舞揮打，給予對手傷害。'), (694, '極光幕', 'オーロラベール', 'Aurora Veil', 'ice', '冰', '變化', 0, 100, 20, '在５回合內減弱物理和特殊的傷害。只有下雪時才能使出。'), (695, '遮天蔽日暗影箭', 'シャドーアローズストライク', 'Sinister Arrow Raid', 'ghost', '幽靈', '物理', 180, 100, 1, '透過Ｚ力量製造出無數箭的狙射樹梟全力射穿對手進行攻擊。'), (696, '極惡飛躍粉碎擊', 'ハイパーダーククラッシャー', 'Malicious Moonsault', 'dark', '惡', '物理', 180, 100, 1, '透過Ｚ力量得到強壯肉體的熾焰咆哮虎全力撞向對手進行攻擊。'), (697, '海神莊嚴交響樂', 'わだつみのシンフォニア', 'Oceanic Operetta', 'water', '水', '特殊', 195, 100, 1, '透過Ｚ力量呼喚大量水的西獅海壬全力攻擊對手。'), (698, '巨人衛士・阿羅拉', 'ガーディアン・デ・アローラ', 'Guardian of Alola', 'fairy', '妖精', '特殊', 0, 100, 1, '透過Ｚ力量得到阿羅拉之力的土地神寶可夢的全力攻擊。會大量減少對手的現有HP。'), (699, '七星奪魂腿', 'しちせいだっこんたい', 'Soul-Stealing 7-Star Strike', 'ghost', '幽靈', '物理', 195, 100, 1, '得到Ｚ力量的瑪夏多全力使出拳頭和腳踢的連續招式叩打對手。'), (700, '駕雷馭電戲衝浪', 'ライトニングサーフライド', 'Stoked Sparksurfer', 'electric', '電', '特殊', 175, 100, 1, '得到Ｚ力量的阿羅拉地區的雷丘全力進行攻擊。讓對手陷入麻痺狀態。'), (701, '認真起來大爆擊', 'ほんきをだす こうげき', 'Pulverizing Pancake', 'normal', '一般', '物理', 210, 100, 1, '透過Ｚ力量使認真起來的卡比獸躍動其巨大身軀，全力向對手發動襲擊。'), (702, '九彩昇華齊聚頂', 'ナインエボルブースト', 'Extreme Evoboost', 'normal', '一般', '變化', 0, 100, 1, '得到Ｚ力量的伊布借助進化後的夥伴們的力量大幅提升自己的能力。'), (703, '起源超新星大爆炸', 'オリジンズスーパーノヴァ', 'Genesis Supernova', 'psychic', '超能力', '特殊', 185, 100, 1, '得到Ｚ力量的夢幻全力攻擊對手。腳下變成精神場地。'), (704, '陷阱甲殼', 'トラップシェル', 'Shell Trap', 'fire', '火', '特殊', 150, 100, 5, '設下甲殼陷阱。若對手使出物理招式，就會爆炸並給予傷害。'), (705, '花朵加農炮', 'フルールカノン', 'Fleur Cannon', 'fairy', '妖精', '特殊', 130, 90, 5, '放出強力光束後，自己的特攻會大幅降低。'), (706, '精神之牙', 'サイコファング', 'Psychic Fangs', 'psychic', '超能力', '物理', 85, 100, 10, '利用精神力量咬住對手進行攻擊。並可破壞光牆和反射壁等。'), (707, '跺腳', 'じだんだ', 'Stomping Tantrum', 'ground', '地面', '物理', 75, 100, 10, '化悔恨為力量進行攻擊。如果上一回合的招式沒有命中，威力就會加倍。'), (708, '暗影之骨', 'シャドーボーン', 'Shadow Bone', 'ghost', '幽靈', '物理', 85, 100, 10, '用寄宿了靈魂的骨頭毆打對手進行攻擊。有時會降低對手的防禦。'), (709, '衝岩', 'アクセルロック', 'Accelerock', 'rock', '岩石', '物理', 40, 100, 20, '迅速撞向對手進行攻擊。必定能夠發動先制攻擊。'), (710, '水流裂破', 'アクアブレイク', 'Liquidation', 'water', '水', '物理', 85, 100, 10, '用水之力量撞向對手進行攻擊。有時會降低對手的防禦。'), (711, '稜鏡鐳射', 'プリズムレーザー', 'Prismatic Laser', 'psychic', '超能力', '特殊', 160, 100, 10, '用稜鏡的力量發射強烈光線。下一回合自己將無法動彈。'), (712, '暗影偷盜', 'シャドースチール', 'Spectral Thief', 'ghost', '幽靈', '物理', 90, 100, 10, '潛入對手的影子，奪取對手的能力提升進行攻擊。'), (713, '流星閃衝', 'メテオドライブ', 'Sunsteel Strike', 'steel', '鋼', '物理', 100, 100, 5, '以流星般的氣勢猛撞對手。可不顧對手的特性進行攻擊。'), (714, '暗影之光', 'シャドーレイ', 'Moongeist Beam', 'ghost', '幽靈', '特殊', 100, 100, 5, '放出奇怪的光線攻擊對手。可不顧對手的特性進行攻擊。'), (715, '淚眼汪汪', 'なみだめ', 'Tearful Look', 'normal', '一般', '變化', 0, 100, 20, '變得淚眼汪汪，令對手喪失鬥志。進而降低對手的攻擊和特攻。'), (716, '麻麻刺刺', 'びりびりちくちく', 'Zing Zap', 'electric', '電', '物理', 80, 100, 10, '撞向對手，並利用強電流使其感到麻麻刺刺的。有時會使對手畏縮。'), (717, '自然之怒', 'しぜんのいかり', 'Nature''s Madness', 'fairy', '妖精', '特殊', 0, 90, 10, '向對手發洩自然之怒。會使對手的ＨＰ減半。'), (718, '多屬性攻擊', 'マルチアタック', 'Multi-Attack', 'normal', '一般', '物理', 120, 100, 10, '讓強大能量圍繞全身，撞向對手進行攻擊。招式屬性會隨著記憶碟而改變。'), (719, '千萬伏特', '１０００まんボルト', '10,000,000 Volt Thunderbolt', 'electric', '電', '特殊', 195, 100, 1, '戴著帽子的皮卡丘向對手釋放出藉由Ｚ力量增強的電流。容易擊中要害。'), (720, '驚爆大頭', 'ビックリヘッド', 'Mind Blown', 'fire', '火', '特殊', 150, 100, 5, '會讓自己的頭爆炸，來攻擊周遭的一切。自己也會受到傷害。'), (721, '等離子閃電拳', 'プラズマフィスト', 'Plasma Fists', 'electric', '電', '物理', 100, 100, 15, '用帶有電流的拳頭進行攻擊。可以將一般屬性的招式轉變為電屬性。'), (722, '光子噴湧', 'フォトンゲイザー', 'Photon Geyser', 'psychic', '超能力', '特殊', 100, 100, 5, '以光柱進行攻擊。會比較自己的攻擊和特攻，以數值較高的一方進行攻擊。'), (723, '熾魂熱舞烈音爆', 'ブレイジングソウルビート', 'Clangorous Soulblaze', 'dragon', '龍', '特殊', 185, 100, 1, '獲得Ｚ力量的杖尾鱗甲龍將會以全力攻擊對手，並提升自己的能力。'), (724, '狼嘯石牙颶風暴', 'ラジアルエッジストーム', 'Splintered Stormshards', 'rock', '岩石', '物理', 190, 100, 1, '獲得Ｚ力量的鬃岩狼人將會以全力進行攻擊，並消除場地的狀態。'), (725, '親密無間大亂揍', 'ぽかぼかフレンドタイム', 'Let''s Snuggle Forever', 'fairy', '妖精', '物理', 190, 100, 1, '獲得Ｚ力量的謎擬Ｑ將會以全力進行亂揍攻擊。'), (726, '日光迴旋下蒼穹', 'サンシャインスマッシャー', 'Searing Sunraze Smash', 'steel', '鋼', '物理', 200, 100, 1, '獲得Ｚ力量的索爾迦雷歐將會以全力進行攻擊。可以無視對手的特性效果。'), (727, '月華飛濺落靈霄', 'ムーンライトブラスター', 'Menacing Moonraze Maelstrom', 'ghost', '幽靈', '特殊', 200, 100, 1, '獲得Ｚ力量的露奈雅拉將會以全力進行攻擊。可以無視對手的特性效果。'), (728, '焚天滅世熾光爆', 'てんこがすめつぼうのひかり', 'Light That Burns the Sky', 'psychic', '超能力', '特殊', 200, 100, 1, '奈克洛茲瑪會無視對手特性的效果，在攻擊與特攻之間選擇數值較高的一方進行攻擊。'), (729, '電電加速', 'ばちばちアクセル', 'Zippy Zap', 'electric', '電', '物理', 50, 100, 15, '迅猛無比的電擊。必定能夠發動先制攻擊，擊中對手要害。'), (730, '滔滔衝浪', 'ざぶざぶサーフ', 'Splishy Splash', 'water', '水', '特殊', 90, 100, 15, '在巨浪中注入電力，撞向對手進行攻擊。有時會讓對手陷入麻痺狀態。'), (731, '飄飄墜落', 'ふわふわフォール', 'Floaty Fall', 'flying', '飛行', '物理', 90, 95, 15, '輕飄飄地浮起，然後急速墜落，進行攻擊。有時會讓對手畏縮。'), (732, '閃閃雷光', 'ピカピカサンダー', 'Pika Papow', 'electric', '電', '特殊', 0, 100, 20, '皮卡丘越喜歡訓練家，威力就越強大的電擊。攻擊必定會命中。'), (733, '活活氣泡', 'いきいきバブル', 'Bouncy Bubble', 'water', '水', '特殊', 90, 100, 15, '投擲水球進行攻擊。攻擊後吸水並回復給予對手傷害的一半HP。'), (734, '麻麻電擊', 'びりびりエレキ', 'Buzzy Buzz', 'electric', '電', '特殊', 90, 100, 15, '發出電擊攻擊對手。讓對手陷入麻痺狀態。'), (735, '熊熊火爆', 'めらめらバーン', 'Sizzly Slide', 'fire', '火', '物理', 90, 100, 15, '用纏繞著火焰的身體猛烈地衝撞對手，讓對手陷入灼傷狀態。'), (736, '嘩嘩氣場', 'どばどばオーラ', 'Glitzy Glow', 'psychic', '超能力', '特殊', 90, 100, 15, '用極大量的念力攻擊對手，造出一道能夠減弱對手特殊攻擊的神奇牆壁。'), (737, '壞壞領域', 'わるわるゾーン', 'Baddy Bad', 'dark', '惡', '特殊', 90, 100, 15, '惡形惡狀地進行攻擊，造出一道能夠減弱對手物理攻擊的神奇牆壁。');