INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (738, '茁茁轟炸', 'すくすくボンバー', 'Sappy Seed', 'grass', '草', '物理', 90, 100, 15, '長出巨大的藤蔓，散播種子進行攻擊。種子每回合都會吸取對手的HP。'), (739, '冰冰霜凍', 'こちこちフロスト', 'Freezy Frost', 'ice', '冰', '特殊', 90, 100, 15, '用冰凍的黑霧結晶進行攻擊，讓全體寶可夢的能力變化回到初始狀態。'), (740, '亮亮風暴', 'きらきらストーム', 'Sparkly Swirl', 'fairy', '妖精', '特殊', 90, 100, 15, '用帶著嗆鼻香氣的龍捲風包圍對手，進行攻擊，並治癒我方寶可夢的異常狀態。'), (741, '砰砰擊破', 'ブイブイブレイク', 'Veevee Volley', 'normal', '一般', '物理', 0, 100, 20, '伊布越喜歡訓練家，威力就越強大的衝撞。攻擊必定會命中。'), (742, '鋼拳雙擊', 'ダブルパンツァー', 'Double Iron Bash', 'steel', '鋼', '物理', 60, 100, 5, '以胸口的螺帽為軸心旋轉，連續２次揮動手臂痛擊對手。有時會使對手畏縮。'), (743, '極巨防壁', 'ダイウォール', 'Max Guard', 'normal', '一般', '變化', 0, 100, 0, '完全防住對手的所有攻擊。連續使用時容易失敗。'), (744, '極巨炮', 'ダイマックスほう', 'Dynamax Cannon', 'dragon', '龍', '特殊', 100, 100, 5, '從核心發射凝聚在體內的能量進行攻擊。'), (745, '狙擊', 'ねらいうち', 'Snipe Shot', 'water', '水', '特殊', 80, 100, 15, '能不受具有吸引對手招式效果的特性或招式影響，向選定的對手進行攻擊。'), (746, '緊咬不放', 'くらいつく', 'Jaw Lock', 'dark', '惡', '物理', 80, 100, 10, '使攻擊對象及自己在陷入瀕死前都無法進行替換。當兩隻寶可夢其中一方退場時，效果就會消失。'), (747, '大快朵頤', 'ほおばる', 'Stuff Cheeks', 'normal', '一般', '變化', 0, 100, 10, '吃掉攜帶的樹果，大幅提高自己的防禦。'), (748, '背水一戰', 'はいすいのじん', 'No Retreat', 'fighting', '格鬥', '變化', 0, 100, 5, '提高自己所有的能力，但無法進行替換或逃走。'), (749, '瀝青射擊', 'タールショット', 'Tar Shot', 'rock', '岩石', '變化', 0, 100, 15, '射出黏黏的瀝青，降低對手的速度。火屬性會變成對手的弱點。'), (750, '魔法粉', 'まほうのこな', 'Magic Powder', 'psychic', '超能力', '變化', 0, 100, 20, '撒出魔法粉末，使對手變為超能力屬性。'), (751, '龍箭', 'ドラゴンアロー', 'Dragon Darts', 'dragon', '龍', '物理', 50, 100, 10, '讓多龍梅西亞進行２次攻擊。當對手場上有２隻寶可夢時，則向牠們各進行１次攻擊。'), (752, '茶會', 'おちゃかい', 'Teatime', 'normal', '一般', '變化', 0, 100, 10, '舉辦茶會，在場上的寶可夢都會吃掉自己攜帶的樹果。'), (753, '蛸固', 'たこがため', 'Octolock', 'fighting', '格鬥', '變化', 0, 100, 15, '讓對手無法逃走。對手被纏住後，防禦和特防每回合都會降低。'), (754, '電喙', 'でんげきくちばし', 'Bolt Beak', 'electric', '電', '物理', 85, 100, 10, '用帶有電流的鳥嘴刺向對手。若在對手之前進行攻擊，招式的威力會變成２倍。'), (755, '鰓咬', 'エラがみ', 'Fishious Rend', 'water', '水', '物理', 85, 100, 10, '用堅硬的鰓咬住對手。若在對手之前進行攻擊，招式的威力會變成２倍。'), (756, '換場', 'コートチェンジ', 'Court Change', 'normal', '一般', '變化', 0, 100, 10, '用神奇的力量與對手交換場地效果。'), (757, '極巨火爆', 'ダイバーン', 'Max Flare', 'fire', '火', '極巨', 0, 100, 0, '極巨化寶可夢所使出的火屬性攻擊。可在５回合內讓日照增強。'), (758, '極巨蟲蠱', 'ダイワーム', 'Max Flutterby', 'bug', '蟲', '極巨', 0, 100, 0, '極巨化寶可夢所使出的蟲屬性攻擊。可降低對手的特攻。'), (759, '極巨閃電', 'ダイサンダー', 'Max Lightning', 'electric', '電', '極巨', 0, 100, 0, '極巨化寶可夢所使出的電屬性攻擊。可在５回合內將腳下變成電氣場地。'), (760, '極巨攻擊', 'ダイアタック', 'Max Strike', 'normal', '一般', '極巨', 0, 100, 0, '極巨化寶可夢所使出的一般屬性攻擊。可降低對手的速度。'), (761, '極巨拳鬥', 'ダイナックル', 'Max Knuckle', 'fighting', '格鬥', '極巨', 0, 100, 0, '極巨化寶可夢所使出的格鬥屬性攻擊。可提高我方的攻擊。'), (762, '極巨幽魂', 'ダイホロウ', 'Max Phantasm', 'ghost', '幽靈', '極巨', 0, 100, 0, '極巨化寶可夢所使出的幽靈屬性攻擊。可降低對手的防禦。'), (763, '極巨寒冰', 'ダイアイス', 'Max Hailstorm', 'ice', '冰', '極巨', 0, 100, 0, '極巨化寶可夢所使出的冰屬性攻擊。在５回合內會下雪。'), (764, '極巨酸毒', 'ダイアシッド', 'Max Ooze', 'poison', '毒', '極巨', 0, 100, 0, '極巨化寶可夢所使出的毒屬性攻擊。可提高我方的特攻。'), (765, '極巨水流', 'ダイストリーム', 'Max Geyser', 'water', '水', '極巨', 0, 100, 0, '極巨化寶可夢所使出的水屬性攻擊。在５回合內會降下大雨。'), (766, '極巨飛衝', 'ダイジェット', 'Max Airstream', 'flying', '飛行', '極巨', 0, 100, 0, '極巨化寶可夢所使出的飛行屬性攻擊。可提高我方的速度。'), (767, '極巨妖精', 'ダイフェアリー', 'Max Starfall', 'fairy', '妖精', '極巨', 0, 100, 0, '極巨化寶可夢所使出的妖精屬性攻擊。可在５回合內將腳下變成薄霧場地。'), (768, '極巨龍騎', 'ダイドラグーン', 'Max Wyrmwind', 'dragon', '龍', '極巨', 0, 100, 0, '極巨化寶可夢所使出的龍屬性攻擊。可降低對手的攻擊。'), (769, '極巨超能', 'ダイサイコ', 'Max Mindstorm', 'psychic', '超能力', '極巨', 0, 100, 0, '極巨化寶可夢所使出的超能力屬性攻擊。可在５回合內將腳下變成精神場地。'), (770, '極巨岩石', 'ダイロック', 'Max Rockfall', 'rock', '岩石', '極巨', 0, 100, 0, '極巨化寶可夢所使出的岩石屬性攻擊。在５回合內會捲起沙暴。'), (771, '極巨大地', 'ダイアース', 'Max Quake', 'ground', '地面', '極巨', 0, 100, 0, '極巨化寶可夢所使出的地面屬性攻擊。可提高我方的特防。'), (772, '極巨惡霸', 'ダイアーク', 'Max Darkness', 'dark', '惡', '極巨', 0, 100, 0, '極巨化寶可夢所使出的惡屬性攻擊。可降低對手的特防。'), (773, '極巨草原', 'ダイソウゲン', 'Max Overgrowth', 'grass', '草', '極巨', 0, 100, 0, '極巨化寶可夢所使出的草屬性攻擊。可在５回合內將腳下變成青草場地。'), (774, '極巨鋼鐵', 'ダイスチル', 'Max Steelspike', 'steel', '鋼', '極巨', 0, 100, 0, '極巨化寶可夢所使出的鋼屬性攻擊。可提高我方的防禦。'), (775, '魂舞烈音爆', 'ソウルビート', 'Clangorous Soul', 'dragon', '龍', '變化', 0, 100, 5, '消耗一些自己的ＨＰ，提高自己的所有能力。'), (776, '撲擊', 'ボディプレス', 'Body Press', 'fighting', '格鬥', '物理', 80, 100, 10, '用身體撞向對手進行攻擊。自己的防禦越高，給予對手的傷害越大。'), (777, '裝飾', 'デコレーション', 'Decorate', 'fairy', '妖精', '變化', 0, 100, 15, '透過裝飾，大幅提高對手的攻擊和特攻。'), (778, '鼓擊', 'ドラムアタック', 'Drum Beating', 'grass', '草', '物理', 80, 100, 10, '用打鼓來控制鼓的根部進行攻擊，進而降低對手的速度。'), (779, '捕獸夾', 'トラバサミ', 'Snap Trap', 'grass', '草', '物理', 35, 100, 15, '用捕獸夾，在４～５回合內夾住對手進行攻擊。'), (780, '火焰球', 'かえんボール', 'Pyro Ball', 'fire', '火', '物理', 120, 90, 5, '燃燒小石子，形成火球攻擊對手。有時會讓對手陷入灼傷狀態。'), (781, '巨獸斬', 'きょじゅうざん', 'Behemoth Blade', 'steel', '鋼', '物理', 100, 100, 5, '用全身的力量舉起強大的劍，猛烈地斬向對手進行攻擊。'), (782, '巨獸彈', 'きょじゅうだん', 'Behemoth Bash', 'steel', '鋼', '物理', 100, 100, 5, '將全身變為堅固的盾，猛烈地撞向對手進行攻擊。'), (783, '氣場輪', 'オーラぐるま', 'Aura Wheel', 'electric', '電', '物理', 110, 100, 10, '用儲存在頰囊中的能量進行攻擊，並提高自己的速度。招式屬性會隨著莫魯貝可的樣子而改變。'), (784, '廣域破壞', 'ワイドブレイカー', 'Breaking Swipe', 'dragon', '龍', '物理', 60, 100, 15, '激烈地甩動強韌的尾巴攻擊對手，降低對手的攻擊。'), (785, '木枝突刺', 'えだづき', 'Branch Poke', 'grass', '草', '物理', 40, 100, 40, '用尖銳的樹枝刺向對手進行攻擊。'), (786, '破音', 'オーバードライブ', 'Overdrive', 'electric', '電', '特殊', 80, 100, 10, '彈奏吉他和貝斯，用發出巨響的激烈震動攻擊對手。'), (787, '蘋果酸', 'りんごさん', 'Apple Acid', 'grass', '草', '特殊', 80, 100, 10, '使用從酸蘋果提煉出的酸性液體進行攻擊。可降低對手的特防。');