INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (788, '萬有引力', 'Ｇのちから', 'Grav Apple', 'grass', '草', '物理', 80, 100, 10, '讓蘋果從高處落下，給予對手傷害。可降低對手的防禦。'), (789, '靈魂衝擊', 'ソウルクラッシュ', 'Spirit Break', 'fairy', '妖精', '物理', 75, 100, 15, '用足以讓對手一蹶不振的氣勢進行攻擊。可降低對手的特攻。'), (790, '神奇蒸汽', 'ワンダースチーム', 'Strange Steam', 'fairy', '妖精', '特殊', 90, 95, 10, '噴出煙霧攻擊對手。有時會使對手混亂。'), (791, '生命水滴', 'いのちのしずく', 'Life Dew', 'water', '水', '變化', 0, 100, 10, '灑出神奇之水，回復自己和場上同伴的ＨＰ。'), (792, '攔堵', 'ブロッキング', 'Obstruct', 'dark', '惡', '變化', 0, 100, 10, '完全防住對手的所有攻擊。連續使用時容易失敗。一旦觸碰到，防禦會大幅降低。'), (793, '假跪真撞', 'どげざつき', 'False Surrender', 'dark', '惡', '物理', 80, 100, 10, '假裝低頭道歉，用凌亂的頭髮刺向對手。攻擊必定會命中。'), (794, '流星突擊', 'スターアサルト', 'Meteor Assault', 'fighting', '格鬥', '物理', 150, 100, 5, '揮舞粗壯的莖進行攻擊。但同時自己也會站不穩，下一回合將無法動彈。'), (795, '無極光束', 'ムゲンダイビーム', 'Eternabeam', 'dragon', '龍', '特殊', 160, 90, 5, '無極汰那變回本來樣子時的最強攻擊。下一回合自己將無法動彈。'), (796, '鐵蹄光線', 'てっていこうせん', 'Steel Beam', 'steel', '鋼', '特殊', 140, 95, 5, '將從全身匯聚起來的鋼鐵化為光束激烈地發射出去。自己也會受到傷害。'), (797, '廣域戰力', 'ワイドフォース', 'Expanding Force', 'psychic', '超能力', '特殊', 80, 100, 10, '用精神力量攻擊對手。在精神場地時，威力會提高，能給予所有對手傷害。'), (798, '鐵滾輪', 'アイアンローラー', 'Steel Roller', 'steel', '鋼', '物理', 130, 100, 5, '一邊破壞場地，一邊進行攻擊。在不是特殊狀態的場地上使用，招式就會失敗。'), (799, '鱗射', 'スケイルショット', 'Scale Shot', 'dragon', '龍', '物理', 25, 90, 20, '射出鱗片進行攻擊。可連續攻擊２～５次。速度會提高但防禦會降低。'), (800, '流星光束', 'メテオビーム', 'Meteor Beam', 'rock', '岩石', '特殊', 120, 90, 10, '使用後的第１回合會聚集宇宙的力量來提高特攻，並於第２回合攻擊對手。'), (801, '臂貝武器', 'シェルアームズ', 'Shell Side Arm', 'poison', '毒', '特殊', 90, 100, 10, '用物理攻擊和特殊攻擊中能夠給予更大傷害的能力進行攻擊。有時會讓對手陷入中毒狀態。'), (802, '薄霧炸裂', 'ミストバースト', 'Misty Explosion', 'fairy', '妖精', '特殊', 100, 100, 5, '對自己周圍的所有寶可夢進行攻擊，但使出後，自己會陷入瀕死。在薄霧場地時，招式威力會提高。'), (803, '青草滑梯', 'グラススライダー', 'Grassy Glide', 'grass', '草', '物理', 55, 100, 20, '在地面上滑行般地攻擊對手。在青草場地時，必定能夠發動先制攻擊。'), (804, '電力上升', 'ライジングボルト', 'Rising Voltage', 'electric', '電', '特殊', 70, 100, 20, '用從地面升騰的電擊攻擊。當對手在電氣場地時，招式威力會變成２倍。'), (805, '大地波動', 'だいちのはどう', 'Terrain Pulse', 'normal', '一般', '特殊', 50, 100, 10, '藉助場地的力量進行攻擊。視使出招式時場地狀態不同，招式的屬性和威力會有所變化。'), (806, '爬擊', 'はいよるいちげき', 'Skitter Smack', 'bug', '蟲', '物理', 70, 90, 10, '從對手背後爬近後進行攻擊。會降低對手的特攻。'), (807, '妒火', 'しっとのほのお', 'Burning Jealousy', 'fire', '火', '特殊', 70, 100, 5, '用嫉妒的能量攻擊對手。讓此回合內能力提高的寶可夢陷入灼傷狀態。'), (808, '洩憤', 'うっぷんばらし', 'Lash Out', 'dark', '惡', '物理', 75, 100, 5, '將惱怒砸向對手進行攻擊。如果此回合已被降低能力，招式威力就會變成２倍。'), (809, '靈騷', 'ポルターガイスト', 'Poltergeist', 'ghost', '幽靈', '物理', 110, 90, 5, '操控對手的持有物進行攻擊。對手沒有攜帶道具時，則會失敗。'), (810, '腐蝕氣體', 'ふしょくガス', 'Corrosive Gas', 'poison', '毒', '變化', 0, 100, 40, '用強烈的酸性氣體包圍周圍所有的寶可夢，並將攜帶的道具融化掉。'), (811, '指導', 'コーチング', 'Coaching', 'fighting', '格鬥', '變化', 0, 100, 10, '透過適切的指導，提高我方全體的攻擊和防禦。'), (812, '快速折返', 'クイックターン', 'Flip Turn', 'water', '水', '物理', 60, 100, 20, '攻擊後迅速返回，和後備寶可夢進行替換。'), (813, '三旋擊', 'トリプルアクセル', 'Triple Axel', 'ice', '冰', '物理', 20, 90, 10, '連續踢對手３次進行攻擊。每踢中一次，威力就會提高。'), (814, '雙翼', 'ダブルウイング', 'Dual Wingbeat', 'flying', '飛行', '物理', 40, 90, 10, '用翅膀撞向對手進行攻擊。連續２次給予傷害。'), (815, '熱沙大地', 'ねっさのだいち', 'Scorching Sands', 'ground', '地面', '特殊', 70, 100, 10, '向對手投灑熾熱的沙子進行攻擊。有時會讓對手陷入灼傷狀態。'), (816, '叢林治療', 'ジャングルヒール', 'Jungle Healing', 'grass', '草', '變化', 0, 100, 10, '和叢林化為一體，回復自己和場上同伴的ＨＰ和狀態。'), (817, '暗冥強擊', 'あんこくきょうだ', 'Wicked Blow', 'dark', '惡', '物理', 75, 100, 5, '將惡之流派鍛鍊到了極致的強烈一擊。必定會擊中要害。'), (818, '水流連打', 'すいりゅうれんだ', 'Surging Strikes', 'water', '水', '物理', 25, 100, 5, '將水之流派鍛鍊到了極致，猶如行雲流水的３次連擊。必定會擊中要害。'), (819, '雷電囚籠', 'サンダープリズン', 'Thunder Cage', 'electric', '電', '特殊', 80, 90, 15, '將對手困在電流奔竄的電牢中，在４～５回合內進行攻擊。'), (820, '巨龍威能', 'ドラゴンエナジー', 'Dragon Energy', 'dragon', '龍', '特殊', 150, 100, 5, '將生命力化為力量攻擊對手。自己的ＨＰ越少，招式的威力越小。'), (821, '冰冷視線', 'いてつくしせん', 'Freezing Glare', 'psychic', '超能力', '特殊', 90, 100, 10, '從雙眼釋放出精神力量進行攻擊。有時會讓對手陷入冰凍狀態。'), (822, '怒火中燒', 'もえあがるいかり', 'Fiery Wrath', 'dark', '惡', '特殊', 90, 100, 10, '將憤怒化為火焰般的氣場進行攻擊。有時會讓對手畏縮。'), (823, '雷鳴蹴擊', 'らいめいげり', 'Thunderous Kick', 'fighting', '格鬥', '物理', 90, 100, 10, '以雷電般的動作一邊戲弄對手一邊使出踢擊。可降低對手的防禦。'), (824, '雪矛', 'ブリザードランス', 'Glacial Lance', 'ice', '冰', '物理', 120, 100, 5, '向對手投擲捲著暴風雪的冰矛進行攻擊。'), (825, '星碎', 'アストラルビット', 'Astral Barrage', 'ghost', '幽靈', '特殊', 120, 100, 5, '向對手釋出大量的小靈體進行攻擊。'), (826, '詭異咒語', 'ぶきみなじゅもん', 'Eerie Spell', 'psychic', '超能力', '特殊', 80, 100, 5, '用強大的精神力量攻擊。讓對手最近一次使用的招式減少３ＰＰ。'), (827, '剋命爪', 'フェイタルクロー', 'Dire Claw', 'poison', '毒', '物理', 80, 100, 15, '以破滅之爪進行攻擊。有時還會讓對手陷入中毒、麻痺、睡眠之中的一種狀態。'), (828, '屏障猛攻', 'バリアーラッシュ', 'Psyshield Bash', 'psychic', '超能力', '物理', 70, 90, 10, '讓意念的能量圍繞全身，撞向對手進行攻擊。提高自己的防禦。'), (829, '力量轉換', 'パワーシフト', 'Power Shift', 'normal', '一般', '變化', 0, 100, 10, '將自己的攻擊與防禦互相交換。'), (830, '岩斧', 'がんせきアックス', 'Stone Axe', 'rock', '岩石', '物理', 65, 90, 15, '用岩石之斧進行攻擊。散落的岩石碎片會在對手周圍飄浮。'), (831, '陽春風暴', 'はるのあらし', 'Springtide Storm', 'fairy', '妖精', '特殊', 100, 80, 5, '用交織著愛與恨的烈風席捲對手進行攻擊。有時會降低對手的攻擊。'), (832, '神秘之力', 'しんぴのちから', 'Mystical Power', 'psychic', '超能力', '特殊', 70, 90, 10, '放出不可思議的力量進行攻擊。自己的特攻會提高。'), (833, '大憤慨', 'だいふんげき', 'Raging Fury', 'fire', '火', '物理', 120, 100, 10, '在２～３回合內放出火焰，同時瘋狂亂打。大鬧一番後自己會陷入混亂。'), (834, '波動衝', 'ウェーブタックル', 'Wave Crash', 'water', '水', '物理', 120, 100, 10, '用水圍繞全身並撞向對手，但自己也會受到不小的傷害。'), (835, '葉綠爆震', 'クロロブラスト', 'Chloroblast', 'grass', '草', '特殊', 150, 95, 5, '將自身的葉綠素凝聚起來後放出去進行攻擊。自己也會受到傷害。'), (836, '冰山風', 'ひょうざんおろし', 'Mountain Gale', 'ice', '冰', '物理', 100, 85, 10, '將冰山般巨大的冰塊砸向對手進行攻擊。有時會使對手畏縮。'), (837, '勝利之舞', 'しょうりのまい', 'Victory Dance', 'fighting', '格鬥', '變化', 0, 100, 10, '激烈地跳起喚來勝利的舞蹈，提高自己的攻擊、防禦和速度。');