INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (838, '突飛猛撲', 'ぶちかまし', 'Headlong Rush', 'ground', '地面', '物理', 120, 100, 5, '向對手使出灌注了全心全力的撞擊。自己的防禦和特防會降低。'), (839, '毒千針', 'どくばりセンボン', 'Barb Barrage', 'poison', '毒', '物理', 60, 100, 10, '用無數的毒針進行攻擊。有時還會讓對手陷入中毒狀態。攻擊處於中毒狀態的對手時，威力會變成２倍。'), (840, '氣場之翼', 'オーラウイング', 'Esper Wing', 'psychic', '超能力', '特殊', 80, 100, 10, '用經過氣場強化的翅膀撕裂對手。容易擊中要害。自己的速度會提高。'), (841, '冤冤相報', 'うらみつらみ', 'Bitter Malice', 'ghost', '幽靈', '特殊', 75, 100, 10, '用令人毛骨悚然的怨念進行攻擊，降低對手的攻擊。'), (842, '閉關', 'たてこもる', 'Shelter', 'steel', '鋼', '變化', 0, 100, 10, '讓皮膚變得像鐵盾般堅硬，大幅提高自己的防禦。'), (843, '三連箭', '３ぼんのや', 'Triple Arrows', 'fighting', '格鬥', '物理', 90, 100, 10, '使出腿技後同時發射３箭。有時會降低對手的防禦或讓對手畏縮。容易擊中要害。'), (844, '群魔亂舞', 'ひゃっきやこう', 'Infernal Parade', 'ghost', '幽靈', '特殊', 60, 100, 15, '用無數的火球進行攻擊，有時會讓對手陷入灼傷狀態。攻擊處於異常狀態的對手時，威力會變成２倍。'), (845, '秘劍・千重濤', 'ひけん・ちえなみ', 'Ceaseless Edge', 'dark', '惡', '物理', 65, 90, 15, '用貝殼之劍進行攻擊。碎裂開的貝殼碎片會變成撒菱散落在對手腳下。'), (846, '枯葉風暴', 'こがらしあらし', 'Bleakwind Storm', 'flying', '飛行', '特殊', 100, 80, 10, '用足以讓身心都止不住顫抖的冰冷狂風進行攻擊。有時會降低對手的速度。'), (847, '鳴雷風暴', 'かみなりあらし', 'Wildbolt Storm', 'electric', '電', '特殊', 100, 80, 10, '呼喚雷雲引起風暴，用雷與風進行激烈的攻擊。有時還會讓對手陷入麻痺狀態。'), (848, '熱沙風暴', 'ねっさのあらし', 'Sandsear Storm', 'ground', '地面', '特殊', 100, 80, 10, '用灼熱的沙子和強烈的風席捲對手進行攻擊。有時會讓對手陷入灼傷狀態。'), (849, '新月祈禱', 'みかづきのいのり', 'Lunar Blessing', 'psychic', '超能力', '變化', 0, 100, 5, '向新月獻上祈禱，回復自己和場上同伴的ＨＰ和狀態。'), (850, '勇氣填充', 'ブレイブチャージ', 'Take Heart', 'psychic', '超能力', '變化', 0, 100, 10, '鼓起衝勁，治癒自己的異常狀態，同時提高自己的特攻和特防。'), (851, '太晶爆發', 'テラバースト', 'Tera Blast', 'normal', '一般', '特殊', 80, 100, 10, '若是在太晶化時，將釋放太晶屬性的能量進行攻擊。會比較自己的攻擊和特攻，以數值較高的一方進行攻擊。（其他屬性）／用攻擊和特攻中數值較高的一方攻擊。對手處於太晶化時效果絕佳。自己的攻擊和特攻會降低。（星晶）'), (852, '線阱', 'スレッドトラップ', 'Silk Trap', 'bug', '蟲', '變化', 0, 100, 10, '布下線製成的陷阱。防住對手攻擊的同時，降低接觸到的對手的速度。'), (853, '下壓踢', 'かかとおとし', 'Axe Kick', 'fighting', '格鬥', '物理', 120, 90, 10, '劈下踢起的腳跟攻擊對手。有時會使對手混亂。沒命中對手時自己會受到傷害。'), (854, '掃墓', 'おはかまいり', 'Last Respects', 'ghost', '幽靈', '物理', 50, 100, 10, '為了替夥伴雪恨而攻擊。被打倒的我方寶可夢越多，招式威力就會越強。'), (855, '琉光衝激', 'ルミナコリジョン', 'Lumina Crash', 'psychic', '超能力', '特殊', 80, 100, 10, '發出對精神也會起作用的奇異光線進行攻擊。大幅降低對手的特防。'), (856, '上菜', 'いっちょうあがり', 'Order Up', 'dragon', '龍', '物理', 80, 100, 10, '以瀟灑的身段進行攻擊。若口中有米立龍，會依米立龍的姿勢而提高不同能力。'), (857, '噴射拳', 'ジェットパンチ', 'Jet Punch', 'water', '水', '物理', 60, 100, 15, '讓激流圍繞在拳頭上，以迅雷不及掩耳之勢出拳。必定能夠發動先制攻擊。'), (858, '辣椒精華', 'ハバネロエキス', 'Spicy Extract', 'grass', '草', '變化', 0, 100, 15, '放出相當強勁的辣味精華。對手的攻擊會大幅提高，防禦會大幅降低。'), (859, '疾速轉輪', 'ホイールスピン', 'Spin Out', 'steel', '鋼', '物理', 100, 100, 5, '藉由在腳上負重來劇烈地旋轉並給予傷害。自己的速度會大幅降低。'), (860, '鼠數兒', 'ネズミざん', 'Population Bomb', 'normal', '一般', '物理', 20, 90, 10, '夥伴們紛紛聚集而來，以群體形式逐一給予攻擊。連續命中１～１０次。'), (861, '冰旋', 'アイススピナー', 'Ice Spinner', 'ice', '冰', '物理', 80, 100, 15, '在腳上包覆薄冰，骨碌碌地旋轉並進行撞擊。以旋轉的動作破壞場地。'), (862, '巨劍突擊', 'きょけんとつげき', 'Glaive Rush', 'dragon', '龍', '物理', 120, 100, 5, '豁出己身的魯莽突擊。使出招式後對手的攻擊必定會命中，且傷害會變成２倍。'), (863, '復生祈禱', 'さいきのいのり', 'Revival Blessing', 'normal', '一般', '變化', 0, 100, 1, '懷著慈愛之心祈禱，使後備中瀕死的寶可夢以一半的ＨＰ復活。'), (864, '鹽醃', 'しおづけ', 'Salt Cure', 'rock', '岩石', '物理', 40, 100, 15, '讓對手陷入鹽醃狀態，每回合都會受到傷害。鋼、水屬性的寶可夢還會更痛苦。'), (865, '三連鑽', 'トリプルダイブ', 'Triple Dive', 'water', '水', '物理', 30, 95, 10, '默契十足地飛身鑽入水中，將水花濺向對手。連續３次給予傷害。'), (866, '晶光轉轉', 'キラースピン', 'Mortal Spin', 'poison', '毒', '物理', 30, 100, 15, '藉由旋轉來攻擊對手。也可以擺脫綁緊、緊束、寄生種子等招式。讓對手陷入中毒狀態。'), (867, '描繪', 'うつしえ', 'Doodle', 'normal', '一般', '變化', 0, 100, 10, '掌握對手的本質，予以描繪，使自己和同伴的特性變得和對手相同。'), (868, '甩肉', 'みをけずる', 'Fillet Away', 'normal', '一般', '變化', 0, 100, 10, '削減自己的ＨＰ，大幅提高自己的攻擊、特攻和速度。'), (869, '仆斬', 'ドゲザン', 'Kowtow Cleave', 'dark', '惡', '物理', 85, 100, 10, '向對手下跪使其大意，接著對其劈斬。攻擊必定會命中。'), (870, '千變萬花', 'トリックフラワー', 'Flower Trick', 'grass', '草', '物理', 70, 100, 10, '將動過手腳的花束投向對手進行攻擊。必定會命中並擊中要害。'), (871, '閃焰高歌', 'フレアソング', 'Torch Song', 'fire', '火', '特殊', 80, 100, 10, '將熊熊燃燒的火焰如唱歌般地吹向對手將其燒焦。會提高自己的特攻。'), (872, '流水旋舞', 'アクアステップ', 'Aqua Step', 'water', '水', '物理', 80, 100, 10, '以盈盈欲滴的輕巧步伐戲弄對手，給予傷害。會提高自己的速度。'), (873, '怒牛', 'レイジングブル', 'Raging Bull', 'normal', '一般', '物理', 90, 100, 10, '憤怒狂亂的暴牛發動之猛烈衝撞。招式的屬性會依形態而改變，並可破壞光牆和反射壁等。'), (874, '淘金潮', 'ゴールドラッシュ', 'Make It Rain', 'steel', '鋼', '特殊', 120, 100, 5, '撒出大量硬幣進行攻擊。自己的特攻會降低。戰鬥後還可以得到錢。'), (875, '精神劍', 'サイコブレイド', 'Psyblade', 'psychic', '超能力', '物理', 80, 100, 15, '用無形的刀刃劈開對手。在電氣場地時，招式威力會變成１.５倍。'), (876, '水蒸氣', 'ハイドロスチーム', 'Hydro Steam', 'water', '水', '特殊', 80, 100, 15, '將滾燙的水猛烈地噴向對手。日照很強時，招式威力不但不會降低，還會變成１.５倍。'), (877, '大災難', 'カタストロフィ', 'Ruination', 'dark', '惡', '特殊', 0, 90, 10, '捲起破滅之災厄，使對手的ＨＰ減半。'), (878, '全開猛撞', 'アクセルブレイク', 'Collision Course', 'fighting', '格鬥', '物理', 100, 100, 5, '一邊變形，一邊氣勢洶洶地落下，引發古代的大爆炸。打到弱點時威力會更為增加。'), (879, '閃電猛衝', 'イナズマドライブ', 'Electro Drift', 'electric', '電', '特殊', 100, 100, 5, '一邊變形，一邊以超高速度奔馳，未知的電流將貫穿對手。打到弱點時威力會更為增加。'), (880, '斷尾', 'しっぽきり', 'Shed Tail', 'normal', '一般', '變化', 0, 100, 10, '削減自己的ＨＰ，製造分身後返回，和後備寶可夢進行替換。'), (881, '冷笑話', 'さむいギャグ', 'Chilly Reception', 'ice', '冰', '變化', 0, 100, 10, '留下使場面凍結的笑話後，和後備寶可夢進行替換。在５回合內會下雪。'), (882, '大掃除', 'おかたづけ', 'Tidy Up', 'normal', '一般', '變化', 0, 100, 10, '將撒菱、隱形岩、黏黏網、毒菱、替身全部清理乾淨。自己的攻擊和速度會提高。'), (883, '雪景', 'ゆきげしき', 'Snowscape', 'ice', '冰', '變化', 0, 100, 10, '在５回合內會下雪。冰屬性寶可夢的防禦會提高。'), (884, '蟲撲', 'とびつく', 'Pounce', 'bug', '蟲', '物理', 50, 100, 20, '撲向對手進行攻擊。可降低對手的速度。'), (885, '起草', 'くさわけ', 'Trailblaze', 'grass', '草', '物理', 50, 100, 20, '從草叢裡跳出來進行攻擊。輕快的腳步會使得自己的速度提高。'), (886, '潑冷水', 'ひやみず', 'Chilling Water', 'water', '水', '特殊', 50, 100, 20, '潑灑冰冷到足以讓對手失去活力的水進行攻擊。可降低對手的攻擊。'), (887, '強力鑽', 'ハイパードリル', 'Hyper Drill', 'normal', '一般', '物理', 100, 100, 5, '高速旋轉尖銳的身體部位進行貫穿。可無視對手的守住和看穿等招式。');