INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (888, '雙光束', 'ツインビーム', 'Twin Beam', 'psychic', '超能力', '特殊', 40, 100, 10, '從雙眼發射不可思議的光線進行攻擊。連續２次給予傷害。'), (889, '憤怒之拳', 'ふんどのこぶし', 'Rage Fist', 'ghost', '幽靈', '物理', 50, 100, 10, '將憤怒化為能量進行攻擊。受到的攻擊次數越多，招式的威力越大。'), (890, '鎧農炮', 'アーマーキャノン', 'Armor Cannon', 'fire', '火', '特殊', 120, 100, 5, '將自身的鎧甲做成熊熊燃燒的砲彈擊發進行攻擊。自己的防禦和特防會降低。'), (891, '悔念劍', 'むねんのつるぎ', 'Bitter Blade', 'fire', '火', '物理', 90, 100, 10, '將對這個世界的牽掛注入劍尖進行劈斬。可以回復給予對手傷害的一半ＨＰ。'), (892, '電光雙擊', 'でんこうそうげき', 'Double Shock', 'electric', '電', '物理', 120, 100, 5, '施放全身的所有電力給予對手巨大的傷害。自己的電屬性將會消失。'), (893, '巨力錘', 'デカハンマー', 'Gigaton Hammer', 'steel', '鋼', '物理', 160, 100, 5, '將大錘連同身體一起揮舞進行攻擊。這個招式無法連續２次使用。'), (894, '復仇', 'ほうふく', 'Comeuppance', 'dark', '惡', '物理', 0, 100, 10, '將發動攻擊前最後受到的招式傷害放大，向對手進行報復。'), (895, '水波刀', 'アクアカッター', 'Aqua Cutter', 'water', '水', '物理', 70, 100, 20, '噴射出經過加壓的水，如刀刃似地劈開對手。容易擊中要害。'), (896, '灼熱暴衝', 'バーンアクセル', 'Blazing Torque', 'fire', '火', '物理', 80, 100, 10, '―――'), (897, '黑暗暴衝', 'ダークアクセル', 'Wicked Torque', 'dark', '惡', '物理', 80, 100, 10, '―――'), (898, '劇毒暴衝', 'ポイズンアクセル', 'Noxious Torque', 'poison', '毒', '物理', 100, 100, 10, '―――'), (899, '格鬥暴衝', 'ファイトアクセル', 'Combat Torque', 'fighting', '格鬥', '物理', 100, 100, 10, '―――'), (900, '魔法暴衝', 'マジカルアクセル', 'Magical Torque', 'fairy', '妖精', '物理', 100, 100, 10, '―――'), (901, '血月', 'ブラッドムーン', 'Blood Moon', 'normal', '一般', '特殊', 140, 100, 5, '從鮮血般赤紅的滿月發射出渾身的氣勢。這個招式無法連續２次使用。'), (902, '刷刷茶炮', 'シャカシャカほう', 'Matcha Gotcha', 'grass', '草', '特殊', 80, 90, 15, '像大炮般發射攪拌過的茶。可以回復給予對手傷害的一半ＨＰ，有時還會讓對手陷入灼傷狀態。'), (903, '糖漿炸彈', 'みずあめボム‎', 'Syrup Bomb‎', 'grass', '草', '特殊', 60, 85, 10, '使黏稠的糖漿爆炸，使對手陷入滿身糖狀態，在３回合內持續降低對手的速度。'), (904, '棘藤棒', 'ツタこんぼう', 'Ivy Cudgel', 'grass', '草', '物理', 100, 100, 10, '用纏繞著藤蔓的棍棒毆打。屬性會隨著所戴的面具而改變。容易擊中要害。'), (905, '電光束', 'エレクトロビーム', 'Electro Shot', 'electric', '電', '特殊', 130, 100, 10, '使用後的第１回合會收集電力來提高特攻，並於第２回合發射出高壓電。天氣為下雨時可立即發射。'), (906, '晶光星群', 'テラクラスター', 'Tera Starstorm', 'normal', '一般', '特殊', 120, 100, 5, '投射結晶之力來掃除敵人。當太樂巴戈斯以星晶形態使出時，能給予所有對手傷害。'), (907, '隨機光', 'きまぐレーザー', 'Fickle Beam', 'dragon', '龍', '特殊', 80, 100, 5, '發射光線進行攻擊。有時會和其他的頭合力發射雷射，讓招式威力變成２倍。'), (908, '火焰守護', 'かえんのまもり', 'Burning Bulwark', 'fire', '火', '變化', 0, 100, 10, '用超高溫的體毛防住對手攻擊，同時讓碰觸到自己的對手灼傷。'), (909, '迅雷', 'じんらい', 'Thunderclap', 'electric', '電', '特殊', 70, 100, 5, '可以搶先對手一步用電擊進行攻擊。若對手使用的不是攻擊招式則會失敗。'), (910, '強刃攻擊', 'パワフルエッジ', 'Mighty Cleave', 'rock', '岩石', '物理', 95, 100, 5, '用積蓄在頭部的光斬斷對手。可不顧對手的守護進行攻擊。'), (911, '迅子利刃', 'タキオンカッター', 'Tachyon Cutter', 'steel', '鋼', '特殊', 50, 100, 10, '連續發射粒子的利刃，連續２次給予傷害。攻擊必定會命中。'), (912, '硬壓', 'ハードプレス', 'Hard Press', 'steel', '鋼', '物理', 0, 100, 10, '用手臂或鉗子壓迫對手。對手現有的ＨＰ越多，招式的威力越大。'), (913, '龍聲鼓舞', 'ドラゴンエール', 'Dragon Cheer', 'dragon', '龍', '變化', 0, 100, 15, '以龍之鼓舞提高士氣。我方的招式會變得容易擊中要害。對龍屬性的鼓舞效果更強。'), (914, '魅誘之聲', 'みわくのボイス', 'Alluring Voice', 'fairy', '妖精', '特殊', 80, 100, 10, '用天使般的歌聲攻擊對手。讓此回合內能力提高的寶可夢陷入混亂狀態。'), (915, '豁出去', 'やけっぱち', 'Temper Flare', 'fire', '火', '物理', 75, 100, 10, '用自暴自棄的狠勁攻擊對手。如果上一回合的招式沒有命中，威力就會加倍。'), (916, '閃電強襲', 'サンダーダイブ', 'Supercell Slam', 'electric', '電', '物理', 100, 95, 15, '讓身體帶電然後壓向對手。沒命中對手時自己會受到傷害。'), (917, '精神噪音', 'サイコノイズ', 'Psychic Noise', 'psychic', '超能力', '特殊', 75, 100, 10, '用令人不快的音波攻擊對手，使對手在２回合內變得無法透過招式、特性或攜帶的道具回復ＨＰ。'), (918, '快手還擊', 'はやてがえし', 'Upper Hand', 'fighting', '格鬥', '物理', 65, 100, 15, '察覺對手動作並以掌底攻擊來反應，讓對手畏縮。若對手使用的招式不是先制攻擊則會失敗。'), (919, '邪毒鎖鏈', 'じゃどくのくさり', 'Malignant Chain', 'poison', '毒', '特殊', 100, 100, 5, '用毒素形成的鎖鏈纏住對手，注入毒素加以侵蝕。有時會讓對手陷入劇毒狀態。'), (920, '歸無之光', 'むにきすひかり', 'Nihil Light', 'dragon', '龍', '特殊', 200, 100, 0, '釋放無視萬物法則的強烈光芒。連妖精屬性的寶可夢也能擊中，可不顧對手的能力變化給予傷害。'), (0, '超極巨地獄滅焰', 'キョダイゴクエン', 'G-Max Wildfire', 'fire', '火', '超極巨', 0, 100, 0, '超極巨化噴火龍使出的火屬性攻擊。可在４回合內給予對手傷害。'), (0, '超極巨蝶影蠱惑', 'キョダイコワク', 'G-Max Befuddle', 'bug', '蟲', '超極巨', 0, 100, 0, '超極巨化巴大蝶使出的蟲屬性攻擊。會讓對手陷入中毒、麻痺或睡眠狀態。'), (0, '超極巨萬雷轟頂', 'キョダイバンライ', 'G-Max Volt Crash', 'electric', '電', '超極巨', 0, 100, 0, '超極巨化皮卡丘使出的電屬性攻擊。會讓對手陷入麻痺狀態。'), (0, '超極巨特大金幣', 'キョダイコバン', 'G-Max Gold Rush', 'normal', '一般', '超極巨', 0, 100, 0, '超極巨化喵喵使出的一般屬性攻擊。會讓對手陷入混亂狀態，還可以獲得金錢。'), (0, '超極巨會心一擊', 'キョダイシンゲキ', 'G-Max Chi Strike', 'fighting', '格鬥', '超極巨', 0, 100, 0, '超極巨化怪力使出的格鬥屬性攻擊。會變得容易擊中要害。'), (0, '超極巨幻影幽魂', 'キョダイゲンエイ', 'G-Max Terror', 'ghost', '幽靈', '超極巨', 0, 100, 0, '超極巨化耿鬼使出的幽靈屬性攻擊。會踩住對手的影子，使其無法進行替換。'), (0, '超極巨極光旋律', 'キョダイセンリツ', 'G-Max Resonance', 'ice', '冰', '超極巨', 0, 100, 0, '超極巨化拉普拉斯使出的冰屬性攻擊。在５回合內減輕所受到的傷害。'), (0, '超極巨熱情擁抱', 'キョダイホーヨー', 'G-Max Cuddle', 'normal', '一般', '超極巨', 0, 100, 0, '超極巨化伊布使出的一般屬性攻擊。會讓對手陷入著迷狀態。'), (0, '超極巨資源再生', 'キョダイサイセイ', 'G-Max Replenish', 'normal', '一般', '超極巨', 0, 100, 0, '超極巨化卡比獸使出的一般屬性攻擊。會讓吃掉的樹果再生。'), (0, '超極巨臭氣沖天', 'キョダイシュウキ', 'G-Max Malodor', 'poison', '毒', '超極巨', 0, 100, 0, '超極巨化灰塵山使出的毒屬性攻擊。會讓對手陷入中毒狀態。'), (0, '超極巨岩陣以待', 'キョダイガンジン', 'G-Max Stonesurge', 'water', '水', '超極巨', 0, 100, 0, '超極巨化暴噬龜使出的水屬性攻擊。會撒出無數的銳利岩石。'), (0, '超極巨旋風襲捲', 'キョダイフウゲキ', 'G-Max Wind Rage', 'flying', '飛行', '超極巨', 0, 100, 0, '超極巨化鋼鎧鴉使出的飛行屬性攻擊。能消除反射壁及光牆。'), (0, '超極巨異毒電場', 'キョダイカンデン', 'G-Max Stun Shock', 'electric', '電', '超極巨', 0, 100, 0, '超極巨化顫弦蠑螈使出的電屬性攻擊。會讓對手陷入中毒或麻痺狀態。'), (0, '超極巨幸福圓滿', 'キョダイダンエン', 'G-Max Finale', 'fairy', '妖精', '超極巨', 0, 100, 0, '超極巨化霜奶仙使出的妖精屬性攻擊。會回復我方寶可夢的ＨＰ。'), (0, '超極巨劣化衰變', 'キョダイゲンスイ', 'G-Max Depletion', 'dragon', '龍', '超極巨', 0, 100, 0, '超極巨化鋁鋼龍使出的龍屬性攻擊。會減少對手最後使用的招式的ＰＰ。'), (0, '超極巨天道七星', 'キョダイテンドウ', 'G-Max Gravitas', 'psychic', '超能力', '超極巨', 0, 100, 0, '超極巨化以歐路普使出的超能力屬性攻擊。在５回合內重力發生變化。'), (0, '超極巨炎石噴發', 'キョダイフンセキ', 'G-Max Volcalith', 'rock', '岩石', '超極巨', 0, 100, 0, '超極巨化巨炭山使出的岩石屬性攻擊。可在４回合內給予對手傷害。');