INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (101, '黑夜魔影', 'ナイトヘッド', 'Night Shade', 'ghost', '幽靈', '特殊', 0, 100, 15, '讓對手看見恐怖幻影，給予對手和自己等級相同的傷害。'), (102, '模仿', 'ものまね', 'Mimic', 'normal', '一般', '變化', 0, 100, 10, '在戰鬥期間，將對手最近一次使用的招式變成自己的招式。'), (103, '刺耳聲', 'いやなおと', 'Screech', 'normal', '一般', '變化', 0, 85, 40, '發出令人不由自主想要摀起耳朵的刺耳聲，大幅降低對手的防禦。'), (104, '影子分身', 'かげぶんしん', 'Double Team', 'normal', '一般', '變化', 0, 100, 15, '利用快速移動製造出分身擾亂對手，提高自己的閃避率。'), (105, '自我再生', 'じこさいせい', 'Recover', 'normal', '一般', '變化', 0, 100, 5, '讓細胞再生，回復自己最大ＨＰ的一半。'), (106, '變硬', 'かたくなる', 'Harden', 'normal', '一般', '變化', 0, 100, 30, '使用全身的力量讓身體硬化，提高自己的防禦。'), (107, '變小', 'ちいさくなる', 'Minimize', 'normal', '一般', '變化', 0, 100, 10, '蜷縮身體使自己顯得很小，大幅提高自己的閃避率。'), (108, '煙幕', 'えんまく', 'Smokescreen', 'normal', '一般', '變化', 0, 100, 20, '向對手噴出煙霧或墨汁等，降低對手的命中率。'), (109, '奇異之光', 'あやしいひかり', 'Confuse Ray', 'ghost', '幽靈', '變化', 0, 100, 10, '讓對手看奇怪的光線擾亂對手。使對手混亂。'), (110, '縮入殼中', 'からにこもる', 'Withdraw', 'water', '水', '變化', 0, 100, 40, '縮入殼中保護身體，提高自己的防禦。'), (111, '變圓', 'まるくなる', 'Defense Curl', 'normal', '一般', '變化', 0, 100, 40, '將身體蜷縮成圓形，提高自己的防禦。'), (112, '屏障', 'バリアー', 'Barrier', 'psychic', '超能力', '變化', 0, 100, 20, '製造堅固的屏障，大幅提高自己的防禦。'), (113, '光牆', 'ひかりのかべ', 'Light Screen', 'psychic', '超能力', '變化', 0, 100, 30, '利用神奇的屏障，在５回合內減輕來自對手的特殊攻擊傷害。'), (114, '黑霧', 'くろいきり', 'Haze', 'ice', '冰', '變化', 0, 100, 30, '放出黑霧，讓場上全體寶可夢的能力變化回到初始狀態。'), (115, '反射壁', 'リフレクター', 'Reflect', 'psychic', '超能力', '變化', 0, 100, 20, '利用神奇的屏障，在５回合內減輕來自對手的物理攻擊傷害。'), (116, '聚氣', 'きあいだめ', 'Focus Energy', 'normal', '一般', '變化', 0, 100, 30, '深呼吸後集中精神。讓自己的攻擊變得容易擊中要害。'), (117, '忍耐', 'がまん', 'Bide', 'normal', '一般', '物理', 0, 100, 10, '在２回合內忍受攻擊，然後將受到的傷害加倍返還給對手。'), (118, '揮指', 'ゆびをふる', 'Metronome', 'normal', '一般', '變化', 0, 100, 10, '揮動手指刺激自己的大腦，從許多的招式中隨機選出１招使出。'), (119, '鸚鵡學舌', 'オウムがえし', 'Mirror Move', 'flying', '飛行', '變化', 0, 100, 20, '模仿對手使用的招式，自己也使用相同招式。'), (120, '自爆', 'じばく', 'Self-Destruct', 'normal', '一般', '物理', 200, 100, 5, '引起爆炸，攻擊自己周圍所有的寶可夢。使用後自己會陷入瀕死。'), (121, '炸蛋', 'タマゴばくだん', 'Egg Bomb', 'normal', '一般', '物理', 100, 75, 10, '向對手用力投擲大大的蛋進行攻擊。'), (122, '舌舔', 'したでなめる', 'Lick', 'ghost', '幽靈', '物理', 30, 100, 30, '用長長的舌頭，舔遍對手進行攻擊。有時會讓對手陷入麻痺狀態。'), (123, '濁霧', 'スモッグ', 'Smog', 'poison', '毒', '特殊', 30, 70, 20, '將骯髒的濃霧吹向對手進行攻擊。有時會讓對手陷入中毒狀態。'), (124, '污泥攻擊', 'ヘドロこうげき', 'Sludge', 'poison', '毒', '特殊', 65, 100, 20, '向對手投擲污泥進行攻擊。有時會讓對手陷入中毒狀態。'), (125, '骨棒', 'ホネこんぼう', 'Bone Club', 'ground', '地面', '物理', 65, 85, 20, '用手中的骨頭毆打對手進行攻擊。有時會使對手畏縮。'), (126, '大字爆炎', 'だいもんじ', 'Fire Blast', 'fire', '火', '特殊', 110, 85, 5, '用大字形狀的火焰燒盡對手。有時會讓對手陷入灼傷狀態。'), (127, '攀瀑', 'たきのぼり', 'Waterfall', 'water', '水', '物理', 80, 100, 15, '以驚人的氣勢撲向對手。有時會讓對手畏縮。'), (128, '貝殼夾擊', 'からではさむ', 'Clamp', 'water', '水', '物理', 35, 85, 15, '用非常堅固且厚實的貝殼，在４～５回合內夾住對手進行攻擊。'), (129, '高速星星', 'スピードスター', 'Swift', 'normal', '一般', '特殊', 60, 100, 20, '發射星型光線攻擊對手。攻擊必定會命中。'), (130, '火箭頭錘', 'ロケットずつき', 'Skull Bash', 'normal', '一般', '物理', 130, 100, 10, '使用後的第１回合把頭後縮，提高防禦。並於第２回合攻擊對手。'), (131, '尖刺加農炮', 'とげキャノン', 'Spike Cannon', 'normal', '一般', '物理', 20, 100, 15, '向對手發射銳利的針進行攻擊。可連續攻擊２～５次。'), (132, '纏繞', 'からみつく', 'Constrict', 'normal', '一般', '物理', 10, 100, 35, '用觸手或青藤等纏繞對手進行攻擊。有時會降低對手的速度。'), (133, '瞬間失憶', 'ドわすれ', 'Amnesia', 'psychic', '超能力', '變化', 0, 100, 20, '將頭腦清空，藉由在一瞬間遺忘某些事大幅提高自己的特防。'), (134, '折彎湯匙', 'スプーンまげ', 'Kinesis', 'psychic', '超能力', '變化', 0, 80, 15, '折彎湯匙引起注意，降低對手的命中率。'), (135, '生蛋', 'タマゴうみ', 'Soft-Boiled', 'normal', '一般', '變化', 0, 100, 5, '回復自己最大ＨＰ的一半。'), (136, '飛膝踢', 'とびひざげり', 'High Jump Kick', 'fighting', '格鬥', '物理', 130, 90, 10, '跳起後用膝蓋踢擊對手。沒踢中對手時自己會受到傷害。'), (137, '大蛇瞪眼', 'へびにらみ', 'Glare', 'normal', '一般', '變化', 0, 100, 30, '用腹部的花紋使對手心生恐懼，讓對手陷入麻痺狀態。'), (138, '食夢', 'ゆめくい', 'Dream Eater', 'psychic', '超能力', '特殊', 100, 100, 15, '吃掉熟睡中對手的夢進行攻擊。可以回復給予對手傷害的一半ＨＰ。'), (139, '毒瓦斯', 'どくガス', 'Poison Gas', 'poison', '毒', '變化', 0, 90, 40, '將毒瓦斯吹到對手的臉上，讓對手陷入中毒狀態。'), (140, '投球', 'たまなげ', 'Barrage', 'normal', '一般', '物理', 15, 85, 20, '向對手投擲球狀物體進行攻擊。可連續攻擊２～５次。'), (141, '吸血', 'きゅうけつ', 'Leech Life', 'bug', '蟲', '物理', 80, 100, 10, '吸取對手的血液進行攻擊。可以回復給予對手傷害的一半ＨＰ。'), (142, '惡魔之吻', 'あくまのキッス', 'Lovely Kiss', 'normal', '一般', '變化', 0, 75, 10, '用恐怖的臉強吻對手。能讓對手陷入睡眠狀態。'), (143, '神鳥猛擊', 'ゴッドバード', 'Sky Attack', 'flying', '飛行', '物理', 140, 90, 5, '在使用後的下一回合進行攻擊。有時會使對手畏縮。且容易擊中要害。'), (144, '變身', 'へんしん', 'Transform', 'normal', '一般', '變化', 0, 100, 10, '變身成對手寶可夢的樣子，能夠使用和對手完全相同的招式。'), (145, '泡沫', 'あわ', 'Bubble', 'water', '水', '特殊', 40, 100, 30, '將無數的泡泡吹向對手進行攻擊。有時會降低對手的速度。'), (146, '迷昏拳', 'ピヨピヨパンチ', 'Dizzy Punch', 'normal', '一般', '物理', 70, 100, 10, '有節奏地出拳攻擊對手。有時會使對手混亂。'), (147, '蘑菇孢子', 'キノコのほうし', 'Spore', 'grass', '草', '變化', 0, 100, 15, '大把撒出有催眠效果的孢子，讓對手陷入睡眠狀態。'), (148, '閃光', 'フラッシュ', 'Flash', 'normal', '一般', '變化', 0, 100, 20, '放出光芒，降低對手的命中率。此外，還能照亮黑暗的洞窟。'), (149, '精神波', 'サイコウェーブ', 'Psywave', 'psychic', '超能力', '特殊', 0, 100, 15, '向對手發射神奇的念力波進行攻擊。每次給予的傷害都不同。'), (150, '躍起', 'はねる', 'Splash', 'normal', '一般', '變化', 0, 100, 40, '不攻擊對手，只是蹦蹦跳跳的，什麼事也不會發生……');