INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (151, '溶化', 'とける', 'Acid Armor', 'poison', '毒', '變化', 0, 100, 20, '藉由細胞的變化讓身體液化，大幅提高自己的防禦。'), (152, '蟹鉗錘', 'クラブハンマー', 'Crabhammer', 'water', '水', '物理', 100, 90, 10, '用巨大的鉗子敲打對手進行攻擊。容易擊中要害。'), (153, '大爆炸', 'だいばくはつ', 'Explosion', 'normal', '一般', '物理', 250, 100, 5, '引起大爆炸，攻擊自己周圍所有的寶可夢。使用後自己會陷入瀕死。'), (154, '亂抓', 'みだれひっかき', 'Fury Swipes', 'normal', '一般', '物理', 18, 80, 15, '用爪子或鐮刀等向對手進行攻擊。可連續攻擊２～５次。'), (155, '骨頭回力鏢', 'ホネブーメラン', 'Bonemerang', 'ground', '地面', '物理', 50, 90, 10, '向對手投擲手中的骨頭，來回連續２次給予傷害。'), (156, '睡覺', 'ねむる', 'Rest', 'psychic', '超能力', '變化', 0, 100, 5, '連續睡２回合。回復自己的全部ＨＰ以及治癒所有異常狀態。'), (157, '岩崩', 'いわなだれ', 'Rock Slide', 'rock', '岩石', '物理', 75, 90, 10, '將大岩石猛烈地砸向對手進行攻擊。有時會讓對手畏縮。'), (158, '必殺門牙', 'ひっさつまえば', 'Hyper Fang', 'normal', '一般', '物理', 80, 90, 15, '用銳利的門牙牢牢咬住對手進行攻擊。有時會使對手畏縮。'), (159, '稜角化', 'かくばる', 'Sharpen', 'normal', '一般', '變化', 0, 100, 30, '增加身體的稜角，變得方方正正，提高自己的攻擊。'), (160, '紋理', 'テクスチャー', 'Conversion', 'normal', '一般', '變化', 0, 100, 30, '將自己的屬性轉換成與已學會的招式中第１個招式相同的屬性。'), (161, '三重攻擊', 'トライアタック', 'Tri Attack', 'normal', '一般', '特殊', 80, 100, 10, '用３種光線進行攻擊。有時會讓對手陷入麻痺、灼傷或冰凍的狀態。'), (162, '憤怒門牙', 'いかりのまえば', 'Super Fang', 'normal', '一般', '物理', 0, 90, 10, '用銳利的門牙猛烈地咬住對手進行攻擊。對手的ＨＰ將會減半。'), (163, '劈開', 'きりさく', 'Slash', 'normal', '一般', '物理', 70, 100, 20, '用爪子或鐮刀等劈開對手進行攻擊。容易擊中要害。'), (164, '替身', 'みがわり', 'Substitute', 'normal', '一般', '變化', 0, 100, 10, '消耗一些自己的ＨＰ，製造分身。分身將成為自己的替身。'), (165, '掙扎', 'わるあがき', 'Struggle', 'normal', '一般', '物理', 50, 100, 0, '在自己的ＰＰ耗盡時，努力掙扎攻擊對手。自己也會受到少許傷害。'), (166, '寫生', 'スケッチ', 'Sketch', 'normal', '一般', '變化', 0, 100, 1, '將對手使用的招式變成自己的。寫生在使用１次之後就會消失。'), (167, '三連踢', 'トリプルキック', 'Triple Kick', 'fighting', '格鬥', '物理', 10, 90, 10, '連續踢對手３次進行攻擊。每踢中一次，威力就會提高。'), (168, '小偷', 'どろぼう', 'Thief', 'dark', '惡', '物理', 60, 100, 25, '在攻擊的同時偷取道具。當寶可夢自己有攜帶道具時，則無法偷取。'), (169, '蛛網', 'クモのす', 'Spider Web', 'bug', '蟲', '變化', 0, 100, 10, '用黏糊糊的細絲一層一層纏住對手，讓對手不能從戰鬥中逃走。'), (170, '心之眼', 'こころのめ', 'Mind Reader', 'normal', '一般', '變化', 0, 100, 5, '用心感受對手的行動模式，下次攻擊時必定會命中對手。'), (171, '惡夢', 'あくむ', 'Nightmare', 'ghost', '幽靈', '變化', 0, 100, 15, '讓處於睡眠狀態的對手作惡夢，每回合減少一部分HP。'), (172, '火焰輪', 'かえんぐるま', 'Flame Wheel', 'fire', '火', '物理', 60, 100, 25, '用火焰圍繞身體，猛撞對手進行攻擊。有時會讓對手陷入灼傷狀態。'), (173, '打鼾', 'いびき', 'Snore', 'normal', '一般', '特殊', 50, 100, 15, '在自己睡著時，發出噪音進行攻擊。有時會讓對手畏縮。'), (174, '詛咒', 'のろい', 'Curse', 'ghost', '幽靈', '變化', 0, 100, 10, '使用詛咒時，幽靈屬性的寶可夢和其他屬性的寶可夢，會獲得不同的招式效果。'), (175, '抓狂', 'じたばた', 'Flail', 'normal', '一般', '物理', 0, 100, 15, '抓狂般亂打進行攻擊。自己的ＨＰ越少，招式的威力越大。'), (176, '紋理２', 'テクスチャー２', 'Conversion 2', 'normal', '一般', '變化', 0, 100, 30, '改變屬性，讓自己能夠抵抗對手最近一次使出的招式。'), (177, '氣旋攻擊', 'エアロブラスト', 'Aeroblast', 'flying', '飛行', '特殊', 100, 95, 5, '向對手發射空氣旋渦進行攻擊。容易擊中要害。'), (178, '棉孢子', 'わたほうし', 'Cotton Spore', 'grass', '草', '變化', 0, 100, 40, '將棉花般柔軟的孢子纏繞對手，大幅降低對手的速度。'), (179, '起死回生', 'きしかいせい', 'Reversal', 'fighting', '格鬥', '物理', 0, 100, 15, '竭盡全力進行攻擊。自己的ＨＰ越少，招式的威力越大。'), (180, '怨恨', 'うらみ', 'Spite', 'ghost', '幽靈', '變化', 0, 100, 10, '向對手最近一次使用的招式發出怨念，讓該招式減少４ＰＰ。'), (181, '細雪', 'こなゆき', 'Powder Snow', 'ice', '冰', '特殊', 40, 100, 25, '將冰冷的細雪吹向對手進行攻擊。有時會讓對手陷入冰凍狀態。'), (182, '守住', 'まもる', 'Protect', 'normal', '一般', '變化', 0, 100, 10, '完全防住對手的所有攻擊。連續使用時容易失敗。'), (183, '音速拳', 'マッハパンチ', 'Mach Punch', 'fighting', '格鬥', '物理', 40, 100, 30, '以迅雷不及掩耳之勢出拳。必定能夠發動先制攻擊。'), (184, '鬼面', 'こわいかお', 'Scary Face', 'normal', '一般', '變化', 0, 100, 10, '用恐怖的表情瞪著對手，讓對手害怕，大幅降低對手的速度。'), (185, '出奇一擊', 'だましうち', 'Feint Attack', 'dark', '惡', '物理', 60, 100, 20, '若無其事地靠近對手，出其不意地進行攻擊。攻擊必定會命中。'), (186, '天使之吻', 'てんしのキッス', 'Sweet Kiss', 'fairy', '妖精', '變化', 0, 75, 10, '像天使般可愛地親吻對手，進而使對手混亂。'), (187, '腹鼓', 'はらだいこ', 'Belly Drum', 'normal', '一般', '變化', 0, 100, 10, '將現有的ＨＰ減去最大ＨＰ的一半，讓自己的攻擊提高至最大值。'), (188, '污泥炸彈', 'ヘドロばくだん', 'Sludge Bomb', 'poison', '毒', '特殊', 90, 100, 10, '向對手投擲污泥進行攻擊。有時會讓對手陷入中毒狀態。'), (189, '擲泥', 'どろかけ', 'Mud-Slap', 'ground', '地面', '特殊', 20, 100, 10, '向對手的臉等處投擲泥巴進行攻擊。可降低對手的命中率。'), (190, '章魚桶炮', 'オクタンほう', 'Octazooka', 'water', '水', '特殊', 65, 85, 10, '向對手的臉等噴出墨汁進行攻擊。有時會降低對手的命中率。'), (191, '撒菱', 'まきびし', 'Spikes', 'ground', '地面', '變化', 0, 100, 20, '將撒菱散布在對手的腳下。讓對手替換出場的寶可夢受到傷害。'), (192, '電磁炮', 'でんじほう', 'Zap Cannon', 'electric', '電', '特殊', 120, 50, 5, '發射如大炮般的電流進行攻擊。能讓對手陷入麻痺狀態。'), (193, '識破', 'みやぶる', 'Foresight', 'normal', '一般', '變化', 0, 100, 40, '讓對幽靈屬性寶可夢無效的招式能夠發揮效果，也可不顧對手提高後的閃避率進行攻擊。'), (194, '同命', 'みちづれ', 'Destiny Bond', 'ghost', '幽靈', '變化', 0, 100, 5, '使出招式後，當受到對手攻擊，陷入瀕死時，對手也會一同陷入瀕死。連續使用時會失敗。'), (195, '滅亡之歌', 'ほろびのうた', 'Perish Song', 'normal', '一般', '變化', 0, 100, 5, '聽了歌聲的寶可夢３回合後將陷入瀕死。替換寶可夢後效果就會消失。'), (196, '冰凍之風', 'こごえるかぜ', 'Icy Wind', 'ice', '冰', '特殊', 55, 95, 15, '將冰冷的寒氣吹向對手進行攻擊。可降低對手的速度。'), (197, '看穿', 'みきり', 'Detect', 'fighting', '格鬥', '變化', 0, 100, 5, '完全防住對手的所有攻擊。連續使用時容易失敗。'), (198, '骨棒亂打', 'ボーンラッシュ', 'Bone Rush', 'ground', '地面', '物理', 25, 90, 10, '用堅硬的骨頭毆打對手進行攻擊。連續攻擊２～５次。'), (199, '鎖定', 'ロックオン', 'Lock-On', 'normal', '一般', '變化', 0, 100, 5, '精確地瞄準，下次攻擊時一定會命中對手。'), (200, '逆鱗', 'げきりん', 'Outrage', 'dragon', '龍', '物理', 120, 100, 10, '在２～３回合內瘋狂亂打對手進行攻擊。大鬧一番後自己會陷入混亂。');