INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (201, '沙暴', 'すなあらし', 'Sandstorm', 'rock', '岩石', '變化', 0, 100, 10, '揚起沙暴，在５回合內給予岩石、地面和鋼屬性以外的寶可夢傷害。岩石屬性寶可夢的特防會提高。'), (202, '終極吸取', 'ギガドレイン', 'Giga Drain', 'grass', '草', '特殊', 75, 100, 10, '吸取對手的養分進行攻擊。可以回復給予對手傷害的一半ＨＰ。'), (203, '挺住', 'こらえる', 'Endure', 'normal', '一般', '變化', 0, 100, 10, '即使受到再強烈的攻擊，也一定會剩下１ＨＰ。連續使用時容易失敗。'), (204, '撒嬌', 'あまえる', 'Charm', 'fairy', '妖精', '變化', 0, 100, 20, '用撒嬌的眼神誘使對手疏忽大意，大幅降低對手的攻擊。'), (205, '滾動', 'ころがる', 'Rollout', 'rock', '岩石', '物理', 30, 90, 20, '在５回合內連續滾動攻擊對手。每擊中一次，威力就會提高。'), (206, '點到為止', 'みねうち', 'False Swipe', 'normal', '一般', '物理', 40, 100, 40, '手下留情地攻擊對手，一定會讓對手剩下１ＨＰ。'), (207, '虛張聲勢', 'いばる', 'Swagger', 'normal', '一般', '變化', 0, 85, 15, '讓對手火大並陷入混亂。但對手的攻擊會因憤怒而大幅提高。'), (208, '喝牛奶', 'ミルクのみ', 'Milk Drink', 'normal', '一般', '變化', 0, 100, 5, '回復自己最大ＨＰ的一半。'), (209, '電光', 'スパーク', 'Spark', 'electric', '電', '物理', 65, 100, 20, '用電流圍繞身體，猛撞對手進行攻擊。有時會讓對手陷入麻痺狀態。'), (210, '連斬', 'れんぞくぎり', 'Fury Cutter', 'bug', '蟲', '物理', 40, 95, 20, '用鐮刀或爪子等斬切對手進行攻擊。連續命中時威力會提高。'), (211, '鋼翼', 'はがねのつばさ', 'Steel Wing', 'steel', '鋼', '物理', 70, 90, 25, '用堅硬的翅膀撞擊對手進行攻擊。有時會提高自己的防禦。'), (212, '黑色目光', 'くろいまなざし', 'Mean Look', 'normal', '一般', '變化', 0, 100, 5, '用漆黑深邃的眼神一動也不動地凝視對手，讓對手無法從戰鬥中逃走。'), (213, '迷人', 'メロメロ', 'Attract', 'normal', '一般', '變化', 0, 100, 15, '♂誘惑♀而♀誘惑♂，讓對手著迷。對手將難以使用招式。'), (214, '夢話', 'ねごと', 'Sleep Talk', 'normal', '一般', '變化', 0, 100, 10, '從自己已學會的招式中隨機選出１個使出。只有在睡眠狀態時可以使用。'), (215, '治癒鈴聲', 'いやしのすず', 'Heal Bell', 'normal', '一般', '變化', 0, 100, 5, '發出悅耳的鈴聲，治癒我方全體的異常狀態。'), (216, '報恩', 'おんがえし', 'Return', 'normal', '一般', '物理', 0, 100, 20, '為了訓練家而全力攻擊對手。親密度越高，威力越大。'), (217, '禮物', 'プレゼント', 'Present', 'normal', '一般', '物理', 0, 90, 15, '將暗藏機關的盒子遞給對手進行攻擊。但也有可能回復對手的ＨＰ。'), (218, '遷怒', 'やつあたり', 'Frustration', 'normal', '一般', '物理', 0, 100, 20, '為了發洩不滿而全力攻擊對手。親密度越低，威力越大。'), (219, '神秘守護', 'しんぴのまもり', 'Safeguard', 'normal', '一般', '變化', 0, 100, 25, '在５回合內被神奇的力量守護，不會陷入異常狀態。'), (220, '分擔痛楚', 'いたみわけ', 'Pain Split', 'normal', '一般', '變化', 0, 100, 20, '將自己的ＨＰ和對手的ＨＰ相加，然後雙方友好地平分。'), (221, '神聖之火', 'せいなるほのお', 'Sacred Fire', 'fire', '火', '物理', 100, 95, 5, '用神秘的火焰燒盡對手進行攻擊。有時會讓對手陷入灼傷狀態。'), (222, '震級', 'マグニチュード', 'Magnitude', 'ground', '地面', '物理', 0, 100, 30, '晃動地面，攻擊自己周圍所有的寶可夢。招式的威力有多種變化。'), (223, '爆裂拳', 'ばくれつパンチ', 'Dynamic Punch', 'fighting', '格鬥', '物理', 100, 50, 5, '使出渾身力量出拳進行攻擊。必定會使對手混亂。'), (224, '超級角擊', 'メガホーン', 'Megahorn', 'bug', '蟲', '物理', 120, 85, 10, '用堅硬且華麗的角狠狠地刺進對手進行攻擊。'), (225, '龍息', 'りゅうのいぶき', 'Dragon Breath', 'dragon', '龍', '特殊', 60, 100, 20, '將強烈的氣息吹向對手進行攻擊。有時會讓對手陷入麻痺狀態。'), (226, '接棒', 'バトンタッチ', 'Baton Pass', 'normal', '一般', '變化', 0, 100, 40, '與後備寶可夢進行替換。替換上場的寶可夢，將承接目前寶可夢的能力變化。'), (227, '再來一次', 'アンコール', 'Encore', 'normal', '一般', '變化', 0, 100, 5, '要求對手再來一次，讓對手連續３次使出最近一次使用的招式。'), (228, '追打', 'おいうち', 'Pursuit', 'dark', '惡', '物理', 40, 100, 20, '在對手替換寶可夢時使用此招式的話，便能以２倍的威力進行攻擊。'), (229, '高速旋轉', 'こうそくスピン', 'Rapid Spin', 'normal', '一般', '物理', 50, 100, 40, '藉由旋轉來攻擊對手。也可以擺脫綁緊、緊束、寄生種子等招式。自己的速度也會提高。'), (230, '甜甜香氣', 'あまいかおり', 'Sweet Scent', 'normal', '一般', '變化', 0, 100, 20, '用香氣大幅降低對手的閃避率。'), (231, '鐵尾', 'アイアンテール', 'Iron Tail', 'steel', '鋼', '物理', 100, 75, 15, '將堅硬的尾巴甩向對手進行攻擊。有時會降低對手的防禦。'), (232, '金屬爪', 'メタルクロー', 'Metal Claw', 'steel', '鋼', '物理', 50, 95, 35, '用鋼鐵之爪撕裂對手進行攻擊。有時會提高自己的攻擊。'), (233, '借力摔', 'あてみなげ', 'Vital Throw', 'fighting', '格鬥', '物理', 70, 100, 10, '攻擊的順序會在對手之後。但是自己的攻擊必定會命中。'), (234, '晨光', 'あさのひざし', 'Morning Sun', 'normal', '一般', '變化', 0, 100, 5, '回復自己的ＨＰ。回復量會隨著天氣的不同而有所變化。'), (235, '光合作用', 'こうごうせい', 'Synthesis', 'grass', '草', '變化', 0, 100, 5, '回復自己的ＨＰ。回復量會隨著天氣的不同而有所變化。'), (236, '月光', 'つきのひかり', 'Moonlight', 'fairy', '妖精', '變化', 0, 100, 5, '回復自己的ＨＰ。回復量會隨著天氣的不同而有所變化。'), (237, '覺醒力量', 'めざめるパワー', 'Hidden Power', 'normal', '一般', '特殊', 60, 100, 15, '招式的屬性會隨著使用此招式的寶可夢而改變。'), (238, '十字劈', 'クロスチョップ', 'Cross Chop', 'fighting', '格鬥', '物理', 100, 80, 5, '用左右手刀劈打對手進行攻擊。容易擊中要害。'), (239, '龍捲風', 'たつまき', 'Twister', 'dragon', '龍', '特殊', 40, 100, 20, '刮起龍捲風，將對手捲入進行攻擊。有時會使對手畏縮。'), (240, '求雨', 'あまごい', 'Rain Dance', 'water', '水', '變化', 0, 100, 5, '降下大雨，在５回合內提高水屬性招式的威力。火屬性招式的威力會降低。'), (241, '大晴天', 'にほんばれ', 'Sunny Day', 'fire', '火', '變化', 0, 100, 5, '讓日照增強，在５回合內提高火屬性招式的威力。水屬性招式的威力會降低。'), (242, '咬碎', 'かみくだく', 'Crunch', 'dark', '惡', '物理', 80, 100, 15, '用尖銳的牙齒咬碎對手進行攻擊。有時會降低對手的防禦。'), (243, '鏡面反射', 'ミラーコート', 'Mirror Coat', 'psychic', '超能力', '特殊', 0, 100, 20, '將來自對手的特殊攻擊傷害加倍後，返還給該對手。'), (244, '自我暗示', 'じこあんじ', 'Psych Up', 'normal', '一般', '變化', 0, 100, 10, '向自己施加自我暗示，讓能力變化的狀態變得與對手相同。'), (245, '神速', 'しんそく', 'Extreme Speed', 'normal', '一般', '物理', 80, 100, 5, '以迅雷不及掩耳之勢猛撞向對手進行攻擊。必定能夠發動先制攻擊。'), (246, '原始之力', 'げんしのちから', 'Ancient Power', 'rock', '岩石', '特殊', 60, 100, 5, '用原始之力進行攻擊。有時會提高自己的所有能力。'), (247, '暗影球', 'シャドーボール', 'Shadow Ball', 'ghost', '幽靈', '特殊', 80, 100, 15, '投擲一團黑影進行攻擊。有時會降低對手的特防。'), (248, '預知未來', 'みらいよち', 'Future Sight', 'psychic', '超能力', '特殊', 120, 100, 10, '使用招式的２回合後，向對手發送一團念力進行攻擊。'), (249, '碎岩', 'いわくだき', 'Rock Smash', 'fighting', '格鬥', '物理', 40, 100, 15, '用拳頭進行攻擊。有時會降低對手的防禦。'), (250, '潮旋', 'うずしお', 'Whirlpool', 'water', '水', '特殊', 35, 85, 15, '將對手困在激烈的水流漩渦中，在４～５回合內進行攻擊。');