INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (301, '冰球', 'アイスボール', 'Ice Ball', 'ice', '冰', '物理', 30, 90, 20, '在５回合內不斷攻擊對手。每擊中一次，威力就會提高。'), (302, '尖刺臂', 'ニードルアーム', 'Needle Arm', 'grass', '草', '物理', 60, 100, 15, '用帶刺的手臂猛烈揮打進行攻擊。有時會使對手畏縮。'), (303, '偷懶', 'なまける', 'Slack Off', 'normal', '一般', '變化', 0, 100, 5, '偷懶休息。回復自己最大ＨＰ的一半。'), (304, '巨聲', 'ハイパーボイス', 'Hyper Voice', 'normal', '一般', '特殊', 90, 100, 10, '製造出震耳欲聾的巨大聲波攻擊對手。'), (305, '劇毒牙', 'どくどくのキバ', 'Poison Fang', 'poison', '毒', '物理', 50, 100, 15, '用有毒的牙齒咬住對手進行攻擊。有時會讓對手陷入劇毒狀態。'), (306, '撕裂爪', 'ブレイククロー', 'Crush Claw', 'normal', '一般', '物理', 75, 95, 10, '用堅硬的銳爪撕裂對手進行攻擊。有時會降低對手的防禦。'), (307, '爆炸烈焰', 'ブラストバーン', 'Blast Burn', 'fire', '火', '特殊', 150, 90, 5, '用爆炸的火焰燒盡對手進行攻擊。下一回合自己將無法動彈。'), (308, '加農水炮', 'ハイドロカノン', 'Hydro Cannon', 'water', '水', '特殊', 150, 90, 5, '向對手發射水炮進行攻擊。下一回合自己將無法動彈。'), (309, '彗星拳', 'コメットパンチ', 'Meteor Mash', 'steel', '鋼', '物理', 90, 90, 10, '使出如同彗星般的拳頭攻擊對手。有時會提高自己的攻擊。'), (310, '驚嚇', 'おどろかす', 'Astonish', 'ghost', '幽靈', '物理', 30, 100, 15, '用巨大的聲響等突然驚嚇對手進行攻擊。有時會讓對手畏縮。'), (311, '氣象球', 'ウェザーボール', 'Weather Ball', 'normal', '一般', '特殊', 50, 100, 10, '招式屬性和威力會隨著使用時天氣的不同而改變。'), (312, '芳香治療', 'アロマセラピー', 'Aromatherapy', 'grass', '草', '變化', 0, 100, 5, '釋放出宜人的香氣，治癒我方全體的異常狀態。'), (313, '假哭', 'うそなき', 'Fake Tears', 'dark', '惡', '變化', 0, 100, 20, '裝哭掉眼淚。讓對手不知所措，大幅降低對手的特防。'), (314, '空氣利刃', 'エアカッター', 'Air Cutter', 'flying', '飛行', '特殊', 60, 95, 25, '用鋒利的風刃斬切對手進行攻擊。容易擊中要害。'), (315, '過熱', 'オーバーヒート', 'Overheat', 'fire', '火', '特殊', 130, 90, 5, '釋放全部能量攻擊對手。使用後因為反作用力，自己的特攻會大幅降低。'), (316, '氣味偵測', 'かぎわける', 'Odor Sleuth', 'normal', '一般', '變化', 0, 100, 40, '讓對幽靈屬性寶可夢無效的招式能夠發揮效果，也可不顧對手提高後的閃避率進行攻擊。'), (317, '岩石封鎖', 'がんせきふうじ', 'Rock Tomb', 'rock', '岩石', '物理', 60, 95, 15, '投擲岩石進行攻擊。封住對手的行動，進而降低其速度。'), (318, '銀色旋風', 'ぎんいろのかぜ', 'Silver Wind', 'bug', '蟲', '特殊', 60, 100, 5, '乘著風飄送鱗粉攻擊對手。有時會提高自己的所有能力。'), (319, '金屬音', 'きんぞくおん', 'Metal Sound', 'steel', '鋼', '變化', 0, 85, 40, '