101, '黑夜魔影', 'ナイトヘッド', 'Night Shade', 'ghost', '幽靈', '特殊', 0, 100, 15, '讓對手看見恐怖幻影，給予對手和自己等級相同的傷害。'), (102, '模仿', 'ものまね', 'Mimic', 'normal', '一般', '變化', 0, 100, 10, '在戰鬥期間，將對手最近一次使用的招式變成自己的招式。'), (103, '刺耳聲', 'いやなおと', 'Screech', 'normal', '一般', '變化', 0, 85, 40, '發出令人不由自主想要摀起耳朵的刺耳聲，大幅降低對手的防禦。'), (104, '影子分身', 'かげぶんしん', 'Double Team', 'normal', '一般', '變化', 0, 100, 15, '利用快速移動製造出分身擾亂對手，提高自己的閃避率。'), (105, '自我再生', 'じこさいせい', 'Recover', 'normal', '一般', '變化', 0, 100, 5, '讓細胞再生，回復自己最大ＨＰ的一半。'), (106, '變硬', 'かたくなる', 'Harden', 'normal', '一般', '變化', 0, 100, 30, '使用全身的力量讓身體硬化，提高自己的防禦。'), (107, '變小', 'ちいさくなる', 'Minimize', 'normal', '一般', '變化', 0, 100, 10, '蜷縮身體使自己顯得很小，大幅提高自己的閃避率。'), (108, '煙幕', 'えんまく', 'Smokescreen', 'normal', '一般', '變化', 0, 100, 20, '向對手噴出煙霧或墨汁等，降低對手的命中率。'), (109, '奇異之光', 'あやしいひかり', 'Confuse Ray', 'ghost', '幽靈', '變化', 0, 100, 10, '讓對手看奇怪的光線擾亂對手。使對手混亂。'), (110, '縮入殼中', 'からにこもる', 'Withdraw', 'water', '水', '變化', 0, 100, 40, '縮入殼中保護身體，提高自己的防禦。'), (111, '變圓', 'まるくなる', 'Defense Curl', 'normal', '一般', '變化', 0, 100, 40, '將身體蜷縮成圓形，提高自己的防禦。'), (112, '屏障', 'バリアー', 'Barrier', 'psychic', '超能力', '變化', 0, 100, 20, '製造堅固的屏障，大幅提高自己的防禦。'), (113, '光牆', 'ひかりのかべ', 'Light Screen', 'psychic', '超能力', '變化', 0, 100, 30, '利用神奇的屏障，在５回合內減輕來自對手的特殊攻擊傷害。'), (114, '黑霧', 'くろいきり', 'Haze', 'ice', '冰', '變化', 0, 100, 30, '放出黑霧，讓場上全體寶可夢的能力變化回到初始狀態。'), (115, '反射壁', 'リフレクター', 'Reflect', 'psychic', '超能力', '變化', 0, 100, 20, '利用神奇的屏障，在５回合內減輕來自對手的物理攻擊傷害。'), (116, '聚氣', 'きあいだめ', 'Focus Energy', 'normal', '一般', '變化', 0, 100, 30, '深呼吸後集中精神。讓自己的攻擊變得容易擊中要害。'), (117, '忍耐', 'がまん', 'Bide', 'normal', '一般', '物理', 0, 100, 10, '在２回合內忍受攻擊，然後將受到的傷害加倍返還給對手。'), (118, '揮指', 'ゆびをふる', 'Metronome', 'normal', '一般', '變化', 0, 100, 10, '揮動手指刺激自己的大腦，從許多的招式中隨機選出１招使出。'), (119, '鸚鵡學舌', 'オウムがえし', 'Mirror Move', 'flying', '飛行', '變化', 0, 100, 20, '模仿對手使用的招式，自己也使用相同招式。'), (120, '自爆', 'じばく', 'Self-Destruct', 'normal', '一般', '物理', 200, 100, 5, '引起爆炸，攻擊自己周圍所有的寶可夢。使用後自己會陷入瀕死。'), (121, '炸蛋', 'タマゴばくだん', 'Egg Bomb', 'normal', '一般', '物理', 100, 75, 10, '向對手用力投擲大大的蛋進行攻擊。'), (122, '舌舔', 'したでなめる', 'Lick', 'ghost', '幽靈', '物理', 30, 100, 30, '用長長的舌頭，舔遍對手進行攻擊。有時會讓對手陷入麻痺狀態。'), (123, '濁霧', 'スモッグ', 'Smog', 'poison', '毒', '特殊', 30, 70, 20, '將骯髒的濃霧吹向對手進行攻擊。有時會讓對手陷入中毒狀態。'), (124, '污泥攻擊', 'ヘドロこうげき', 'Sludge', 'poison', '毒', '特殊', 65, 100, 20, '向對手投擲污泥進行攻擊。有時會讓對手陷入中毒狀態。'), (125, '骨棒', 'ホネこんぼう', 'Bone Club', 'ground', '地面', '物理', 65, 85, 20, '用手中的骨頭毆打對手進行攻擊。有時會使對手畏縮。'), (126, '大字爆炎', 'だいもんじ', 'Fire Blast', 'fire', '火', '特殊', 110, 85, 5, '用大字形狀的火焰燒盡對手。有時會讓對手陷入灼傷狀態。'), (127, '攀瀑', 'たきのぼり', 'Waterfall', 'water', '水', '物理', 80, 100, 15, '以驚人的氣勢撲向對手。有時會讓對手畏縮。'), (128, '貝殼夾擊', 'からではさむ', 'Clamp', 'water', '水', '物理', 35, 85, 15, '用非常堅固且厚實的貝殼，在４～５回合內夾住對手進行攻擊。'), (129, '高速星星', 'スピードスター', 'Swift', 'normal', '一般', '特殊', 60, 100, 20, '發射星型光線攻擊對手。攻擊必定會命中。'), (130, '火箭頭錘', 'ロケットずつき', 'Skull Bash', 'normal', '一般', '物理', 130, 100, 10, '使用後的第１回合把頭後縮，提高防禦。並於第２回合攻擊對手。'), (131, '尖刺加農炮', 'とげキャノン', 'Spike Cannon', 'normal', '一般', '物理', 20, 100, 15, '向對手發射銳利的針進行攻擊。可連續攻擊２～５次。'), (132, '纏繞', 'からみつく', 'Constrict', 'normal', '一般', '物理', 10, 100, 35, '用觸手或青藤等纏繞對手進行攻擊。有時會降低對手的速度。'), (133, '瞬間失憶', 'ドわすれ', 'Amnesia', 'psychic', '超能力', '變化', 0, 100, 20, '將頭腦清空，藉由在一瞬間遺忘某些事大幅提高自己的特防。'), (134, '折彎湯匙', 'スプーンまげ', 'Kinesis', 'psychic', '超能力', '變化', 0, 80, 15, '折彎湯匙引起注意，降低對手的命中率。'), (135, '生蛋', 'タマゴうみ', 'Soft-Boiled', 'normal', '一般', '變化', 0, 100, 5, '回復自己最大ＨＰ的一半。'), (136, '飛膝踢', 'とびひざげり', 'High Jump Kick', 'fighting', '格鬥', '物理', 130, 90, 10, '跳起後用膝蓋踢擊對手。沒踢中對手時自己會受到傷害。'), (137, '大蛇瞪眼', 'へびにらみ', 'Glare', 'normal', '一般', '變化', 0, 100, 30, '用腹部的花紋使對手心生恐懼，讓對手陷入麻痺狀態。'), (138, '食夢', 'ゆめくい', 'Dream Eater', 'psychic', '超能力', '特殊', 100, 100, 15, '吃掉熟睡中對手的夢進行攻擊。可以回復給予對手傷害的一半ＨＰ。'), (139, '毒瓦斯', 'どくガス', 'Poison Gas', 'poison', '毒', '變化', 0, 90, 40, '將毒瓦斯吹到對手的臉上，讓對手陷入中毒狀態。'), (140, '投球', 'たまなげ', 'Barrage', 'normal', '一般', '物理', 15, 85, 20, '向對手投擲球狀物體進行攻擊。可連續攻擊２～５次。'), (141, '吸血', 'きゅうけつ', 'Leech Life', 'bug', '蟲', '物理', 80, 100, 10, '吸取對手的血液進行攻擊。可以回復給予對手傷害的一半ＨＰ。'), (142, '惡魔之吻', 'あくまのキッス', 'Lovely Kiss', 'normal', '一般', '變化', 0, 75, 10, '用恐怖的臉強吻對手。能讓對手陷入睡眠狀態。'), (143, '神鳥猛擊', 'ゴッドバード', 'Sky Attack', 'flying', '飛行', '物理', 140, 90, 5, '在使用後的下一回合進行攻擊。有時會使對手畏縮。且容易擊中要害。'), (144, '變身', 'へんしん', 'Transform', 'normal', '一般', '變化', 0, 100, 10, '變身成對手寶可夢的樣子，能夠使用和對手完全相同的招式。'), (145, '泡沫', 'あわ', 'Bubble', 'water', '水', '特殊', 40, 100, 30, '將無數的泡泡吹向對手進行攻擊。有時會降低對手的速度。'), (146, '迷昏拳', 'ピヨピヨパンチ', 'Dizzy Punch', 'normal', '一般', '物理', 70, 100, 10, '有節奏地出拳攻擊對手。有時會使對手混亂。'), (147, '蘑菇孢子', 'キノコのほうし', 'Spore', 'grass', '草', '變化', 0, 100, 15, '大把撒出有催眠效果的孢子，讓對手陷入睡眠狀態。'), (148, '閃光', 'フラッシュ', 'Flash', 'normal', '一般', '變化', 0, 100, 20, '放出光芒，降低對手的命中率。此外，還能照亮黑暗的洞窟。'), (149, '精神波', 'サイコウェーブ', 'Psywave', 'psychic', '超能力', '特殊', 0, 100, 15, '向對手發射神奇的念力波進行攻擊。每次給予的傷害都不同。'), (150, '躍起', 'はねる', 'Splash', 'normal', '一般', '變化', 0, 100, 40, '不攻擊對手，只是蹦蹦跳跳的，什麼事也不會發生……');INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (151, '溶化', 'とける', 'Acid Armor', 'poison', '毒', '變化', 0, 100, 20, '藉由細胞的變化讓身體液化，大幅提高自己的防禦。'), (152, '蟹鉗錘', 'クラブハンマー', 'Crabhammer', 'water', '水', '物理', 100, 90, 10, '用巨大的鉗子敲打對手進行攻擊。容易擊中要害。'), (153, '大爆炸', 'だいばくはつ', 'Explosion', 'normal', '一般', '物理', 250, 100, 5, '引起大爆炸，攻擊自己周圍所有的寶可夢。使用後自己會陷入瀕死。'), (154, '亂抓', 'みだれひっかき', 'Fury Swipes', 'normal', '一般', '物理', 18, 80, 15, '用爪子或鐮刀等向對手進行攻擊。可連續攻擊２～５次。'), (155, '骨頭回力鏢', 'ホネブーメラン', 'Bonemerang', 'ground', '地面', '物理', 50, 90, 10, '向對手投擲手中的骨頭，來回連續２次給予傷害。'), (156, '睡覺', 'ねむる', 'Rest', 'psychic', '超能力', '變化', 0, 100, 5, '連續睡２回合。回復自己的全部ＨＰ以及治癒所有異常狀態。'), (157, '岩崩', 'いわなだれ', 'Rock Slide', 'rock', '岩石', '物理', 75, 90, 10, '將大岩石猛烈地砸向對手進行攻擊。有時會讓對手畏縮。'), (158, '必殺門牙', 'ひっさつまえば', 'Hyper Fang', 'normal', '一般', '物理', 80, 90, 15, '用銳利的門牙牢牢咬住對手進行攻擊。有時會使對手畏縮。'), (159, '稜角化', 'かくばる', 'Sharpen', 'normal', '一般', '變化', 0, 100, 30, '增加身體的稜角，變得方方正正，提高自己的攻擊。'), (160, '紋理', 'テクスチャー', 'Conversion', 'normal', '一般', '變化', 0, 100, 30, '將自己的屬性轉換成與已學會的招式中第１個招式相同的屬性。'), (161, '三重攻擊', 'トライアタック', 'Tri Attack', 'normal', '一般', '特殊', 80, 100, 10, '用３種光線進行攻擊。有時會讓對手陷入麻痺、灼傷或冰凍的狀態。'), (162, '憤怒門牙', 'いかりのまえば', 'Super Fang', 'normal', '一般', '物理', 0, 90, 10, '用銳利的門牙猛烈地咬住對手進行攻擊。對手的ＨＰ將會減半。'), (163, '劈開', 'きりさく', 'Slash', 'normal', '一般', '物理', 70, 100, 20, '用爪子或鐮刀等劈開對手進行攻擊。容易擊中要害。'), (164, '替身', 'みがわり', 'Substitute', 'normal', '一般', '變化', 0, 100, 10, '消耗一些自己的ＨＰ，製造分身。分身將成為自己的替身。'), (165, '掙扎', 'わるあがき', 'Struggle', 'normal', '一般', '物理', 50, 100, 0, '在自己的ＰＰ耗盡時，努力掙扎攻擊對手。自己也會受到少許傷害。'), (166, '寫生', 'スケッチ', 'Sketch', 'normal', '一般', '變化', 0, 100, 1, '將對手使用的招式變成自己的。寫生在使用１次之後就會消失。'), (167, '三連踢', 'トリプルキック', 'Triple Kick', 'fighting', '格鬥', '物理', 10, 90, 10, '連續踢對手３次進行攻擊。每踢中一次，威力就會提高。'), (168, '小偷', 'どろぼう', 'Thief', 'dark', '惡', '物理', 60, 100, 25, '在攻擊的同時偷取道具。當寶可夢自己有攜帶道具時，則無法偷取。'), (169, '蛛網', 'クモのす', 'Spider Web', 'bug', '蟲', '變化', 0, 100, 10, '用黏糊糊的細絲一層一層纏住對手，讓對手不能從戰鬥中逃走。'), (170, '心之眼', 'こころのめ', 'Mind Reader', 'normal', '一般', '變化', 0, 100, 5, '用心感受對手的行動模式，下次攻擊時必定會命中對手。'), (171, '惡夢', 'あくむ', 'Nightmare', 'ghost', '幽靈', '變化', 0, 100, 15, '讓處於睡眠狀態的對手作惡夢，每回合減少一部分HP。'), (172, '火焰輪', 'かえんぐるま', 'Flame Wheel', 'fire', '火', '物理', 60, 100, 25, '用火焰圍繞身體，猛撞對手進行攻擊。有時會讓對手陷入灼傷狀態。'), (173, '打鼾', 'いびき', 'Snore', 'normal', '一般', '特殊', 50, 100, 15, '在自己睡著時，發出噪音進行攻擊。有時會讓對手畏縮。'), (174, '詛咒', 'のろい', 'Curse', 'ghost', '幽靈', '變化', 0, 100, 10, '使用詛咒時，幽靈屬性的寶可夢和其他屬性的寶可夢，會獲得不同的招式效果。'), (175, '抓狂', 'じたばた', 'Flail', 'normal', '一般', '物理', 0, 100, 15, '抓狂般亂打進行攻擊。自己的ＨＰ越少，招式的威力越大。'), (176, '紋理２', 'テクスチャー２', 'Conversion 2', 'normal', '一般', '變化', 0, 100, 30, '改變屬性，讓自己能夠抵抗對手最近一次使出的招式。'), (177, '氣旋攻擊', 'エアロブラスト', 'Aeroblast', 'flying', '飛行', '特殊', 100, 95, 5, '向對手發射空氣旋渦進行攻擊。容易擊中要害。'), (178, '棉孢子', 'わたほうし', 'Cotton Spore', 'grass', '草', '變化', 0, 100, 40, '將棉花般柔軟的孢子纏繞對手，大幅降低對手的速度。'), (179, '起死回生', 'きしかいせい', 'Reversal', 'fighting', '格鬥', '物理', 0, 100, 15, '竭盡全力進行攻擊。自己的ＨＰ越少，招式的威力越大。'), (180, '怨恨', 'うらみ', 'Spite', 'ghost', '幽靈', '變化', 0, 100, 10, '向對手最近一次使用的招式發出怨念，讓該招式減少４ＰＰ。'), (181, '細雪', 'こなゆき', 'Powder Snow', 'ice', '冰', '特殊', 40, 100, 25, '將冰冷的細雪吹向對手進行攻擊。有時會讓對手陷入冰凍狀態。'), (182, '守住', 'まもる', 'Protect', 'normal', '一般', '變化', 0, 100, 10, '完全防住對手的所有攻擊。連續使用時容易失敗。'), (183, '音速拳', 'マッハパンチ', 'Mach Punch', 'fighting', '格鬥', '物理', 40, 100, 30, '以迅雷不及掩耳之勢出拳。必定能夠發動先制攻擊。'), (184, '鬼面', 'こわいかお', 'Scary Face', 'normal', '一般', '變化', 0, 100, 10, '用恐怖的表情瞪著對手，讓對手害怕，大幅降低對手的速度。'), (185, '出奇一擊', 'だましうち', 'Feint Attack', 'dark', '惡', '物理', 60, 100, 20, '若無其事地靠近對手，出其不意地進行攻擊。攻擊必定會命中。'), (186, '天使之吻', 'てんしのキッス', 'Sweet Kiss', 'fairy', '妖精', '變化', 0, 75, 10, '像天使般可愛地親吻對手，進而使對手混亂。'), (187, '腹鼓', 'はらだいこ', 'Belly Drum', 'normal', '一般', '變化', 0, 100, 10, '將現有的ＨＰ減去最大ＨＰ的一半，讓自己的攻擊提高至最大值。'), (188, '污泥炸彈', 'ヘドロばくだん', 'Sludge Bomb', 'poison', '毒', '特殊', 90, 100, 10, '向對手投擲污泥進行攻擊。有時會讓對手陷入中毒狀態。'), (189, '擲泥', 'どろかけ', 'Mud-Slap', 'ground', '地面', '特殊', 20, 100, 10, '向對手的臉等處投擲泥巴進行攻擊。可降低對手的命中率。'), (190, '章魚桶炮', 'オクタンほう', 'Octazooka', 'water', '水', '特殊', 65, 85, 10, '向對手的臉等噴出墨汁進行攻擊。有時會降低對手的命中率。'), (191, '撒菱', 'まきびし', 'Spikes', 'ground', '地面', '變化', 0, 100, 20, '將撒菱散布在對手的腳下。讓對手替換出場的寶可夢受到傷害。'), (192, '電磁炮', 'でんじほう', 'Zap Cannon', 'electric', '電', '特殊', 120, 50, 5, '發射如大炮般的電流進行攻擊。能讓對手陷入麻痺狀態。'), (193, '識破', 'みやぶる', 'Foresight', 'normal', '一般', '變化', 0, 100, 40, '讓對幽靈屬性寶可夢無效的招式能夠發揮效果，也可不顧對手提高後的閃避率進行攻擊。'), (194, '同命', 'みちづれ', 'Destiny Bond', 'ghost', '幽靈', '變化', 0, 100, 5, '使出招式後，當受到對手攻擊，陷入瀕死時，對手也會一同陷入瀕死。連續使用時會失敗。'), (195, '滅亡之歌', 'ほろびのうた', 'Perish Song', 'normal', '一般', '變化', 0, 100, 5, '聽了歌聲的寶可夢３回合後將陷入瀕死。替換寶可夢後效果就會消失。'), (196, '冰凍之風', 'こごえるかぜ', 'Icy Wind', 'ice', '冰', '特殊', 55, 95, 15, '將冰冷的寒氣吹向對手進行攻擊。可降低對手的速度。'), (197, '看穿', 'みきり', 'Detect', 'fighting', '格鬥', '變化', 0, 100, 5, '完全防住對手的所有攻擊。連續使用時容易失敗。'), (198, '骨棒亂打', 'ボーンラッシュ', 'Bone Rush', 'ground', '地面', '物理', 25, 90, 10, '用堅硬的骨頭毆打對手進行攻擊。連續攻擊２～５次。'), (199, '鎖定', 'ロックオン', 'Lock-On', 'normal', '一般', '變化', 0, 100, 5, '精確地瞄準，下次攻擊時一定會命中對手。'), (200, '逆鱗', 'げきりん', 'Outrage', 'dragon', '龍', '物理', 120, 100, 10, '在２～３回合內瘋狂亂打對手進行攻擊。大鬧一番後自己會陷入混亂。');INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (201, '沙暴', 'すなあらし', 'Sandstorm', 'rock', '岩石', '變化', 0, 100, 10, '揚起沙暴，在５回合內給予岩石、地面和鋼屬性以外的寶可夢傷害。岩石屬性寶可夢的特防會提高。'), (202, '終極吸取', 'ギガドレイン', 'Giga Drain', 'grass', '草', '特殊', 75, 100, 10, '吸取對手的養分進行攻擊。可以回復給予對手傷害的一半ＨＰ。'), (203, '挺住', 'こらえる', 'Endure', 'normal', '一般', '變化', 0, 100, 10, '即使受到再強烈的攻擊，也一定會剩下１ＨＰ。連續使用時容易失敗。'), (204, '撒嬌', 'あまえる', 'Charm', 'fairy', '妖精', '變化', 0, 100, 20, '用撒嬌的眼神誘使對手疏忽大意，大幅降低對手的攻擊。'), (205, '滾動', 'ころがる', 'Rollout', 'rock', '岩石', '物理', 30, 90, 20, '在５回合內連續滾動攻擊對手。每擊中一次，威力就會提高。'), (206, '點到為止', 'みねうち', 'False Swipe', 'normal', '一般', '物理', 40, 100, 40, '手下留情地攻擊對手，一定會讓對手剩下１ＨＰ。'), (207, '虛張聲勢', 'いばる', 'Swagger', 'normal', '一般', '變化', 0, 85, 15, '讓對手火大並陷入混亂。但對手的攻擊會因憤怒而大幅提高。'), (208, '喝牛奶', 'ミルクのみ', 'Milk Drink', 'normal', '一般', '變化', 0, 100, 5, '回復自己最大ＨＰ的一半。'), (209, '電光', 'スパーク', 'Spark', 'electric', '電', '物理', 65, 100, 20, '用電流圍繞身體，猛撞對手進行攻擊。有時會讓對手陷入麻痺狀態。'), (210, '連斬', 'れんぞくぎり', 'Fury Cutter', 'bug', '蟲', '物理', 40, 95, 20, '用鐮刀或爪子等斬切對手進行攻擊。連續命中時威力會提高。'), (211, '鋼翼', 'はがねのつばさ', 'Steel Wing', 'steel', '鋼', '物理', 70, 90, 25, '用堅硬的翅膀撞擊對手進行攻擊。有時會提高自己的防禦。'), (212, '黑色目光', 'くろいまなざし', 'Mean Look', 'normal', '一般', '變化', 0, 100, 5, '用漆黑深邃的眼神一動也不動地凝視對手，讓對手無法從戰鬥中逃走。'), (213, '迷人', 'メロメロ', 'Attract', 'normal', '一般', '變化', 0, 100, 15, '♂誘惑♀而♀誘惑♂，讓對手著迷。對手將難以使用招式。'), (214, '夢話', 'ねごと', 'Sleep Talk', 'normal', '一般', '變化', 0, 100, 10, '從自己已學會的招式中隨機選出１個使出。只有在睡眠狀態時可以使用。'), (215, '治癒鈴聲', 'いやしのすず', 'Heal Bell', 'normal', '一般', '變化', 0, 100, 5, '發出悅耳的鈴聲，治癒我方全體的異常狀態。'), (216, '報恩', 'おんがえし', 'Return', 'normal', '一般', '物理', 0, 100, 20, '為了訓練家而全力攻擊對手。親密度越高，威力越大。'), (217, '禮物', 'プレゼント', 'Present', 'normal', '一般', '物理', 0, 90, 15, '將暗藏機關的盒子遞給對手進行攻擊。但也有可能回復對手的ＨＰ。'), (218, '遷怒', 'やつあたり', 'Frustration', 'normal', '一般', '物理', 0, 100, 20, '為了發洩不滿而全力攻擊對手。親密度越低，威力越大。'), (219, '神秘守護', 'しんぴのまもり', 'Safeguard', 'normal', '一般', '變化', 0, 100, 25, '在５回合內被神奇的力量守護，不會陷入異常狀態。'), (220, '分擔痛楚', 'いたみわけ', 'Pain Split', 'normal', '一般', '變化', 0, 100, 20, '將自己的ＨＰ和對手的ＨＰ相加，然後雙方友好地平分。'), (221, '神聖之火', 'せいなるほのお', 'Sacred Fire', 'fire', '火', '物理', 100, 95, 5, '用神秘的火焰燒盡對手進行攻擊。有時會讓對手陷入灼傷狀態。'), (222, '震級', 'マグニチュード', 'Magnitude', 'ground', '地面', '物理', 0, 100, 30, '晃動地面，攻擊自己周圍所有的寶可夢。招式的威力有多種變化。'), (223, '爆裂拳', 'ばくれつパンチ', 'Dynamic Punch', 'fighting', '格鬥', '物理', 100, 50, 5, '使出渾身力量出拳進行攻擊。必定會使對手混亂。'), (224, '超級角擊', 'メガホーン', 'Megahorn', 'bug', '蟲', '物理', 120, 85, 10, '用堅硬且華麗的角狠狠地刺進對手進行攻擊。'), (225, '龍息', 'りゅうのいぶき', 'Dragon Breath', 'dragon', '龍', '特殊', 60, 100, 20, '將強烈的氣息吹向對手進行攻擊。有時會讓對手陷入麻痺狀態。'), (226, '接棒', 'バトンタッチ', 'Baton Pass', 'normal', '一般', '變化', 0, 100, 40, '與後備寶可夢進行替換。替換上場的寶可夢，將承接目前寶可夢的能力變化。'), (227, '再來一次', 'アンコール', 'Encore', 'normal', '一般', '變化', 0, 100, 5, '要求對手再來一次，讓對手連續３次使出最近一次使用的招式。'), (228, '追打', 'おいうち', 'Pursuit', 'dark', '惡', '物理', 40, 100, 20, '在對手替換寶可夢時使用此招式的話，便能以２倍的威力進行攻擊。'), (229, '高速旋轉', 'こうそくスピン', 'Rapid Spin', 'normal', '一般', '物理', 50, 100, 40, '藉由旋轉來攻擊對手。也可以擺脫綁緊、緊束、寄生種子等招式。自己的速度也會提高。'), (230, '甜甜香氣', 'あまいかおり', 'Sweet Scent', 'normal', '一般', '變化', 0, 100, 20, '用香氣大幅降低對手的閃避率。'), (231, '鐵尾', 'アイアンテール', 'Iron Tail', 'steel', '鋼', '物理', 100, 75, 15, '將堅硬的尾巴甩向對手進行攻擊。有時會降低對手的防禦。'), (232, '金屬爪', 'メタルクロー', 'Metal Claw', 'steel', '鋼', '物理', 50, 95, 35, '用鋼鐵之爪撕裂對手進行攻擊。有時會提高自己的攻擊。'), (233, '借力摔', 'あてみなげ', 'Vital Throw', 'fighting', '格鬥', '物理', 70, 100, 10, '攻擊的順序會在對手之後。但是自己的攻擊必定會命中。'), (234, '晨光', 'あさのひざし', 'Morning Sun', 'normal', '一般', '變化', 0, 100, 5, '回復自己的ＨＰ。回復量會隨著天氣的不同而有所變化。'), (235, '光合作用', 'こうごうせい', 'Synthesis', 'grass', '草', '變化', 0, 100, 5, '回復自己的ＨＰ。回復量會隨著天氣的不同而有所變化。'), (236, '月光', 'つきのひかり', 'Moonlight', 'fairy', '妖精', '變化', 0, 100, 5, '回復自己的ＨＰ。回復量會隨著天氣的不同而有所變化。'), (237, '覺醒力量', 'めざめるパワー', 'Hidden Power', 'normal', '一般', '特殊', 60, 100, 15, '招式的屬性會隨著使用此招式的寶可夢而改變。'), (238, '十字劈', 'クロスチョップ', 'Cross Chop', 'fighting', '格鬥', '物理', 100, 80, 5, '用左右手刀劈打對手進行攻擊。容易擊中要害。'), (239, '龍捲風', 'たつまき', 'Twister', 'dragon', '龍', '特殊', 40, 100, 20, '刮起龍捲風，將對手捲入進行攻擊