INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (251, '圍攻', 'ふくろだたき', 'Beat Up', 'dark', '惡', '物理', 0, 100, 10, '讓我方所有寶可夢進行攻擊。同行的寶可夢越多，招式的攻擊次數越多。'), (252, '擊掌奇襲', 'ねこだまし', 'Fake Out', 'normal', '一般', '物理', 40, 100, 10, '利用先制攻擊使對手畏縮。要在上場後立即使用才會成功。'), (253, '吵鬧', 'さわぐ', 'Uproar', 'normal', '一般', '特殊', 90, 100, 10, '在３回合內大吵大鬧攻擊對手。在此期間誰都無法入眠。'), (254, '蓄力', 'たくわえる', 'Stockpile', 'normal', '一般', '變化', 0, 100, 20, '蓄積力量，提高自己的防禦和特防。最多蓄積３次。'), (255, '噴出', 'はきだす', 'Spit Up', 'normal', '一般', '特殊', 0, 100, 10, '將蓄積的力量撞向對手進行攻擊。蓄積得越多，威力越大。'), (256, '吞下', 'のみこむ', 'Swallow', 'normal', '一般', '變化', 0, 100, 10, '將蓄積的力量吞下，回復自己的ＨＰ。蓄積得越多，回復越多。'), (257, '熱風', 'ねっぷう', 'Heat Wave', 'fire', '火', '特殊', 95, 90, 10, '將炎熱的氣息吹向對手進行攻擊。有時會讓對手陷入灼傷狀態。'), (258, '冰雹', 'あられ', 'Hail', 'ice', '冰', '變化', 0, 100, 10, '降下冰雹，在５回合內場上除了冰屬性以外的寶可夢都會受到傷害。'), (259, '無理取鬧', 'いちゃもん', 'Torment', 'dark', '惡', '變化', 0, 100, 15, '無理取鬧，讓對手不能連續２次使出相同招式。'), (260, '吹捧', 'おだてる', 'Flatter', 'dark', '惡', '變化', 0, 100, 15, '吹捧對手，使其混亂。同時還會提高對手的特攻。'), (261, '鬼火', 'おにび', 'Will-O-Wisp', 'fire', '火', '變化', 0, 85, 15, '放出詭異的火焰，讓對手陷入灼傷狀態。'), (262, '臨別禮物', 'おきみやげ', 'Memento', 'dark', '惡', '變化', 0, 100, 10, '雖然自己會陷入瀕死，但可以大幅降低對手的攻擊和特攻。'), (263, '硬撐', 'からげんき', 'Facade', 'normal', '一般', '物理', 70, 100, 20, '當自己陷入中毒、麻痺、灼傷等狀態時，向對手使用此招式，威力會變成２倍。'), (264, '真氣拳', 'きあいパンチ', 'Focus Punch', 'fighting', '格鬥', '物理', 150, 100, 20, '集中精神後出拳。若出拳前受到攻擊，則會失敗。'), (265, '清醒', 'きつけ', 'Smelling Salts', 'normal', '一般', '物理', 70, 100, 10, '攻擊陷入麻痺狀態的對手時，威力會變成２倍。但也會治癒對手的麻痺。'), (266, '看我嘛', 'このゆびとまれ', 'Follow Me', 'normal', '一般', '變化', 0, 100, 20, '吸引對手的注意，將對手的攻擊全部轉移到自己身上。'), (267, '自然之力', 'しぜんのちから', 'Nature Power', 'normal', '一般', '變化', 0, 100, 20, '用自然之力進行攻擊。隨著使用地點的不同，使出的招式也會改變。'), (268, '充電', 'じゅうでん', 'Charge', 'electric', '電', '變化', 0, 100, 20, '變成充電狀態，提高下次使出的電屬性的招式威力。也會提高自己的特防。'), (269, '挑釁', 'ちょうはつ', 'Taunt', 'dark', '惡', '變化', 0, 100, 20, '激怒對手。在３回合內讓對手只能使出給予傷害的招式。'), (270, '幫助', 'てだすけ', 'Helping Hand', 'normal', '一般', '變化', 0, 100, 20, '幫助夥伴。被幫助的寶可夢，招式的威力會比原來更大。'), (271, '戲法', 'トリック', 'Trick', 'psychic', '超能力', '變化', 0, 100, 10, '在對手露出空隙時，趁機交換自己和對手的持有物。'), (272, '扮演', 'なりきり', 'Role Play', 'psychic', '超能力', '變化', 0, 100, 10, '扮演成對手，讓自己的特性也變得和對手相同。'), (273, '祈願', 'ねがいごと', 'Wish', 'normal', '一般', '變化', 0, 100, 10, '在下一回合回復自己或是替換上場的寶可夢最大ＨＰ的一半。'), (274, '藉助', 'ねこのて', 'Assist', 'normal', '一般', '變化', 0, 100, 20, '緊急向同伴求救，從我方寶可夢已學會的招式中隨機選出１個使用。'), (275, '扎根', 'ねをはる', 'Ingrain', 'grass', '草', '變化', 0, 100, 20, '在大地上扎根，每回合會回復自己的ＨＰ。但扎根之後無法進行替換。'), (276, '蠻力', 'ばかぢから', 'Superpower', 'fighting', '格鬥', '物理', 120, 100, 5, '發揮驚人的力量攻擊對手。自己的攻擊和防禦會降低。'), (277, '魔法反射', 'マジックコート', 'Magic Coat', 'psychic', '超能力', '變化', 0, 100, 15, '若對手使用寄生種子或其他會讓寶可夢陷入異常狀態的招式時，可將對手的招式反彈回去。'), (278, '回收利用', 'リサイクル', 'Recycle', 'normal', '一般', '變化', 0, 100, 10, '將在戰鬥中已消耗完的寶可夢持有物回收後，回復成可以使用的狀態。'), (279, '報復', 'リベンジ', 'Revenge', 'fighting', '格鬥', '物理', 60, 100, 10, '如果受到對手的招式攻擊，給予該對手的傷害就會變成２倍。'), (280, '劈瓦', 'かわらわり', 'Brick Break', 'fighting', '格鬥', '物理', 75, 100, 15, '猛烈地揮下手刀攻擊對手。還可以破壞光牆和反射壁等。'), (281, '哈欠', 'あくび', 'Yawn', 'normal', '一般', '變化', 0, 100, 10, '打個大哈欠引起睡意。在下一回合讓對手陷入睡眠狀態。'), (282, '拍落', 'はたきおとす', 'Knock Off', 'dark', '惡', '物理', 65, 100, 20, '拍落對手的持有物，讓對手在戰鬥結束前都無法使用。對手有攜帶物品時，傷害會增加。'), (283, '蠻幹', 'がむしゃら', 'Endeavor', 'normal', '一般', '物理', 0, 100, 5, '給予傷害，使對手的ＨＰ變得和自己的ＨＰ一樣。'), (284, '噴火', 'ふんか', 'Eruption', 'fire', '火', '特殊', 150, 100, 5, '爆發怒火攻擊對手。自己的ＨＰ越少，招式的威力越小。'), (285, '特性互換', 'スキルスワップ', 'Skill Swap', 'psychic', '超能力', '變化', 0, 100, 10, '用超能力將自己和對手的特性互相交換。'), (286, '封印', 'ふういん', 'Imprison', 'psychic', '超能力', '變化', 0, 100, 10, '如果對手也學會了和自己相同的招式，則對手將不能使用該招式。'), (287, '煥然一新', 'リフレッシュ', 'Refresh', 'normal', '一般', '變化', 0, 100, 20, '讓身體休息，治癒自己身上的中毒、麻痺、灼傷的異常狀態。'), (288, '怨念', 'おんねん', 'Grudge', 'ghost', '幽靈', '變化', 0, 100, 5, '因為對手的招式而陷入瀕死時，施加怨念，讓該招式的ＰＰ變成０。'), (289, '搶奪', 'よこどり', 'Snatch', 'dark', '惡', '變化', 0, 100, 10, '將對手打算使用的回復招式或能力變化招式奪為己用。'), (290, '秘密之力', 'ひみつのちから', 'Secret Power', 'normal', '一般', '物理', 70, 100, 20, '隨著使用地點的不同，招式的追加效果也會改變。'), (291, '潛水', 'ダイビング', 'Dive', 'water', '水', '物理', 80, 100, 10, '使用後的第１回合潛入水中，並於第２回合浮上來進行攻擊。'), (292, '猛推', 'つっぱり', 'Arm Thrust', 'fighting', '格鬥', '物理', 15, 100, 20, '用張開的雙手猛推對手進行攻擊。連續攻擊２～５次。'), (293, '保護色', 'ほごしょく', 'Camouflage', 'normal', '一般', '變化', 0, 100, 20, '可以因應所在的地點如水邊、草叢和洞窟等，改變自己的屬性。'), (294, '螢火', 'ほたるび', 'Tail Glow', 'bug', '蟲', '變化', 0, 100, 20, '凝視閃爍的光芒集中自己的精神，極大幅提高自己的特攻。'), (295, '潔淨光芒', 'ラスターパージ', 'Luster Purge', 'psychic', '超能力', '特殊', 95, 100, 5, '放出耀眼的光芒進行攻擊。有時會降低對手的特防。'), (296, '薄霧球', 'ミストボール', 'Mist Ball', 'psychic', '超能力', '特殊', 95, 100, 5, '將霧狀的羽毛聚集成團進行攻擊。有時會降低對手的特攻。'), (297, '羽毛舞', 'フェザーダンス', 'Feather Dance', 'flying', '飛行', '變化', 0, 100, 15, '撒出羽毛，纏住對手的身體。大幅降低對手的攻擊。'), (298, '搖晃舞', 'フラフラダンス', 'Teeter Dance', 'normal', '一般', '變化', 0, 100, 20, '搖搖晃晃地跳起舞，讓自己周圍的寶可夢陷入混亂狀態。'), (299, '火焰踢', 'ブレイズキック', 'Blaze Kick', 'fire', '火', '物理', 85, 90, 10, '有時會讓受到攻擊的對手陷入灼傷狀態。且容易擊中要害。'), (300, '玩泥巴', 'どろあそび', 'Mud Sport', 'ground', '地面', '變化', 0, 100, 15, '將周圍弄得到處是泥。在５回合內減弱電屬性的招式。');
