INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (301, '冰球', 'アイスボール', 'Ice Ball', 'ice', '冰', '物理', 30, 90, 20, '在５回合內不斷攻擊對手。每擊中一次，威力就會提高。'), (302, '尖刺臂', 'ニードルアーム', 'Needle Arm', 'grass', '草', '物理', 60, 100, 15, '用帶刺的手臂猛烈揮打進行攻擊。有時會使對手畏縮。'), (303, '偷懶', 'なまける', 'Slack Off', 'normal', '一般', '變化', 0, 100, 5, '偷懶休息。回復自己最大ＨＰ的一半。'), (304, '巨聲', 'ハイパーボイス', 'Hyper Voice', 'normal', '一般', '特殊', 90, 100, 10, '製造出震耳欲聾的巨大聲波攻擊對手。'), (305, '劇毒牙', 'どくどくのキバ', 'Poison Fang', 'poison', '毒', '物理', 50, 100, 15, '用有毒的牙齒咬住對手進行攻擊。有時會讓對手陷入劇毒狀態。'), (306, '撕裂爪', 'ブレイククロー', 'Crush Claw', 'normal', '一般', '物理', 75, 95, 10, '用堅硬的銳爪撕裂對手進行攻擊。有時會降低對手的防禦。'), (307, '爆炸烈焰', 'ブラストバーン', 'Blast Burn', 'fire', '火', '特殊', 150, 90, 5, '用爆炸的火焰燒盡對手進行攻擊。下一回合自己將無法動彈。'), (308, '加農水炮', 'ハイドロカノン', 'Hydro Cannon', 'water', '水', '特殊', 150, 90, 5, '向對手發射水炮進行攻擊。下一回合自己將無法動彈。'), (309, '彗星拳', 'コメットパンチ', 'Meteor Mash', 'steel', '鋼', '物理', 90, 90, 10, '使出如同彗星般的拳頭攻擊對手。有時會提高自己的攻擊。'), (310, '驚嚇', 'おどろかす', 'Astonish', 'ghost', '幽靈', '物理', 30, 100, 15, '用巨大的聲響等突然驚嚇對手進行攻擊。有時會讓對手畏縮。'), (311, '氣象球', 'ウェザーボール', 'Weather Ball', 'normal', '一般', '特殊', 50, 100, 10, '招式屬性和威力會隨著使用時天氣的不同而改變。'), (312, '芳香治療', 'アロマセラピー', 'Aromatherapy', 'grass', '草', '變化', 0, 100, 5, '釋放出宜人的香氣，治癒我方全體的異常狀態。'), (313, '假哭', 'うそなき', 'Fake Tears', 'dark', '惡', '變化', 0, 100, 20, '裝哭掉眼淚。讓對手不知所措，大幅降低對手的特防。'), (314, '空氣利刃', 'エアカッター', 'Air Cutter', 'flying', '飛行', '特殊', 60, 95, 25, '用鋒利的風刃斬切對手進行攻擊。容易擊中要害。'), (315, '過熱', 'オーバーヒート', 'Overheat', 'fire', '火', '特殊', 130, 90, 5, '釋放全部能量攻擊對手。使用後因為反作用力，自己的特攻會大幅降低。'), (316, '氣味偵測', 'かぎわける', 'Odor Sleuth', 'normal', '一般', '變化', 0, 100, 40, '讓對幽靈屬性寶可夢無效的招式能夠發揮效果，也可不顧對手提高後的閃避率進行攻擊。'), (317, '岩石封鎖', 'がんせきふうじ', 'Rock Tomb', 'rock', '岩石', '物理', 60, 95, 15, '投擲岩石進行攻擊。封住對手的行動，進而降低其速度。'), (318, '銀色旋風', 'ぎんいろのかぜ', 'Silver Wind', 'bug', '蟲', '特殊', 60, 100, 5, '乘著風飄送鱗粉攻擊對手。有時會提高自己的所有能力。'), (319, '金屬音', 'きんぞくおん', 'Metal Sound', 'steel', '鋼', '變化', 0, 85, 40, '讓對手聽如同金屬摩擦般的刺耳聲。大幅降低對手的特防。'), (320, '草笛', 'くさぶえ', 'Grass Whistle', 'grass', '草', '變化', 0, 55, 15, '發出悅耳的笛聲，讓對手陷入睡眠狀態。'), (321, '搔癢', 'くすぐる', 'Tickle', 'normal', '一般', '變化', 0, 100, 20, '搔癢對手的身體，讓對手發笑，降低對手的攻擊和防禦。'), (322, '宇宙力量', 'コスモパワー', 'Cosmic Power', 'psychic', '超能力', '變化', 0, 100, 20, '吸取來自宇宙的神秘力量，提高自己的防禦和特防。'), (323, '噴水', 'しおふき', 'Water Spout', 'water', '水', '特殊', 150, 100, 5, '噴起潮水進行攻擊。自己的ＨＰ越少，招式的威力越小。'), (324, '信號光束', 'シグナルビーム', 'Signal Beam', 'bug', '蟲', '特殊', 75, 100, 15, '發射神奇的光線進行攻擊。有時會使對手混亂。'), (325, '暗影拳', 'シャドーパンチ', 'Shadow Punch', 'ghost', '幽靈', '物理', 60, 100, 20, '暗藏在陰影之中出拳。攻擊必定會命中。'), (326, '神通力', 'じんつうりき', 'Extrasensory', 'psychic', '超能力', '特殊', 80, 100, 20, '發送看不見的神奇力量進行攻擊。有時會使對手畏縮。'), (327, '衝天拳', 'スカイアッパー', 'Sky Uppercut', 'fighting', '格鬥', '物理', 85, 90, 15, '用直衝天空般的上勾拳高高地打飛對手進行攻擊。'), (328, '流沙地獄', 'すなじごく', 'Sand Tomb', 'ground', '地面', '物理', 35, 85, 15, '將對手困在鋪天蓋地的沙暴中，在４～５回合內進行攻擊。'), (329, '絕對零度', 'ぜったいれいど', 'Sheer Cold', 'ice', '冰', '特殊', 0, 100, 5, '一擊讓對手陷入瀕死。冰屬性以外的寶可夢使用時，會比較不易命中對手。'), (330, '濁流', 'だくりゅう', 'Muddy Water', 'water', '水', '特殊', 90, 85, 10, '用渾濁的污水攻擊對手。有時會降低對手的命中率。'), (331, '種子機關槍', 'タネマシンガン', 'Bullet Seed', 'grass', '草', '物理', 25, 100, 30, '向對手猛烈地發射種子進行攻擊。連續攻擊２～５次。'), (332, '燕返', 'つばめがえし', 'Aerial Ace', 'flying', '飛行', '物理', 60, 100, 20, '以敏捷的動作戲弄對手後進行切斬。攻擊必定會命中。'), (333, '冰錐', 'つららばり', 'Icicle Spear', 'ice', '冰', '物理', 25, 100, 30, '向對手發射尖銳的冰柱進行攻擊。可連續攻擊２～５次。'), (334, '鐵壁', 'てっぺき', 'Iron Defense', 'steel', '鋼', '變化', 0, 100, 15, '將皮膚變得堅硬如鐵，大幅提高自己的防禦。'), (335, '擋路', 'とおせんぼう', 'Block', 'normal', '一般', '變化', 0, 100, 5, '張開雙手進行阻擋，封住對手的退路，讓對手無法逃走。'), (336, '長嚎', 'とおぼえ', 'Howl', 'normal', '一般', '變化', 0, 100, 40, '大聲地吼叫提升氣勢，提高自己和同伴的攻擊。'), (337, '龍爪', 'ドラゴンクロー', 'Dragon Claw', 'dragon', '龍', '物理', 80, 100, 15, '用尖銳的巨爪撕裂對手進行攻擊。'), (338, '瘋狂植物', 'ハードプラント', 'Frenzy Plant', 'grass', '草', '特殊', 150, 90, 5, '將大大的樹木甩向對手進行攻擊。下一回合自己將無法動彈。'), (339, '健美', 'ビルドアップ', 'Bulk Up', 'fighting', '格鬥', '變化', 0, 100, 20, '將力量集中於體內，增厚肌肉，提高自己的攻擊和防禦。'), (340, '彈跳', 'とびはねる', 'Bounce', 'flying', '飛行', '物理', 85, 85, 5, '彈跳到高空中，在下一回合攻擊對手。有時會讓對手陷入麻痺狀態。'), (341, '泥巴射擊', 'マッドショット', 'Mud Shot', 'ground', '地面', '特殊', 55, 95, 15, '向對手投擲泥塊進行攻擊。同時降低對手的速度。'), (342, '毒尾', 'ポイズンテール', 'Poison Tail', 'poison', '毒', '物理', 50, 100, 25, '將尾巴甩向對手進行攻擊。有時會讓對手陷入中毒狀態，且容易擊中要害。'), (343, '渴望', 'ほしがる', 'Covet', 'normal', '一般', '物理', 60, 100, 25, '一邊可愛地撒嬌，一邊靠近對手，奪取對手攜帶的道具。'), (344, '伏特攻擊', 'ボルテッカー', 'Volt Tackle', 'electric', '電', '物理', 120, 100, 15, '用電流圍繞全身後猛撞對手。自己也會受到不小的傷害。有時會讓對手陷入麻痺狀態。'), (345, '魔法葉', 'マジカルリーフ', 'Magical Leaf', 'grass', '草', '特殊', 60, 100, 20, '散落可以追蹤對手的神奇葉子。攻擊必定會命中。'), (346, '玩水', 'みずあそび', 'Water Sport', 'water', '水', '變化', 0, 100, 15, '用水把周圍弄得濕答答。在５回合內減弱火屬性的招式。'), (347, '冥想', 'めいそう', 'Calm Mind', 'psychic', '超能力', '變化', 0, 100, 20, '屏氣凝神，提高自己的特攻和特防。'), (348, '葉刃', 'リーフブレード', 'Leaf Blade', 'grass', '草', '物理', 90, 100, 15, '如用劍般操控葉子切斬對手進行攻擊。容易擊中要害。'), (349, '龍之舞', 'りゅうのまい', 'Dragon Dance', 'dragon', '龍', '變化', 0, 100, 20, '激烈地跳起神秘又強而有力的舞蹈。提高自己的攻擊和速度。'), (350, '岩石爆擊', 'ロックブラスト', 'Rock Blast', 'rock', '岩石', '物理', 25, 90, 10, '向對手發射堅硬的岩石進行攻擊。連續攻擊２～５次。');
