INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (351, '電擊波', 'でんげきは', 'Shock Wave', 'electric', '電', '特殊', 60, 100, 20, '向對手迅速放出電流。攻擊一定會命中。'), (352, '水之波動', 'みずのはどう', 'Water Pulse', 'water', '水', '特殊', 60, 100, 20, '用水的震動攻擊對手。有時會使對手混亂。'), (353, '破滅之願', 'はめつのねがい', 'Doom Desire', 'steel', '鋼', '特殊', 140, 100, 5, '使用招式的２回合後，向對手發射無數道光束進行攻擊。'), (354, '精神突進', 'サイコブースト', 'Psycho Boost', 'psychic', '超能力', '特殊', 140, 90, 5, '釋放全部能量攻擊對手。使用後因為反作用力，自己的特攻會大幅降低。'), (355, '羽棲', 'はねやすめ', 'Roost', 'flying', '飛行', '變化', 0, 100, 5, '降落到地面，讓身體休息。回復自己最大ＨＰ的一半。'), (356, '重力', 'じゅうりょく', 'Gravity', 'psychic', '超能力', '變化', 0, 100, 5, '在５回合內，飄浮特性和飛行屬性的寶可夢會被地面屬性的招式擊中。且無法使用飛向空中的招式。'), (357, '奇跡之眼', 'ミラクルアイ', 'Miracle Eye', 'psychic', '超能力', '變化', 0, 100, 40, '讓對惡屬性寶可夢無效的招式能夠發揮效果，也可不顧對手提高後的閃避率進行攻擊。'), (358, '喚醒巴掌', 'めざましビンタ', 'Wake-Up Slap', 'fighting', '格鬥', '物理', 70, 100, 10, '攻擊陷入睡眠狀態的對手時，可以給予較大的傷害。但對手也會從睡眠中醒過來。'), (359, '臂錘', 'アームハンマー', 'Hammer Arm', 'fighting', '格鬥', '物理', 100, 90, 10, '揮出強力而沉重的拳頭，給予對手傷害。自己的速度會降低。'), (360, '陀螺球', 'ジャイロボール', 'Gyro Ball', 'steel', '鋼', '物理', 0, 100, 5, '高速旋轉身體，撞擊對手。速度比對手越慢，威力越大。'), (361, '治癒之願', 'いやしのねがい', 'Healing Wish', 'psychic', '超能力', '變化', 0, 100, 10, '雖然自己會陷入瀕死，但可以治癒接替上場的寶可夢的異常狀態並回復ＨＰ。'), (362, '鹽水', 'しおみず', 'Brine', 'water', '水', '特殊', 65, 100, 10, '當對手負傷，ＨＰ剩一半左右時，招式威力會變成２倍。'), (363, '自然之恩', 'しぜんのめぐみ', 'Natural Gift', 'normal', '一般', '物理', 0, 100, 15, '從樹果上獲得力量進行攻擊。招式屬性和威力會隨著攜帶的樹果不同而改變。'), (364, '佯攻', 'フェイント', 'Feint', 'normal', '一般', '物理', 30, 100, 10, '能夠攻擊使用了守住或看穿的對手。並解除對手的防守效果。'), (365, '啄食', 'ついばむ', 'Pluck', 'flying', '飛行', '物理', 60, 100, 20, '用鳥喙進行攻擊。可以吃下對手攜帶的樹果，並獲得樹果的效果。'), (366, '順風', 'おいかぜ', 'Tailwind', 'flying', '飛行', '變化', 0, 100, 15, '刮起猛烈的強風，在４回合內提高我方全體的速度。'), (367, '點穴', 'つぼをつく', 'Acupressure', 'normal', '一般', '變化', 0, 100, 30, '利用點穴激發身體的活力，大幅提高某一項能力。'), (368, '金屬爆炸', 'メタルバースト', 'Metal Burst', 'steel', '鋼', '物理', 0, 100, 10, '將發動攻擊前最後受到的招式傷害放大，向對手進行反擊。'), (369, '急速折返', 'とんぼがえり', 'U-turn', 'bug', '蟲', '物理', 70, 100, 20, '攻擊後迅速返回，和後備寶可夢進行替換。'), (370, '近身戰', 'インファイト', 'Close Combat', 'fighting', '格鬥', '物理', 120, 100, 5, '反守為攻，近身突擊對手。自己的防禦和特防會降低。'), (371, '以牙還牙', 'しっぺがえし', 'Payback', 'dark', '惡', '物理', 50, 100, 10, '累積力量進行攻擊。若在對手之後進行攻擊，招式的威力會變成２倍。'), (372, '惡意追擊', 'ダメおし', 'Assurance', 'dark', '惡', '物理', 60, 100, 10, '如果此回合內對手已受到傷害，招式威力會變成２倍。'), (373, '查封', 'さしおさえ', 'Embargo', 'dark', '惡', '變化', 0, 100, 15, '讓對手在５回合內無法使用攜帶的道具。訓練家也不能對該寶可夢使用道具。'), (374, '投擲', 'なげつける', 'Fling', 'dark', '惡', '物理', 0, 100, 10, '快速投擲攜帶的道具進行攻擊。招式威力和效果會隨著道具不同而改變。'), (375, '精神轉移', 'サイコシフト', 'Psycho Shift', 'psychic', '超能力', '變化', 0, 100, 10, '用超能力施加暗示，將自己的異常狀態轉移給對手。'), (376, '王牌', 'きりふだ', 'Trump Card', 'normal', '一般', '特殊', 0, 100, 5, '這個招式的剩餘PP越少，威力越大。'), (377, '回復封鎖', 'かいふくふうじ', 'Heal Block', 'psychic', '超能力', '變化', 0, 100, 15, '讓對手在５回合內無法利用招式、特性或攜帶的道具回復HP。'), (378, '絞緊', 'しぼりとる', 'Wring Out', 'normal', '一般', '特殊', 0, 100, 5, '用力勒緊對手進行攻擊。對手現有的HP越多，招式的威力越大。'), (379, '力量戲法', 'パワートリック', 'Power Trick', 'psychic', '超能力', '變化', 0, 100, 10, '利用超能力，將自己的攻擊和防禦互相交換。'), (380, '胃液', 'いえき', 'Gastro Acid', 'poison', '毒', '變化', 0, 100, 10, '將胃液吐向對手的身體。沾到的胃液會消除對手的特性效果。'), (381, '幸運咒語', 'おまじない', 'Lucky Chant', 'normal', '一般', '變化', 0, 100, 30, '對天許願，讓自己在５回合內不會被對手的攻擊命中要害。'), (382, '搶先一步', 'さきどり', 'Me First', 'normal', '一般', '變化', 0, 100, 20, '以更高的威力，搶先發動對手準備使出的招式。若行動順序在對手之後則會失敗。'), (383, '仿效', 'まねっこ', 'Copycat', 'normal', '一般', '變化', 0, 100, 20, '模仿對手最近一次使用的招式，若對手沒有成功出招則會失敗。'), (384, '力量互換', 'パワースワップ', 'Power Swap', 'psychic', '超能力', '變化', 0, 100, 10, '用超能力將自己和對手的攻擊與特攻的能力變化互相交換。'), (385, '防守互換', 'ガードスワップ', 'Guard Swap', 'psychic', '超能力', '變化', 0, 100, 10, '用超能力將自己和對手的防禦與特防的能力變化互相交換。'), (386, '懲罰', 'おしおき', 'Punishment', 'dark', '惡', '物理', 0, 100, 5, '對手的能力因為能力變化而提高得越多，招式的威力就越大。'), (387, '珍藏', 'とっておき', 'Last Resort', 'normal', '一般', '物理', 140, 100, 5, '在戰鬥中將其他已學會的招式全部使用過後，才能夠使出的珍藏招式。'), (388, '煩惱種子', 'なやみのタネ', 'Worry Seed', 'grass', '草', '變化', 0, 100, 10, '埋下令人心煩意亂的種子，讓對手睡不著，將對手的特性變成不眠。'), (389, '突襲', 'ふいうち', 'Sucker Punch', 'dark', '惡', '物理', 70, 100, 5, '可以搶先對手一步進行攻擊。若對手使用的不是攻擊招式則會失敗。'), (390, '毒菱', 'どくびし', 'Toxic Spikes', 'poison', '毒', '變化', 0, 100, 20, '在對手腳下散布毒菱。讓對手替換出場的寶可夢陷入中毒狀態。'), (391, '心靈互換', 'ハートスワップ', 'Heart Swap', 'psychic', '超能力', '變化', 0, 100, 10, '用超能力將自己和對手的能力變化互相交換。'), (392, '水流環', 'アクアリング', 'Aqua Ring', 'water', '水', '變化', 0, 100, 20, '在自己的身邊套上水流環。每回合會回復自己的ＨＰ。'), (393, '電磁飄浮', 'でんじふゆう', 'Magnet Rise', 'electric', '電', '變化', 0, 100, 10, '利用電氣產生的磁力浮在空中。在５回合內可以飄浮。'), (394, '閃焰衝鋒', 'フレアドライブ', 'Flare Blitz', 'fire', '火', '物理', 120, 100, 15, '將火焰圍繞全身後猛撞對手。自己也會受到不小的傷害。有時會讓對手陷入灼傷狀態。'), (395, '發勁', 'はっけい', 'Force Palm', 'fighting', '格鬥', '物理', 60, 100, 10, '向對手的身體發出衝擊波進行攻擊。有時會讓對手陷入麻痺狀態。'), (396, '波導彈', 'はどうだん', 'Aura Sphere', 'fighting', '格鬥', '特殊', 80, 100, 20, '將來自體內深處的波導之力擊向對手。攻擊必定會命中。'), (397, '岩石打磨', 'ロックカット', 'Rock Polish', 'rock', '岩石', '變化', 0, 100, 20, '打磨自己的身體，減少空氣阻力。可大幅提高自己的速度。'), (398, '毒擊', 'どくづき', 'Poison Jab', 'poison', '毒', '物理', 80, 100, 20, '以帶有毒性的觸手或手臂刺進對手。有時會讓對手陷入中毒狀態。'), (399, '惡之波動', 'あくのはどう', 'Dark Pulse', 'dark', '惡', '特殊', 80, 100, 15, '從身體散發出充滿惡意的恐怖氣場。有時會使對手畏縮。'), (400, '暗襲要害', 'つじぎり', 'Night Slash', 'dark', '惡', '物理', 70, 100, 15, '抓住瞬間的空隙斬切對手。容易擊中要害。');
