INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (401, '水流尾', 'アクアテール', 'Aqua Tail', 'water', '水', '物理', 90, 90, 10, '如驚濤駭浪般地甩動大尾巴攻擊對手。'), (402, '種子炸彈', 'タネばくだん', 'Seed Bomb', 'grass', '草', '物理', 80, 100, 15, '從上方砸下外殼堅硬的大種子，攻擊對手。'), (403, '空氣斬', 'エアスラッシュ', 'Air Slash', 'flying', '飛行', '特殊', 75, 95, 15, '用連天空也能劈開的空氣之刃進行攻擊。有時會使對手畏縮。'), (404, '十字剪', 'シザークロス', 'X-Scissor', 'bug', '蟲', '物理', 80, 100, 15, '將鐮刀或爪子像剪刀般地交叉，順勢劈開對手。'), (405, '蟲鳴', 'むしのさざめき', 'Bug Buzz', 'bug', '蟲', '特殊', 90, 100, 10, '利用振動產生出的音波進行攻擊。有時會降低對手的特防。'), (406, '龍之波動', 'りゅうのはどう', 'Dragon Pulse', 'dragon', '龍', '特殊', 85, 100, 10, '從張大的口中發射衝擊波攻擊對手。'), (407, '龍之俯衝', 'ドラゴンダイブ', 'Dragon Rush', 'dragon', '龍', '物理', 100, 75, 10, '釋放出駭人的殺氣，威嚇並撞擊對手。有時會使對手畏縮。'), (408, '力量寶石', 'パワージェム', 'Power Gem', 'rock', '岩石', '特殊', 80, 100, 20, '發射如寶石般閃耀的光芒攻擊對手。'), (409, '吸取拳', 'ドレインパンチ', 'Drain Punch', 'fighting', '格鬥', '物理', 75, 100, 10, '從拳頭吸取對手的力量。可以回復給予對手傷害的一半ＨＰ。'), (410, '真空波', 'しんくうは', 'Vacuum Wave', 'fighting', '格鬥', '特殊', 40, 100, 30, '揮動拳頭，捲起真空波。必定能夠發動先制攻擊。'), (411, '真氣彈', 'きあいだま', 'Focus Blast', 'fighting', '格鬥', '特殊', 120, 70, 5, '提高氣勢，釋放全身所有力量。有時會降低對手的特防。'), (412, '能量球', 'エナジーボール', 'Energy Ball', 'grass', '草', '特殊', 90, 100, 10, '發射從大自然匯聚而來的生命力量。有時會降低對手的特防。'), (413, '勇鳥猛攻', 'ブレイブバード', 'Brave Bird', 'flying', '飛行', '物理', 120, 100, 15, '收起翅膀，低空飛行突擊對手。自己也會受到不小的傷害。'), (414, '大地之力', 'だいちのちから', 'Earth Power', 'ground', '地面', '特殊', 90, 100, 10, '向對手腳下釋放出大地之力。有時會降低對手的特防。'), (415, '掉包', 'すりかえ', 'Switcheroo', 'dark', '惡', '變化', 0, 100, 10, '以肉眼看不清的速度將自己和對手的持有物互相交換。'), (416, '終極衝擊', 'ギガインパクト', 'Giga Impact', 'normal', '一般', '物理', 150, 90, 5, '使出全身上下所有力量突擊對手。下一回合自己將無法動彈。'), (417, '詭計', 'わるだくみ', 'Nasty Plot', 'dark', '惡', '變化', 0, 100, 20, '動歪腦筋，活化大腦。大幅提高自己的特攻。'), (418, '子彈拳', 'バレットパンチ', 'Bullet Punch', 'steel', '鋼', '物理', 40, 100, 30, '向對手連續揮出如子彈般快速且堅硬的拳頭。必定能夠發動先制攻擊。'), (419, '雪崩', 'ゆきなだれ', 'Avalanche', 'ice', '冰', '物理', 60, 100, 10, '如果受到對手的招式攻擊，給予該對手的招式威力就會變成２倍。'), (420, '冰礫', 'こおりのつぶて', 'Ice Shard', 'ice', '冰', '物理', 40, 100, 30, '將瞬間製造出的冰塊快速地扔向對手。必定能夠發動先制攻擊。'), (421, '暗影爪', 'シャドークロー', 'Shadow Claw', 'ghost', '幽靈', '物理', 70, 100, 15, '用黑影化成的銳利爪子撕裂對手。容易擊中要害。'), (422, '雷電牙', 'かみなりのキバ', 'Thunder Fang', 'electric', '電', '物理', 65, 95, 15, '用蓄滿電流的牙齒咬住對手。有時會讓對手畏縮或陷入麻痺狀態。'), (423, '冰凍牙', 'こおりのキバ', 'Ice Fang', 'ice', '冰', '物理', 65, 95, 15, '用帶有寒氣的牙齒咬住對手。有時會讓對手畏縮或陷入冰凍狀態。'), (424, '火焰牙', 'ほのおのキバ', 'Fire Fang', 'fire', '火', '物理', 65, 95, 15, '用纏繞火焰的牙齒咬住對手。有時會讓對手畏縮或陷入灼傷狀態。'), (425, '影子偷襲', 'かげうち', 'Shadow Sneak', 'ghost', '幽靈', '物理', 40, 100, 30, '伸長自己的影子，從對手的背後進行攻擊。必定能夠發動先制攻擊。'), (426, '泥巴炸彈', 'どろばくだん', 'Mud Bomb', 'ground', '地面', '特殊', 65, 85, 10, '向對手發射堅硬的泥彈進行攻擊。有時會降低對手的命中率。'), (427, '精神利刃', 'サイコカッター', 'Psycho Cut', 'psychic', '超能力', '物理', 70, 100, 20, '用實體化的精神利刃斬向對手。容易擊中要害。'), (428, '意念頭錘', 'しねんのずつき', 'Zen Headbutt', 'psychic', '超能力', '物理', 80, 90, 15, '將意念的力量集中在前額進行攻擊。有時會讓對手畏縮。'), (429, '鏡光射擊', 'ミラーショット', 'Mirror Shot', 'steel', '鋼', '特殊', 65, 85, 10, '用磨得亮晶晶的身體向對手放出閃光之力進行攻擊。有時會降低對手的命中率。'), (430, '加農光炮', 'ラスターカノン', 'Flash Cannon', 'steel', '鋼', '特殊', 80, 100, 10, '將身體的光芒聚集在一個點後釋放出去。有時會降低對手的特防。'), (431, '攀岩', 'ロッククライム', 'Rock Climb', 'normal', '一般', '物理', 90, 85, 20, '用盡全力撲向對手進行攻擊。有時會使對手混亂。是寶可錶的秘傳招式之一。'), (432, '清除濃霧', 'きりばらい', 'Defog', 'flying', '飛行', '變化', 0, 100, 15, '用強風吹開對手的反射壁或光牆等。也會降低對手的閃避率。'), (433, '戲法空間', 'トリックルーム', 'Trick Room', 'psychic', '超能力', '變化', 0, 100, 5, '創造出非常不可思議的空間。在５回合內，速度慢的寶可夢可以先行動。'), (434, '流星群', 'りゅうせいぐん', 'Draco Meteor', 'dragon', '龍', '特殊', 130, 90, 5, '讓隕石從空中落下以攻擊對手。使用後因為反作用力，自己的特攻會大幅降低。'), (435, '放電', 'ほうでん', 'Discharge', 'electric', '電', '特殊', 80, 100, 15, '用耀眼的電流攻擊自己周圍所有的寶可夢。有時會讓對手陷入麻痺狀態。'), (436, '噴煙', 'ふんえん', 'Lava Plume', 'fire', '火', '特殊', 80, 100, 15, '用熊熊烈火攻擊自己周圍所有的寶可夢。有時會讓對手陷入灼傷狀態。'), (437, '飛葉風暴', 'リーフストーム', 'Leaf Storm', 'grass', '草', '特殊', 130, 90, 5, '用尖銳的葉子捲起風暴向對手進行攻擊。使用後因為反作用力，自己的特攻會大幅降低。'), (438, '強力鞭打', 'パワーウィップ', 'Power Whip', 'grass', '草', '物理', 120, 85, 10, '激烈地揮舞藤蔓或觸手向對手進行攻擊。'), (439, '岩石炮', 'がんせきほう', 'Rock Wrecker', 'rock', '岩石', '物理', 150, 90, 5, '向對手發射巨大的岩石進行攻擊。下一回合自己將無法動彈。'), (440, '十字毒刃', 'クロスポイズン', 'Cross Poison', 'poison', '毒', '物理', 70, 100, 20, '用毒刃劈開對手。有時會讓對手陷入中毒狀態，也容易擊中要害。'), (441, '垃圾射擊', 'ダストシュート', 'Gunk Shot', 'poison', '毒', '物理', 120, 80, 5, '將骯髒的垃圾丟向對手進行攻擊。有時會讓對手陷入中毒狀態。'), (442, '鐵頭', 'アイアンヘッド', 'Iron Head', 'steel', '鋼', '物理', 80, 100, 15, '用如同鋼鐵般堅硬的頭部進行攻擊。有時會讓對手畏縮。'), (443, '磁鐵炸彈', 'マグネットボム', 'Magnet Bomb', 'steel', '鋼', '物理', 60, 100, 20, '發射能吸住對手的鋼鐵炸彈。攻擊必定會命中。'), (444, '尖石攻擊', 'ストーンエッジ', 'Stone Edge', 'rock', '岩石', '物理', 100, 80, 5, '用尖銳的岩石刺進對手進行攻擊。容易擊中要害。'), (445, '誘惑', 'ゆうわく', 'Captivate', 'normal', '一般', '變化', 0, 100, 20, '♂誘惑♀而♀誘惑♂，大幅降低對手的特攻。'), (446, '隱形岩', 'ステルスロック', 'Stealth Rock', 'rock', '岩石', '變化', 0, 100, 20, '在對手周圍撒下無數飄浮的岩石，讓對手替換出場的寶可夢受到傷害。'), (447, '打草結', 'くさむすび', 'Grass Knot', 'grass', '草', '特殊', 0, 100, 20, '用打結的草絆倒對手。對手越重，威力越大。'), (448, '喋喋不休', 'おしゃべり', 'Chatter', 'flying', '飛行', '特殊', 65, 100, 20, '用非常煩人且喋喋不休的音波攻擊對手。使對手混亂。'), (449, '制裁光礫', 'さばきのつぶて', 'Judgment', 'normal', '一般', '特殊', 100, 100, 10, '向對手放出無數的光彈。招式的屬性會隨著自己攜帶的石板不同而改變。'), (450, '蟲咬', 'むしくい', 'Bug Bite', 'bug', '蟲', '物理', 60, 100, 20, '叮咬對手進行攻擊。可以吃下對手攜帶的樹果，並獲得樹果的效果。');
