INSERT INTO skills (skill_number, name_zh, name_ja, name_en, type, type_zh, category, power, accuracy, pp, description) VALUES (451, '充電光束', 'チャージビーム', 'Charge Beam', 'electric', '電', '特殊', 50, 90, 10, '向對手發射電擊光束。有時會因電流蓄積，提高自己的特攻。'), (452, '木槌', 'ウッドハンマー', 'Wood Hammer', 'grass', '草', '物理', 120, 100, 15, '用堅硬的軀體撞擊對手進行攻擊。自己也會受到不小的傷害。'), (453, '水流噴射', 'アクアジェット', 'Aqua Jet', 'water', '水', '物理', 40, 100, 20, '以迅雷不及掩耳之勢撲向對手。必定能夠發動先制攻擊。'), (454, '攻擊指令', 'こうげきしれい', 'Attack Order', 'bug', '蟲', '物理', 90, 100, 15, '呼喚手下，向對手發動攻擊。容易擊中要害。'), (455, '防禦指令', 'ぼうぎょしれい', 'Defend Order', 'bug', '蟲', '變化', 0, 100, 10, '呼喚手下，覆蓋住自己的身體。可提高自己的防禦和特防。'), (456, '回復指令', 'かいふくしれい', 'Heal Order', 'bug', '蟲', '變化', 0, 100, 10, '呼喚手下，治療自己的傷勢。回復自己最大HP的一半。'), (457, '雙刃頭錘', 'もろはのずつき', 'Head Smash', 'rock', '岩石', '物理', 150, 80, 5, '使出渾身力量，捨身向對手進行頭錘攻擊。自己也會受到非常大的傷害。'), (458, '二連擊', 'ダブルアタック', 'Double Hit', 'normal', '一般', '物理', 35, 90, 10, '用尾巴等向對手進行攻擊。連續２次給予傷害。'), (459, '時光咆哮', 'ときのほうこう', 'Roar of Time', 'dragon', '龍', '特殊', 150, 90, 5, '釋放出足以扭曲時間的強大力量攻擊對手。下一回合自己將無法動彈。'), (460, '亞空裂斬', 'あくうせつだん', 'Spacial Rend', 'dragon', '龍', '特殊', 100, 95, 5, '將對手連同周圍的空間一起切開，給予對手傷害。容易擊中要害。'), (461, '新月舞', 'みかづきのまい', 'Lunar Dance', 'psychic', '超能力', '變化', 0, 100, 10, '雖然自己會陷入瀕死，但可以讓接替上場的寶可夢回復到萬全狀態。'), (462, '捏碎', 'にぎりつぶす', 'Crush Grip', 'normal', '一般', '物理', 0, 100, 5, '用驚人的力量捏碎對手。對手現有的ＨＰ越多，招式的威力越大。'), (463, '熔岩風暴', 'マグマストーム', 'Magma Storm', 'fire', '火', '特殊', 100, 75, 5, '將對手困在熊熊燃燒的火焰中，在４～５回合內進行攻擊。'), (464, '暗黑洞', 'ダークホール', 'Dark Void', 'dark', '惡', '變化', 0, 50, 10, '將對手強制拉進黑暗的世界，讓對手陷入睡眠狀態。'), (465, '種子閃光', 'シードフレア', 'Seed Flare', 'grass', '草', '特殊', 120, 85, 5, '從身體裡發出衝擊波攻擊對手。有時會大幅降低對手的特防。'), (466, '奇異之風', 'あやしいかぜ', 'Ominous Wind', 'ghost', '幽靈', '特殊', 60, 100, 5, '用令人毛骨悚然的強風攻擊對手。有時會提高自己的所有能力。'), (467, '暗影潛襲', 'シャドーダイブ', 'Shadow Force', 'ghost', '幽靈', '物理', 120, 100, 5, '使用後的第１回合消失身影，並於第２回合攻擊對手。處於守護的對手也能擊中。'), (468, '磨爪', 'つめとぎ', 'Hone Claws', 'dark', '惡', '變化', 0, 100, 15, '將爪子磨得更加鋒利。可提高自己的攻擊和命中率。'), (469, '廣域防守', 'ワイドガード', 'Wide Guard', 'rock', '岩石', '變化', 0, 100, 10, '在１回合內，防禦住攻擊我方全體的攻擊招式。'), (470, '防守平分', 'ガードシェア', 'Guard Split', 'psychic', '超能力', '變化', 0, 100, 10, '用超能力將自己和對手的防禦和特防各別相加後，再進行平分。'), (471, '力量平分', 'パワーシェア', 'Power Split', 'psychic', '超能力', '變化', 0, 100, 10, '用超能力將自己和對手的攻擊和特攻各別相加後，再進行平分。'), (472, '奇妙空間', 'ワンダールーム', 'Wonder Room', 'psychic', '超能力', '變化', 0, 100, 10, '創造出非常不可思議的空間。在５回合內，讓所有寶可夢的防禦和特防互相交換。'), (473, '精神衝擊', 'サイコショック', 'Psyshock', 'psychic', '超能力', '特殊', 80, 100, 10, '將神奇的念力波實體化後攻擊對手。給予對手物理傷害。'), (474, '毒液衝擊', 'ベノムショック', 'Venoshock', 'poison', '毒', '特殊', 65, 100, 10, '將特殊的毒液潑向對手。攻擊陷入中毒狀態的對手時，威力會變成２倍。'), (475, '身體輕量化', 'ボディパージ', 'Autotomize', 'steel', '鋼', '變化', 0, 100, 15, '削去身體上多餘的部分。以大幅提高自己的速度，同時體重也會變輕。'), (476, '憤怒粉', 'いかりのこな', 'Rage Powder', 'bug', '蟲', '變化', 0, 100, 20, '將令人煩躁的粉末撒在自己身上，吸引對手的注意。進而將對手的攻擊全部轉移到自己身上。'), (477, '意念移物', 'テレキネシス', 'Telekinesis', 'psychic', '超能力', '變化', 0, 100, 15, '用超能力使對手浮起來。在３回合內攻擊會變得容易命中對手。'), (478, '魔法空間', 'マジックルーム', 'Magic Room', 'psychic', '超能力', '變化', 0, 100, 10, '創造出非常不可思議的空間。在５回合內，所有寶可夢攜帶的道具都會失去效果。'), (479, '擊落', 'うちおとす', 'Smack Down', 'rock', '岩石', '物理', 50, 100, 15, '向空中的對手投擲石頭或炮彈等物體進行攻擊。對手會被擊落，掉到地面。'), (480, '山嵐摔', 'やまあらし', 'Storm Throw', 'fighting', '格鬥', '物理', 60, 100, 10, '向對手發動強烈的一擊。一定會擊中要害。'), (481, '烈焰濺射', 'はじけるほのお', 'Flame Burst', 'fire', '火', '特殊', 70, 100, 15, '用命中後會爆裂開來的火焰攻擊對手。爆裂後的火焰也會波及到對手身旁的同伴。'), (482, '污泥波', 'ヘドロウェーブ', 'Sludge Wave', 'poison', '毒', '特殊', 95, 100, 10, '用污泥波攻擊自己周圍所有的寶可夢。有時會讓對手陷入中毒狀態。'), (483, '蝶舞', 'ちょうのまい', 'Quiver Dance', 'bug', '蟲', '變化', 0, 100, 20, '輕巧地跳起神秘又美麗的舞蹈。提高自己的特攻、特防和速度。'), (484, '重磅衝撞', 'ヘビーボンバー', 'Heavy Slam', 'steel', '鋼', '物理', 0, 100, 10, '用沉重的身體衝撞對手進行攻擊。體重比對手重越多，威力越大。'), (485, '同步干擾', 'シンクロノイズ', 'Synchronoise', 'psychic', '超能力', '特殊', 120, 100, 10, '用神奇的電波，攻擊周圍所有和自己相同屬性的寶可夢。'), (486, '電球', 'エレキボール', 'Electro Ball', 'electric', '電', '特殊', 0, 100, 10, '用電球攻擊對手。速度比對手快越多，威力越大。'), (487, '浸水', 'みずびたし', 'Soak', 'water', '水', '變化', 0, 100, 20, '朝對手灑下大量的水，讓對手變成水屬性。'), (488, '蓄能焰襲', 'ニトロチャージ', 'Flame Charge', 'fire', '火', '物理', 50, 100, 20, '讓火焰圍繞全身後攻擊對手。同時蓄積力量來提高自己的速度。'), (489, '盤蜷', 'とぐろをまく', 'Coil', 'poison', '毒', '變化', 0, 100, 20, '盤蜷身體集中精神。可提高自己的攻擊、防禦和命中率。'), (490, '下盤踢', 'ローキック', 'Low Sweep', 'fighting', '格鬥', '物理', 65, 100, 20, '瞄準對手的腳，以敏捷的動作進行攻擊。可降低對手的速度。'), (491, '酸液炸彈', 'アシッドボム', 'Acid Spray', 'poison', '毒', '特殊', 40, 100, 20, '噴出能溶化對手的液體進行攻擊。可大幅降低對手的特防。'), (492, '欺詐', 'イカサマ', 'Foul Play', 'dark', '惡', '物理', 95, 100, 15, '利用對手的力量進行攻擊。對手的攻擊越高，給予對手的傷害越大。'), (493, '單純光束', 'シンプルビーム', 'Simple Beam', 'normal', '一般', '變化', 0, 100, 15, '向對手發射謎樣的念力波。接收了念力波的對手，特性會變成單純。'), (494, '找夥伴', 'なかまづくり', 'Entrainment', 'normal', '一般', '變化', 0, 100, 15, '用神奇的節奏跳舞。讓對手模仿自己的動作，變成和自己一樣的特性。'), (495, '您先請', 'おさきにどうぞ', 'After You', 'normal', '一般', '變化', 0, 100, 15, '支援對手，讓對手緊接在自己之後行動。'), (496, '輪唱', 'りんしょう', 'Round', 'normal', '一般', '特殊', 60, 100, 15, '用歌聲攻擊對手。大家一起輪唱便可以接連使出，威力也會提高。'), (497, '迴聲', 'エコーボイス', 'Echoed Voice', 'normal', '一般', '特殊', 40, 100, 15, '用回聲攻擊對手。每回合有寶可夢持續使用回聲時，招式的威力會提高。'), (498, '逐步擊破', 'なしくずし', 'Chip Away', 'normal', '一般', '物理', 70, 100, 20, '看準時機，確實地攻擊對手。可不顧對手的能力變化給予傷害。'), (499, '清除之煙', 'クリアスモッグ', 'Clear Smog', 'poison', '毒', '特殊', 50, 100, 15, '向對手投擲特殊的泥塊進行攻擊。能讓對手的能力變化回復到初始狀態。'), (500, '輔助力量', 'アシストパワー', 'Stored Power', 'psychic', '超能力', '特殊', 20, 100, 10, '用蓄積的力量攻擊對手。自己的能力提高越多，招式的威力越大。');
